[Form]
public class PurchAgreementCreate extends FormRun implements AgreementCreateIForm
{
    PurchAgreementCreateForm purchAgreementCreateForm;
    PurchAgreementFormDatasourceManager purchAgreementFormDatasourceManager;
    AgreementFormArgs purchAgreementFormArgs;

    AgreementClassificationLookup agreementClassificationLookup;

    // <GEERU>
    #ISOCountryRegionCodes
    boolean                             countryRegion_RU;
}

// </GEERU>

/// <summary>
/// Gets or set the arguments object containing all parameters passed to the <see cref="PurchAgreement Form"/>.
/// </summary>
/// <param name = "_purchAgreementFormArgs">The <c>AgreementFormArgs</c> object.</param>
/// <returns>
/// The <c>AgreementFormArgs</c> object.
/// </returns>
public AgreementFormArgs parmPurchAgreementFormArgs(AgreementFormArgs _purchAgreementFormArgs = purchAgreementFormArgs)
{
    purchAgreementFormArgs = _purchAgreementFormArgs;
    return purchAgreementFormArgs;
}

/// <summary>
/// Gets the results of the Purchase agreement creation process.
/// </summary>
/// <remarks>
/// It is only relevant to call this method after the form has been closed with OK result.
/// </remarks>
/// <returns>
/// The result of the creation. The object hold the new elements buffers and the result of the form.
/// </returns>
public AgreementCreateResult getResult()
{
    AgreementCreateResult result = AgreementCreateResult::construct();

    result.parmCanceled(!element.closedOk());

    // Copying my table buffers to the DTO.
    result.parmAgreementHeader(purchAgreementHeader);
    result.parmPurchSalesAgreementHeaderDefault(purchAgreementHeaderDefault);
    result.parmAgreementHeaderDefault(agreementHeaderDefault);
    result.parmAgreementHeaderExt_RU(purchAgreementHeaderExt_RU);
    result.parmTmsAgreementHeader(tmsPurchAgreementHeader);

    return result;
}

/// <summary>
/// Builds the shared datasource manager between this form and <see cref="PurchAgreement Form"/>.
/// </summary>
void buildSharedDatasourceManager()
{
    // Init of the shared code object.
    purchAgreementFormDatasourceManager = PurchAgreementFormDatasourceManager::construct();
    purchAgreementFormDatasourceManager.parmFormRun(element);
    purchAgreementFormDatasourceManager.parmAgreementHeader(purchAgreementHeader);
    purchAgreementFormDatasourceManager.parmPurchSalesAgreementHeaderDefault(purchAgreementHeaderDefault);
    purchAgreementFormDatasourceManager.parmAgreementHeaderDefault(agreementHeaderDefault);
    purchAgreementFormDatasourceManager.parmAgreementHeaderExt_RU(purchAgreementHeaderExt_RU);
    purchAgreementFormDatasourceManager.parmTmsAgreementHeader(tmsPurchAgreementHeader);
}

boolean canClose()
{
    if (!element.closedOk())
    {
        // Cancelling. Releasing purchase agreement sequence id.
        purchAgreementCreateForm.parmNumberSeqFormHandler().formMethodClose();

        return true;
    }

    // Checking for mandatory fields.
    if (!purchAgreementHeader_ds.validateWrite())
    {
        return false;
    }

    // Essaie.
    ttsbegin;
    purchAgreementHeader_ds.write();

    // Setting up foreign keys in order to be able to validate data.
    agreementHeaderDefault.AgreementHeader = purchAgreementHeader.RecId;
    purchAgreementHeaderDefault.PurchaseAgreementHeader = purchAgreementHeader.RecId;
    if (countryRegion_RU)
    {
        purchAgreementHeaderExt_RU.initFromAgreementHeader(purchAgreementHeader);
    }
    tmsPurchAgreementHeader.initFromPurchAgreement(purchAgreementHeader.RecId);

    // Validating.
    if (!agreementHeaderDefault_ds.validateWrite() ||
        !purchAgreementHeaderDefault_ds.validateWrite() ||
        !purchAgreementHeaderExt_RU_ds.validateWrite() ||
        !tmsPurchAgreementHeader_ds.validateWrite())
    {
        // This case must never happen, the validation has to be performed on fields
        // before reaching this.
        throw Global::error("@SYS6144");
    }

    // Writing.
    agreementHeaderDefault_ds.write();
    purchAgreementHeaderDefault_ds.write();
    purchAgreementHeaderExt_RU_ds.write();
    tmsPurchAgreementHeader_ds.write();

    ttscommit;

    return true;
}

/// <summary>
///    Initializes the Create Purchase Agreement form and creates a new instance of the Purchase Agreement Header class.
/// </summary>
public void init()
{
    boolean isFrenchRegulatoryEnabled = PublicSectorUtils::isFrenchRegulatoryEnabled();
    xArgs args = element.args() as xArgs;

    super();

    // Checking if the required parameter is passed.
    if (!args || !args.caller() || !args.parmObject())
    {
        throw error("@SYS22539");
    }

    if (ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled())
    {
        boolean poEnabledForProjOps = FeatureStateProvider::IsFeatureEnabled(ProjOpsProjectPurchaseOrderFeature::instance());
        Project.visible(poEnabledForProjOps);
        Project.enabled(poEnabledForProjOps);
    }

    // Init of the shared code object.
    element.buildSharedDatasourceManager();

    // Init of dependency objects.
    agreementClassificationLookup = AgreementClassificationLookup::newAgreementClassificationLookup(tablenum(PurchAgreementHeader));

    // Retrieving arguments of the PurchAgreement form and instantiate PurchAgreementCreateForm class.
    purchAgreementFormArgs = args.parmObject() as AgreementFormArgs;
    purchAgreementCreateForm = PurchAgreementCreateForm::newPurchAgreementCreateForm(element);
    purchAgreementCreateForm.getSysLastValue();
    purchAgreementCreateForm.parmAgreementFormArgs(purchAgreementFormArgs);

    // <GEERU>
    countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    // </GEERU>

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        purchAgreementHeaderDefault_ds.object(fieldNum(PurchAgreementHeaderDefault, VendorInvoiceAccount)).mandatory(NoYes::Yes);

        purchAgreementHeader_PurchAgreementType1.visible(isFrenchRegulatoryEnabled);

        if (PublicSectorUtils::isFrenchRegulatoryEnabled())
        {
            purchAgreementHeader_ds.object(fieldNum(PurchAgreementHeader, ParentPurchAgreementID_PSN)).allowEdit(false);
        }
    }
    else
    {
        purchAgreementHeaderDefault_ds.object(fieldNum(PurchAgreementHeaderDefault, VendorInvoiceAccount)).mandatory(NoYes::No);
    }
}

void run()
{
    // Creation of the new reccord.
    purchAgreementHeader_ds.create();

    // Focusing on vendor account.
    purchAgreementHeader_VendAccount1.setFocus();

    super();
}

/// <summary>
///    Initializing values on a newly created Purchase Agreement Header record.
/// </summary>
public void initValue()
{
    super();

    purchAgreementHeader.DefaultAgreementLineType = purchAgreementCreateForm.parmDefaultCommitmentType();

    /// When PurchAgreement form has been called to create a new PurchAgreement and we have vendor
    /// record passed via args, the PurchAgreementCreate form has to be prefilled
    /// with the vendor account information.
    //
    // WE INITIALISE AccountNum field out of VendTable record passed with args.
    if (purchAgreementFormArgs.parmTransferAccountOnCreate())
    {
        purchAgreementHeader.VendAccount = purchAgreementFormArgs.parmTransferCustVendAccountAccountNum();
        purchAgreementHeader.VendorDataAreaId = purchAgreementFormArgs.parmTransferCustVendAccountDataAreaId();

        purchAgreementFormDatasourceManager.transferVendAccount(false);
    }

    // <GEERU>
    if (countryRegion_RU)
    {
        purchAgreementHeaderExt_RU_ds.initValue();
        if (purchAgreementHeader.VendAccount)
        {
            purchAgreementHeaderDefault.initFromVendTable_RU(VendTable::find(purchAgreementHeader.VendAccount));
        }
    }
    else
    {
        // </GEERU>
        purchAgreementCreateForm.parmNumberSeqFormHandler().formMethodDataSourceCreate();
        // <GEERU>
    }
    // </GEERU>

    // Specific init depending on the caller.
    switch (purchAgreementFormArgs.parmCallerDataSet())
    {
        case tableNum(ProjTable):
            agreementHeaderDefault.initFromProjTable(purchAgreementFormArgs.parmCallerRecord());
            if (!purchAgreementFormDatasourceManager.validateProject())
            {
                agreementHeaderDefault.Project = '';
                agreementHeaderDefault.ProjectDataAreaId = '';
            }
            else
            {
                purchAgreementFormDatasourceManager.initFromProject();
            }

            break;
    }
}

/// <summary>
///    Determines whether the current Purchase Agreement Header is valid to be written.
/// </summary>
/// <returns>true if the record is valid; otherwise, false.</returns>
public boolean validateWrite()
{
    boolean ret;

    if (!element.closedOk())
    {
        return true;
    }

    // <GEERU>
    if (countryRegion_RU)
    {
        if (purchAgreementHeader.AgreementClassification)
        {
            purchAgreementCreateForm.parmNumberSeqFormHandler().formMethodDataSourceCreate();
        }
        else
        {
            return checkFailed(strFmt("@SYS84753", fieldPName(PurchAgreementHeader, AgreementClassification)));
        }
        if (!purchAgreementHeaderExt_RU.AgreementDate)
        {
            return checkFailed(strFmt("@SYS84753", fieldPName(PurchAgreementHeaderExt_RU, AgreementDate)));
        }

        if (!purchAgreementHeaderExt_RU.checkAgreementUniqueness(purchAgreementHeader))
        {
            return false;
        }
    }
    // </GEERU>

    ret = super();

    // Check project information is set correctly...
    if (agreementHeaderDefault_ds.object(fieldNum(AgreementHeaderDefault, Project)).mandatory()
        && !agreementHeaderDefault.Project)
    {
        ret = checkFailed(strFmt("@SYS84753", fieldPName(AgreementHeaderDefault, Project)));
    }

    if (ret && !purchAgreementCreateForm.parmNumberSeqFormHandler().formMethodDataSourceValidateWrite())
    {
        ret = false;
    }

    return ret;
}

/// <summary>
/// Committing the Number Sequence record and saving the current default commitment type before writing the
/// Purchase Agreement Header record to the database.
/// Also hanles currency conversion if necessary.
/// </summary>
public void write()
{
    boolean doRefresh = false;

    if (!element.closedOk())
    {
        return;
    }

    purchAgreementCreateForm.parmNumberSeqFormHandler().formMethodDataSourceWrite();

    // Save the default line commitment type
    purchAgreementCreateForm.parmDefaultCommitmentType(purchAgreementHeader.DefaultAgreementLineType);
    purchAgreementCreateForm.saveSysLastValue();

    super();

    if (doRefresh)
    {
        purchAgreementHeader_ds.reread();
        purchAgreementHeader_ds.refresh();
    }
    // <GEERU>
    if (countryRegion_RU)
    {
        purchAgreementHeaderExt_RU_DS.reread();
        purchAgreementHeaderExt_RU_DS.refresh();
    }
    // </GEERU>
}

/// <summary>
///    Creating a new Purchase Agreement Header record.
/// </summary>
/// <param name="_append">Boolean indicating whether the new record should be appended.</param>
public void create(boolean _append = false)
{
    if (!purchAgreementHeader.RecId)
    {
        super(_append);

        if (PurchAgreementHeader::activeWorkflowTemplateExists())
        {
            purchAgreementHeader.WorkflowStatus_PSN = PurchAgreementWorkflowStatus::NotSubmitted;
        }

        tmsPurchAgreementHeader.initFromPurchAgreement(purchAgreementHeader.RecId);
        tmsPurchAgreementHeader.setCarrierFieldsFromDlvMode(agreementHeaderDefault.DeliveryMode);
    }
}

public void research(boolean _retainPosition = false)
{
    if (!SalesPurchAgreementCreateSkipResearchFlight::instance().isEnabled())
    {
        super(_retainPosition);
    }
}

