public boolean validate()
{
    boolean ret;

    ret = super();

    if (purchAgreementHeader.VendAccount)
    {
        if (!VendTable::exist(purchAgreementHeader.VendAccount))
        {
            ret = ret && checkFailed(strFmt("@SYS112224",purchAgreementHeader.VendAccount,fieldId2name(tableNum(VendTable),fieldNum(VendTable,AccountNum)),tableId2name(tableNum(VendTable))));
        }

        if (ret && countryRegion_RU)
        {
            purchAgreementHeaderExt_RU.initFromVendTable(VendTable::find(purchAgreementHeader.VendAccount));
            ret = purchAgreementHeaderExt_RU.checkVatOperationCode(purchAgreementHeader.VendAccount);
        }
    }

    return ret;
}

/// <summary>
/// Called when the Vendor Account is modified.
/// </summary>
/// <remarks>
/// Transferring vendor data to the Purchase Agreement Header.
/// </remarks>
public void modified()
{
    super();

    purchAgreementFormDatasourceManager.transferVendAccount();

    if (agreementHeaderDefault.Project && this.mustCopyFinancialDimensionOnModify())
    {
        purchAgreementHeader.DefaultDimension = purchAgreementHeader.copyDimension(ProjTable::find(agreementHeaderDefault.Project).DefaultDimension);
    }
    purchAgreementHeader_DefaultAgreementLineType.allowEdit(!purchAgreementHeader.isAgreementUsingActiveIntercompanyPartner());
    purchAgreementHeader_ds.refresh();

    // refresh delivery information
    agreementHeaderDefault_ds.refresh();

    purchAgreementFormDatasourceManager.tmsSetCarrierFieldsFromDlvMode(agreementHeaderDefault.DeliveryMode);
}

/// <summary>
/// Determines if the financial dimension must be copied when modifying.
/// </summary>
/// <returns>true if the dimension must be copied; otherwise, false.</returns>
[Replaceable]
protected boolean mustCopyFinancialDimensionOnModify()
{
    return Box::yesNo("@proj:Project_PurchaseAgreementHeaderDimension", DialogButton::No) == DialogButton::Yes;
}

