/// <summary>
///    Persists a record in the database when super is called.
/// </summary>
/// <remarks>
///    TMS sales agreement records can be created during super() call so we need to reread a corresponding data source
/// </remarks>
public void write()
{
    if (!element.closedOk())
    {
        return;
    }

    TMSPurchAgreementHeader newTMSPurchAgreementHeader;

    super();

    newTMSPurchAgreementHeader = TMSPurchAgreementHeader::find(purchAgreementHeader.RecId);

    tmsPurchAgreementHeader.data(newTMSPurchAgreementHeader);
    tmsPurchAgreementHeader.reread();
}

