/// <summary>
/// Determines whether the current record is valid and ready to be written to the database.
/// </summary>
/// <returns>
/// true if the record is valid; otherwise, false.
/// </returns>
/// <remarks>
/// Some purchase agreement records don't have a corresponding tms transportation agreement record
/// so call super only if corresponding record exists
/// </remarks>
public boolean validateWrite()
{
    if (!element.closedOk())
    {
        return true;
    }

    if (!tmsPurchAgreementHeader.RefRecId)
    {
        tmsPurchAgreementHeader.initFromPurchAgreement(purchAgreementHeader.RecId);
    }

    return super();
}

