[Form]
public class PurchAgreementHistory extends FormRun implements OfficeIMenuCustomizer
{
    PurchAgreementHeader            purchHeader;

    InventDimCtrl_Frm_AgreementLine inventDimFormSetup;

}

public void init()
{
    if (element.args().record())
    {
        if (element.args().dataset() == tableNum(PurchAgreementHeader))
        {
            purchHeader = element.args().record();
        }
        else if (element.args().dataset() == tableNum(PurchAgreementHeaderHistory))
        {
            PurchAgreementHeaderHistory purchAgreementHistory = element.args().record();
            purchHeader = PurchAgreementHeader::find(purchAgreementHistory.AgreementHeader);
        }
        else 
        {
            throw error(Error::wrongUseOfFunction(formStr(PurchAgreementHistory)));
        }

        if (purchHeader)
        {
            super();
            element.updateDesign(InventDimFormDesignUpdate::Init);
            logisticsPostalAddress_Purch_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
        }
    }
    else
    {
        throw error(Error::missingFormActiveBuffer(formStr(PurchAgreementHistory)));
    }
}

/// <summary>
/// Customizes the options used to populate the Office Menu with the procurement and sourcing entities.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    ListIterator dataEntityOptionsIterator = new ListIterator(_menuOptions.dataEntityOptions());

    str procurementAndSourcingModule = enum2Symbol(EnumNum(DMFModule), DMFModule::ProcurementAndSourcing);

    while (dataEntityOptionsIterator.more())
    {
        OfficeMenuDataEntityOptions dataEntityOptions = dataEntityOptionsIterator.value();

        DictDataEntity dataEntity = new DictDataEntity(tablename2Id(dataEntityOptions.dataEntityName()));

        if (dataEntity.modules() != procurementAndSourcingModule)
        {
            dataEntityOptionsIterator.delete();
        }
        else
        {
            dataEntityOptionsIterator.next();
        }
    }
}

/// <summary>
/// Gets the Inventory Dimensions Form Setup object.
/// </summary>
/// <returns>
/// Inventory Dimensions Form Setup object.
/// </returns>
public Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

/// <summary>
/// Updates form design elements.
/// </summary>
/// <param name="_mode">
/// Mode describing purpose for the call.
/// </param>
/// <exception cref="Exception::Error">
/// Throws when called with non-supported mode.
/// </exception>
public void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                if (purchHeader)
                    inventDimFormSetup = InventDimCtrl_Frm_AgreementLine::newFromForm(element, fieldNum(InventDimSetupGrid, ShowGridPurchLine));
            }

        case InventDimFormDesignUpdate::Active      :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(a.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

public void init()
{
    QueryBuildDataSource ds;
    QueryBuildDataSource subDs;

    super();

    ds = this.query().dataSourceTable(this.table());
    ds.clearRanges();
    ds.clearDynalinks();
    ds.clearLinks();

    ds.addRange(fieldnum(AgreementLineHistory, ValidFrom));
    ds.addRange(fieldnum(AgreementLineHistory, ValidTo));

    subDs = ds.addDataSource(tablenum(AgreementLine));
    subDs.relations(true);
    subDs.joinMode(JoinMode::InnerJoin);
    SysQuery::findOrCreateRange(subDs, fieldnum(AgreementLine, Agreement))
        .value(queryValue(purchAgreementHeaderHistory.AgreementHeader));
}

public int active()
{
    int ret;

    ret = super();

    this.setRangeValues();

    return ret;
}

public void init()
{
    super();
    purchAgreementHeaderHistory_ds.cacheAddMethod(tablemethodstr(PurchAgreementHeaderHistory, vendName));
    purchAgreementHeaderHistory_ds.cacheAddMethod(tablemethodstr(PurchAgreementHeaderHistory, agreementConfirmation));
}

void setRangeValues()
{
    QueryBuildDataSource    ds;
    QueryBuildRange         range;

    ds = a_ds.query().dataSourceTable(tablenum(AgreementLineHistory));
    range = ds.rangeField(fieldnum(AgreementLineHistory, ValidFrom));
    range.value(SysQuery::range(null, purchAgreementHeaderHistory.createdDateTime));

    range = ds.rangeField(fieldnum(AgreementLineHistory, ValidTo));
    range.value(SysQuery::range(purchAgreementHeaderHistory.createdDateTime, null));

    ds = a_ds.query().dataSourceTable(tablenum(AgreementLine));
    SysQuery::findOrCreateRange(ds, fieldnum(AgreementLine, Agreement))
        .value(queryValue(purchAgreementHeaderHistory.AgreementHeader));

    a_ds.executeQuery();
}

