Method.Source:
    [Form]
    public class PurchBookProcessVAT_RU extends FormRun
    {
        #resAppl

        BookDataCalc_Purch_RU                   bookDataCalc;

        BookProcessVATEngine_RU                 bookProcessVATEngine;

