void amountModified(TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper,
                    TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOperOrig)
{
    BookStructSmallVATAmounts_RU    operCurrentAmountsOrig = new BookStructSmallVATAmounts_RU();

    try
    {
        if (! element.checkRDeferrals(_tmpPurchBookVATProcessLogTransOperOrig))
        {
            throw Exception::Error;
        }

        bookProcessVATEngine.setCursors(tmpPurchBookVATProcessLogTrans, _tmpPurchBookVATProcessLogTransOper);

        operCurrentAmountsOrig.addAmounts(_tmpPurchBookVATProcessLogTransOperOrig.AmountLiableToVAT + _tmpPurchBookVATProcessLogTransOperOrig.TaxAmount,
                                          _tmpPurchBookVATProcessLogTransOperOrig.AmountLiableToVAT,
                                          _tmpPurchBookVATProcessLogTransOperOrig.TaxAmount);

        bookProcessVATEngine.operAmountModified(_tmpPurchBookVATProcessLogTransOper.AmountLiableToVAT,
                                                operCurrentAmountsOrig,
                                                _tmpPurchBookVATProcessLogTransOperOrig.Percent);

        this.updateLedgerAccounts();
    }
    catch (Exception::Error)
    {
        tmpPurchBookVATProcessLogTransOper_Ds.reread();
    }
}

public boolean checkRDeferrals(TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper)
{
    boolean         ret = true;
    RDeferralsTrans rDeferralsTrans;

    if (_tmpPurchBookVATProcessLogTransOper.Deferrals)
    {
        rDeferralsTrans = RDeferralsTrans::find(_tmpPurchBookVATProcessLogTransOper.rDeferralsTransRecId);
        ret = checkFailed(strfmt("@GLS114360",
                          rDeferralsTrans.DeferralsId ? rDeferralsTrans.DeferralsId : '',
                          enum2str(RDeferralsVATRefundingMethod::Proportional),
                          enum2str(RDeferralsVATRefundingMethod::Standard)));
    }

    return ret;
}

void createHeader()
{
    TmpPurchBookVATProcessLogTrans_RU       localTmpPurchBookVATProcessLogTrans;
    TmpPurchBookVATProcessLogTransOper_RU   localTmpPurchBookVATProcessLogTransOper;

    localTmpPurchBookVATProcessLogTransOper.setTmpData(tmpPurchBookVATProcessLogTransOper);

    if (tmpPurchBookVATProcessLogTrans_DS.anyMarked())
    {
        localTmpPurchBookVATProcessLogTrans = tmpPurchBookVATProcessLogTrans_DS.getFirst(true, false);

        while (localTmpPurchBookVATProcessLogTrans)
        {
            while select localTmpPurchBookVATProcessLogTransOper
                where localTmpPurchBookVATProcessLogTransOper.RefRecId == localTmpPurchBookVATProcessLogTrans.RecId
                   && localTmpPurchBookVATProcessLogTransOper.AllowDelete == NoYes::No
            {
                element.splitLine(localTmpPurchBookVATProcessLogTrans, localTmpPurchBookVATProcessLogTransOper);
            }

            localTmpPurchBookVATProcessLogTrans = tmpPurchBookVATProcessLogTrans_DS.getNext();
        }
    }
    else
    {
        localTmpPurchBookVATProcessLogTrans = tmpPurchBookVATProcessLogTrans_QR.get(tableNum(TmpPurchBookVATProcessLogTrans_RU));

        while select localTmpPurchBookVATProcessLogTransOper
            where localTmpPurchBookVATProcessLogTransOper.RefRecId == localTmpPurchBookVATProcessLogTrans.RecId
               && localTmpPurchBookVATProcessLogTransOper.AllowDelete == NoYes::No
        {
            element.splitLine(localTmpPurchBookVATProcessLogTrans, localTmpPurchBookVATProcessLogTransOper);
        }
    }

    tmpPurchBookVATProcessLogTransOper.setTmpData(localTmpPurchBookVATProcessLogTransOper);

    tmpPurchBookVATProcessLogTransOper_DS.write();
    tmpPurchBookVATProcessLogTransOper_DS.research();

    headerPercent   = 0.00;
    operCodeRecId   = 0;
}

void createLine(TmpPurchBookVATProcessLogTrans_RU _tmpPurchBookVATProcessLogTrans, TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper)
{
    BookStructSmallVATAmounts_RU avaliableAmounts;
    TmpPurchBookVATProcessLogTransOper_RU newProcessLogTransOper;

    newProcessLogTransOper.setTmpData(tmpPurchBookVATProcessLogTransOper);
    buf2Buf(_tmpPurchBookVATProcessLogTransOper, newProcessLogTransOper);

    if (operCodeRecId)
    {
        newProcessLogTransOper.PurchBookVATProcessParameters_RU = operCodeRecId;
    }

    bookProcessVATEngine.setCursors(_tmpPurchBookVATProcessLogTrans, newProcessLogTransOper);
    avaliableAmounts = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::AvaliableAmount);
    bookProcessVATEngine.operAmountModified(avaliableAmounts.amountLiableToVAT());

    newProcessLogTransOper.AllowDelete = NoYes::Yes;
    newProcessLogTransOper.Blocked     = blocked;
    newProcessLogTransOper.insert();
}

void deleteData()
{
    boolean bEnableBtn;

    if (Box::yesNo("@SYS9461", DialogButton::No) != DialogButton::Yes)
    {
        return;
    }

    this.deleteDataFromDS();

    tmpPurchBookVATProcessLogTrans_ds.research();

    bEnableBtn = tmpPurchBookVATProcessLogTrans ? true : false;

    this.enableButton(bEnableBtn);
    btnCreateOperation.enabled(bEnableBtn);
    btnCreateHeader.enabled(bEnableBtn);
}

protected void deleteDataFromDS()
{
    delete_from tmpPurchBookVATProcessLogTransOper;
    delete_from tmpPurchBookVATProcessLogTrans;

    bookProcessVATEngine = new BookProcessVATEngine_RU();
}

protected void enableButton(boolean bEnable)
{
    btnDataCalculate.enabled(! bEnable);

    btnDataDelete.enabled(bEnable);
    btnFactureJournalCust.enabled(bEnable);
    btnFactureJournalVend.enabled(bEnable);
    btnDataPostBtn.enabled(bEnable);
    buttonGroupMark.enabled(bEnable);
    btnCreateOperation.enabled(bEnable);
    buttonGroupBlock.enabled(bEnable);
    btnFactureJournalCust.enabled(bEnable);
    btnFactureJournalVend.enabled(bEnable);
    btnFactureJournalEmpl.enabled(bEnable);
    btnCreateHeader.enabled(bEnable);
}

FactureModule_RU getModuleCached(TmpPurchBookVATProcessLogTrans_RU _tmpPurchBookVATProcessLogTrans = tmpPurchBookVATProcessLogTrans)
{
    FactureModule_RU    module;

    if (moduleCache.exists(_tmpPurchBookVATProcessLogTrans.RecId))
    {
        module = moduleCache.lookup(_tmpPurchBookVATProcessLogTrans.RecId);
    }
    else
    {
        module = _tmpPurchBookVATProcessLogTrans.factureModule();
        moduleCache.insert(_tmpPurchBookVATProcessLogTrans.RecId, module);
    }

    return module;
}

public void init()
{
    FormDesign      design;
    str             caption;

    if ((! this.args()) || (this.args().parmEnumType() != enumNum(PurchBookVATProcessCancelType_RU)))
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }

    purchBookVATProcessCancelType = this.args().parmEnum();
    bookProcessVATEngine = new BookProcessVATEngine_RU();
    moduleCache = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Enum);

    super();

    this.initLabels();

    this.setContragentVisible();

    btnDataCalculate.defaultButton(true);

    design  = this.design();
    caption = design.caption();

    switch (purchBookVATProcessCancelType)
    {
        case PurchBookVATProcessCancelType_RU::Cancel :
            caption = strFmt("@SYS70938", caption, "@GLS101193");

            notUsedAmountLabel.text("@GLS104933");
            operNotUsedAmountLabel.text("@GLS104933");

            usedAmountLabel.visible(false);
            usedAmountLiableToVAT20.visible(false);
            usedTaxAmountVAT20.visible(false);
            usedAmountLiableToVAT10.visible(false);
            usedTaxAmountVAT10.visible(false);
            usedAmountLiableToVAT0.visible(false);
            usedAmountNotLiableToVAT.visible(false);
            usedAmountInclVAT.visible(false);

            operUsedAmountLabel.visible(false);
            operUsedQty.visible(false);
            operUsedAmountLiableToVAT.visible(false);
            operUsedTaxAmountVAT.visible(false);
            operUsedAmountInclVAT.visible(false);

            transOper_Percent.visible(false);

            tmpPurchBookVATProcessLogTransOper_ds.allowDelete(false);

            btnCreateOperation.visible(false);
            btnCreateHeader.visible(false);

            wndOperTransType.visible(false);

            buttonGroupBlock.visible(false);
            break;

        case PurchBookVATProcessCancelType_RU::Process :
            caption = strFmt("@SYS70938", caption, "@GLS101145");
            break;

        default :
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    caption = design.caption(caption);

    imageList = new ImageListAppl_PurchBookProcessVAT_RU();
    winError.imageList(imageList.imageList());

    wndOperTransType.imageList(imageList.imageList());

    btnCreateOperation.enabled(false);
}

private void initLabels()
{
    void setBaseAmountText(FormStaticTextControl _cntr, TaxType_W _taxType)
    {
        if (_taxType == TaxType_W::Other)
        {
             _cntr.text(strFmt("@GLS106745", "@GLS104845"));
        }
        else
        {
            _cntr.text(strFmt("@SYS76498", "@GLS104837", _taxType));
        }
    }

    void setTaxAmountText(FormStaticTextControl _cntr, TaxType_W _taxType)
    {
        _cntr.text(strFmt("@GLS106745", _taxType));
    }

    setBaseAmountText(amountLiableToVAT20Label, TaxType_W::VAT);

    setTaxAmountText(taxAmountVAT20Label, TaxType_W::VAT);

    setBaseAmountText(amountLiableToVAT10Label, TaxType_W::VATReduced);

    setTaxAmountText(taxAmountVAT10Label, TaxType_W::VATReduced);

    setBaseAmountText(amountLiableToVAT0Label, TaxType_W::VATZero);

    setBaseAmountText(amountNotLiableToVATLabel, TaxType_W::Other);
}

void processAmountAdj(PurchBookVATProcessLogTable_RU _logTable, TransDate _transDate)
{
    TransDate                               fromDate, toDate;
    TransDate                               periodStart, periodEnd;
    TmpPurchBookVATProcessLogTrans_RU       logTrans;
    TmpPurchBookVATProcessLogTransOper_RU   logTransOper;

    PurchBookVATProcessLogTrans_RU          localLogTrans, logTransCurrent;
    PurchBookVATProcessLogTransOper_RU      localLogTransOper, logTransOperCurrent;

    BookDataCalc_Purch_RU                   bookDataCalcAdj;
    BookPost_Purch_RU                       bookPostAdj;
    SetEnumerator                           setEnumerator;
    Set                                     periodsSet = new Set(Types::Container);

    void calcAndPost(TransDate _fromDate, TransDate _toDate)
    {
        bookDataCalcAdj = BookDataCalc_Purch_RU::newFromVATProcess(purchBookVATProcessCancelType, NoYes::Yes);

        bookDataCalcAdj.parmFromDate(_fromDate);
        bookDataCalcAdj.parmToDate(_toDate);

        bookDataCalcAdj.runOperation();

        logTrans.setTmpData(bookDataCalcAdj.parmProcessLogTrans());
        logTransOper.setTmpData(bookDataCalcAdj.parmProcessLogTransOper());

        if (purchBookVATProcessCancelType == PurchBookVATProcessCancelType_RU::Process)
        {
            update_recordset logTrans
                setting Marked = NoYes::Yes
                where logTrans.TransType == SalesPurchBookTransType_RU::SumDiff &&
                      logTrans.CorrectedPeriod;
        }
        else
        {
            while select RecId, TransType, FactureId from logTransCurrent
                where logTransCurrent.TransType == SalesPurchBookTransType_RU::Invoice ||
                      logTransCurrent.TransType == SalesPurchBookTransType_RU::CreditNote
            exists join logTransOperCurrent
                where logTransOperCurrent.RefRecId          == logTransCurrent.RecId &&
                      logTransOperCurrent.LogTableRefRecId  == _logTable.RecId       &&
                      logTransOperCurrent.TransDate         >= fromDate              &&
                      logTransOperCurrent.TransDate         <= toDate                &&
                      logTransOperCurrent.CanceledRefRecId
            {
                update_recordset logTrans
                    setting Marked = NoYes::Yes
                    where logTrans.CorrectedFactureId   == logTransCurrent.FactureId           &&
                          logTrans.TransType            == SalesPurchBookTransType_RU::SumDiff &&
                          logTrans.CorrectedPeriod      == toDate;
            }
        }

        update_recordset logTransOper
            setting Marked = NoYes::Yes
        exists join logTrans
            where logTrans.RecId            == logTransOper.RefRecId               &&
                  logTrans.LogTableRefRecId == logTransOper.LogTableRefRecId       &&
                  logTrans.TransType        == SalesPurchBookTransType_RU::SumDiff &&
                  logTrans.CorrectedPeriod                                         &&
                  logTrans.Marked;

        select firstonly logTransOper
            where logTransOper.Marked == NoYes::Yes;

        if (logTransOper)
        {
            bookPostAdj = BookPost_Purch_RU::newFromVATProcess(purchBookVATProcessCancelType, logTrans, logTransOper);
            bookPostAdj.parmTransDate(purchBookVATProcessCancelType == PurchBookVATProcessCancelType_RU::Process ? _transDate : _toDate);
            bookPostAdj.runOperation();
        }
    }

    [fromDate, toDate] = BookDataCalc_RU::datePeriodBook(bookDataCalc.parmToDate());

    if (! VendParameters::find().AmountDiffBookCorrection_RU)
    {
        return;
    }

    setPrefix("@GLS114479");

    if (purchBookVATProcessCancelType == PurchBookVATProcessCancelType_RU::Process)
    {
        calcAndPost(fromDate, toDate);
    }
    else
    {
        while select localLogTransOper
            group by TransDate
            where localLogTransOper.TransDate > toDate &&
                 !localLogTransOper.CanceledRefRecId
        exists join localLogTrans
            where localLogTrans.RecId           == localLogTransOper.RefRecId          &&
                  localLogTrans.TransType       == SalesPurchBookTransType_RU::SumDiff &&
                  localLogTrans.CorrectedPeriod == toDate                              &&
                  localLogTrans.Reversed        == NoYes::No
        exists join logTransCurrent
            where logTransCurrent.FactureId == localLogTrans.CorrectedFactureId    &&
                 (logTransCurrent.TransType == SalesPurchBookTransType_RU::Invoice ||
                  logTransCurrent.TransType == SalesPurchBookTransType_RU::CreditNote)
        exists join logTransOperCurrent
            where logTransOperCurrent.RefRecId          == logTransCurrent.RecId &&
                  logTransOperCurrent.LogTableRefRecId  == _logTable.RecId       &&
                  logTransOperCurrent.TransDate         >= fromDate              &&
                  logTransOperCurrent.TransDate         <= toDate                &&
                  logTransOperCurrent.CanceledRefRecId
        {
            periodsSet.add(BookDataCalc_RU::datePeriodBook(localLogTransOper.TransDate));
        }

        setEnumerator = periodsSet.getEnumerator();
        while (setEnumerator.moveNext())
        {
            [periodStart, periodEnd] = setEnumerator.current();

            calcAndPost(periodStart, periodEnd);
        }
    }
}

void processData()
{
    BookPost_Purch_RU bookPost;
    TmpPurchBookVATProcessLogTrans_RU       logTrans;
    TmpPurchBookVATProcessLogTransOper_RU   logTransOper;

    if ( ! bookDataCalc || ! bookProcessVATEngine)
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }

    logTrans.setTmpData(tmpPurchBookVATProcessLogTrans);
    logTransOper.setTmpData(tmpPurchBookVATProcessLogTransOper);

    bookPost = BookPost_Purch_RU::newFromVATProcess(purchBookVATProcessCancelType,
                                                    logTrans,
                                                    logTransOper);
    if (bookPost.prompt())
    {
        try
        {
            bookPost.runOperation();

            this.processAmountAdj(bookPost.logTable(), bookPost.parmTransDate());
        }
        catch // to research data source
        {
            exceptionTextFallThrough();
        }
    }

    tmpPurchBookVATProcessLogTrans.setTmpData(logTrans);
    tmpPurchBookVATProcessLogTransOper.setTmpData(logTransOper);

    tmpPurchBookVATProcessLogTrans_DS.research();

    this.enableButton(tmpPurchBookVATProcessLogTrans ? true : false);
}

void selectData()
{
    TmpPurchBookVATProcessLogTrans_RU       logTrans;
    TmpPurchBookVATProcessLogTransOper_RU   logTransOper;

    this.deleteDataFromDS();

    bookDataCalc = BookDataCalc_Purch_RU::newFromVATProcess(purchBookVATProcessCancelType);

    if (bookDataCalc.prompt())
    {
        bookDataCalc.runOperation();

        // asynchronous data fetch
        element.setTimeoutEx(formMethodStr(PurchBookProcessVAT_RU, setDataBuffers));

        logTrans.setTmpData(bookDataCalc.parmProcessLogTrans());
        logTransOper.setTmpData(bookDataCalc.parmProcessLogTransOper());

        bookProcessVATEngine = BookProcessVATEngine_RU::create(logTrans, logTransOper);

        select firstonly logTrans;
        this.enableButton(logTrans ? true : false);
    }
}

void setAccessByDS()
{
    boolean bAccess        = false;
    boolean fieldsEditable = false;
    boolean amountAdjustment = tmpPurchBookVATProcessLogTrans.TransType == SalesPurchBookTransType_RU::SumDiff &&
                               tmpPurchBookVATProcessLogTrans.CorrectedPeriod;

    if (purchBookVATProcessCancelType == PurchBookVATProcessCancelType_RU::Process)
    {
        [bAccess, fieldsEditable] = tmpPurchBookVATProcessLogTrans.SalesPurchBookDataMap_RU::isEditable(ModuleCustVend::Vend);
        btnCreateOperation.enabled(bAccess);
        btnCreateHeader.enabled(bAccess);
    }

    if (fieldsEditable)
    {
        allowEditFieldsOnFormDS_W(tmpPurchBookVATProcessLogTransOper_ds, false);
        tmpPurchBookVATProcessLogTransOper_ds.object(fieldNum(TmpPurchBookVATProcessLogTransOper_RU, PurchBookVATProcessParameters_RU)).allowEdit(true);
        tmpPurchBookVATProcessLogTransOper_ds.object(fieldNum(TmpPurchBookVATProcessLogTransOper_RU, LedgerDimension)).allowEdit(true);
        tmpPurchBookVATProcessLogTransOper_ds.object(fieldNum(TmpPurchBookVATProcessLogTransOper_RU, OffsetLedgerDimension)).allowEdit(true);
        tmpPurchBookVATProcessLogTransOper_ds.object(fieldNum(TmpPurchBookVATProcessLogTransOper_RU, Blocked)).allowEdit(true);
    }
    else
    {
        allowEditFieldsOnFormDS_W(tmpPurchBookVATProcessLogTransOper_ds, bAccess);
        tmpPurchBookVATProcessLogTransOper_DS.allowDelete(bAccess);
    }

    tmpPurchBookVATProcessLogTransOper_ds.object(fieldNum(TmpPurchBookVATProcessLogTransOper_RU, TaxCode)).allowEdit(false);

    if (amountAdjustment)
    {
        tmpPurchBookVATProcessLogTransOper_DS.object(fieldNum(TmpPurchBookVATProcessLogTransOper_RU, Blocked)).allowEdit(false);
    }
}

void setContragentVisible()
{
    FactureModule_RU    module;

    groupVendor.visible(false);
    groupCustomer.visible(false);
    groupEmployee.visible(false);

    btnFactureJournalCust.visible(false);
    btnFactureJournalVend.visible(false);
    btnFactureJournalEmpl.visible(false);

    if (tmpPurchBookVATProcessLogTrans.RecId)
    {
        module = element.getModuleCached();

        switch (module)
        {
            case FactureModule_RU::Cust                    :
            case FactureModule_RU::TaxCorrectionPurch_Cust :
                customerAccountNum.value(tmpPurchBookVATProcessLogTrans.AccountNum);
                custTable_DS.executeQuery();
                groupCustomer.visible(true);
                btnFactureJournalCust.visible(true);
                break;
            case FactureModule_RU::TaxCorrectionPurch      :
            case FactureModule_RU::Vend                    :
                groupVendor.visible(true);
                btnFactureJournalVend.visible(true);
                break;
            case FactureModule_RU::Empl                    :
                groupEmployee.visible(true);
                btnFactureJournalEmpl.visible(true);
                break;
            default                                        :
                throw error(Error::wrongUseOfFunction(funcName()));
        }
    }
    else
    {
        groupVendor.visible(true);
    }
}

public void setDataBuffers(AsyncTaskResult _result)
{
    tmpPurchBookVATProcessLogTrans.setTmpData(bookDataCalc.parmProcessLogTrans());
    tmpPurchBookVATProcessLogTransOper.setTmpData(bookDataCalc.parmProcessLogTransOper());

    tmpPurchBookVATProcessLogTrans_DS.research();
}

void setDropDialogValues(Percent _headerPercent, PurchBookVATProcessParametersRecId_RU _operCodeRecId, NoYes _blocked)
{
    headerPercent   = _headerPercent;
    operCodeRecId   = _operCodeRecId;
    blocked         = _blocked;
}

private void splitLine(TmpPurchBookVATProcessLogTrans_RU _tmpPurchBookVATProcessLogTrans, TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper)
{
    BookStructSmallVATAmounts_RU    operSettledAmounts, operUsedAmounts;
    BookStructSmallVATAmounts_RU    operCurrentAmountsOrig;

    _tmpPurchBookVATProcessLogTransOper.Percent = _tmpPurchBookVATProcessLogTransOper.Percent * (1 - headerPercent / 100);

    bookProcessVATEngine.setCursors(_tmpPurchBookVATProcessLogTrans, _tmpPurchBookVATProcessLogTransOper);
    operSettledAmounts = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::SettledAmount);
    operUsedAmounts    = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::UsedAmount);

    _tmpPurchBookVATProcessLogTransOper.AmountLiableToVAT = CurrencyExchangeHelper::amount((operSettledAmounts.amountLiableToVAT() - operUsedAmounts.amountLiableToVAT()) * _tmpPurchBookVATProcessLogTransOper.Percent / 100);

    operCurrentAmountsOrig = new BookStructSmallVATAmounts_RU();
    operCurrentAmountsOrig.addAmounts(_tmpPurchBookVATProcessLogTransOper.orig().AmountLiableToVAT + _tmpPurchBookVATProcessLogTransOper.orig().TaxAmount,
                                        _tmpPurchBookVATProcessLogTransOper.orig().AmountLiableToVAT,
                                        _tmpPurchBookVATProcessLogTransOper.orig().TaxAmount);
    bookProcessVATEngine.operAmountModified(_tmpPurchBookVATProcessLogTransOper.AmountLiableToVAT,
                                            operCurrentAmountsOrig,
                                            _tmpPurchBookVATProcessLogTransOper.orig().Percent);
    element.updateLedgerAccounts(_tmpPurchBookVATProcessLogTrans, _tmpPurchBookVATProcessLogTransOper);

    _tmpPurchBookVATProcessLogTransOper.update();

    element.createLine(_tmpPurchBookVATProcessLogTrans, _tmpPurchBookVATProcessLogTransOper);

    element.updateLedgerAccounts(_tmpPurchBookVATProcessLogTrans, _tmpPurchBookVATProcessLogTransOper);
}

void updateDesign()
{
    boolean qtyVisible;

    qtyVisible = BookStructVATAmounts_RU::create(purchBookVATProcessCancelType == PurchBookVATProcessCancelType_RU::Cancel ?
                                                 tmpPurchBookVATProcessLogTrans.SettledAmounts :
                                                 tmpPurchBookVATProcessLogTrans.FactureAmounts).isGroupBy();

    itemName.visible(qtyVisible);
    qtyGroup.visible(qtyVisible);
    qty.visible(qtyVisible);

    if (purchBookVATProcessCancelType == PurchBookVATProcessCancelType_RU::Cancel)
        return;

    if (tmpPurchBookVATProcessLogTrans.Reversed)
    {
        notUsedAmountLabel.text("@GLS104933");
        operNotUsedAmountLabel.text("@GLS104933");

        usedAmountLabel.visible(false);
        usedAmountLiableToVAT20.visible(false);
        usedTaxAmountVAT20.visible(false);
        usedAmountLiableToVAT10.visible(false);
        usedTaxAmountVAT10.visible(false);
        usedAmountLiableToVAT0.visible(false);
        usedAmountNotLiableToVAT.visible(false);
        usedAmountInclVAT.visible(false);

        operUsedAmountLabel.visible(false);
        operUsedAmountLiableToVAT.visible(false);
        operUsedTaxAmountVAT.visible(false);
        operUsedAmountInclVAT.visible(false);
    }
    else
    {
        notUsedAmountLabel.text("@GLS105074");
        operNotUsedAmountLabel.text("@GLS105074");

        usedAmountLabel.visible(true);
        usedAmountLiableToVAT20.visible(true);
        usedTaxAmountVAT20.visible(true);
        usedAmountLiableToVAT10.visible(true);
        usedTaxAmountVAT10.visible(true);
        usedAmountLiableToVAT0.visible(true);
        usedAmountNotLiableToVAT.visible(true);
        usedAmountInclVAT.visible(true);

        operUsedAmountLabel.visible(true);
        operUsedAmountLiableToVAT.visible(true);
        operUsedTaxAmountVAT.visible(true);
        operUsedAmountInclVAT.visible(true);
    }
}

void updateLedgerAccounts(
    TmpPurchBookVATProcessLogTrans_RU       _tmpPurchBookVATProcessLogTrans     = tmpPurchBookVATProcessLogTrans,
    TmpPurchBookVATProcessLogTransOper_RU   _tmpPurchBookVATProcessLogTransOper = tmpPurchBookVATProcessLogTransOper
)
{
    LedgerDimensionDefaultAccount       defaultAccount, offsetDefaultAccount;
    PurchBookVATProcessParameters_RU    parameters    = PurchBookVATProcessParameters_RU::find(_tmpPurchBookVATProcessLogTransOper.PurchBookVATProcessParameters_RU);
    PurchBookVATLedgerPosting_RU        ledgerPosting = PurchBookVATLedgerPosting_RU::ledgerPosting(_tmpPurchBookVATProcessLogTransOper.TaxCode,
                                                                                                    _tmpPurchBookVATProcessLogTransOper.PurchBookVATProcessParameters_RU);

    if (_tmpPurchBookVATProcessLogTransOper.TaxAmount && _tmpPurchBookVATProcessLogTrans.TransType != SalesPurchBookTransType_RU::PrepaymentStorno)
    {
        [defaultAccount, offsetDefaultAccount] = PurchBookVATProcessParameters_RU::calcLedgerDimensions(_tmpPurchBookVATProcessLogTransOper.TaxCode,
                                                                                                        _tmpPurchBookVATProcessLogTrans.FactureId,
                                                                                                        _tmpPurchBookVATProcessLogTrans.TransType,
                                                                                                        _tmpPurchBookVATProcessLogTrans.InvoiceRecIdRef,
                                                                                                        _tmpPurchBookVATProcessLogTrans.PaymentRecIdRef,
                                                                                                        _tmpPurchBookVATProcessLogTrans.InvoiceCompany,
                                                                                                        _tmpPurchBookVATProcessLogTrans.PaymentCompany);

        _tmpPurchBookVATProcessLogTransOper.LedgerDimension       = ledgerPosting.LedgerDimension        ? ledgerPosting.LedgerDimension       : defaultAccount;
        _tmpPurchBookVATProcessLogTransOper.OffsetLedgerDimension = ledgerPosting.OffsetLedgerDimension  ? ledgerPosting.OffsetLedgerDimension : offsetDefaultAccount;
        _tmpPurchBookVATProcessLogTransOper.TransTxt              = parameters.Description;
    }
    else
    {
        _tmpPurchBookVATProcessLogTransOper.LedgerDimension       = 0;
        _tmpPurchBookVATProcessLogTransOper.OffsetLedgerDimension = 0;
        _tmpPurchBookVATProcessLogTransOper.TransTxt              = '';
    }

    _tmpPurchBookVATProcessLogTransOper.DeriveDim              = parameters.DeriveDim;
    _tmpPurchBookVATProcessLogTransOper.DefaultDimensionPosted = parameters.DeriveDim ? parameters.DefaultDimension : _tmpPurchBookVATProcessLogTransOper.DefaultDimension;

    tmpPurchBookVATProcessLogTransOper_DS.active();
}

display EmployeeId_RU emplAccountNum(TmpPurchBookVATProcessLogTrans_RU _tmpPurchBookVATProcessLogTrans)
{
    EmployeeId_RU employeeId;

    if (element.getModuleCached(_tmpPurchBookVATProcessLogTrans) == FactureModule_RU::Empl)
    {
        employeeId = _tmpPurchBookVATProcessLogTrans.AccountNum;
    }

    return employeeId;
}

display Integer error(TmpPurchBookVATProcessLogTrans_RU _logTrans)
{
    if (_logTrans.Error)
    {
        return imageList.image(#ImageError);
    }

    return -1;
}

public int active()
{
    int     ret = super();

    if (ret)
    {
        element.setAccessByDS();
        element.updateDesign();

        element.setContragentVisible();

        element.setLabels();
    }

    return ret;
}

