public void jumpRef()
{
    FactureModule_RU module = element.getModuleCached();

    switch (module)
    {
        case FactureModule_RU::Cust                    :
        case FactureModule_RU::TaxCorrectionPurch_Cust :
            custTable_DS.object(fieldNum(CustTable, AccountNum)).jumpRef();
            break;
        case FactureModule_RU::TaxCorrectionPurch      :
        case FactureModule_RU::Vend                    :
            super();
            break;
        case FactureModule_RU::Empl                    :
            EmployeeTable_RU::jumpRef(element, tmpPurchBookVATProcessLogTrans.AccountNum);
            break;
    }
}

#define.Colon(": ")
public str toolTip()
{
    str ret;

    FactureModule_RU module = element.getModuleCached();

    switch (module)
    {
        case FactureModule_RU::Cust                    :
        case FactureModule_RU::TaxCorrectionPurch_Cust :
            ret = "@SYS7149" + #Colon +
                  strFmt("@SYS26868",
                         tmpPurchBookVATProcessLogTrans.AccountNum,
                         tmpPurchBookVATProcessLogTrans.AccountName);
            break;
        case FactureModule_RU::TaxCorrectionPurch      :
        case FactureModule_RU::Vend                    :
            ret = super();
            break;
        case FactureModule_RU::Empl                    :
            ret = "@GLS105343" + #Colon +
                  strFmt("@SYS26868",
                         tmpPurchBookVATProcessLogTrans.AccountNum,
                         tmpPurchBookVATProcessLogTrans.AccountName);
            break;
    }

    return ret;
}

public void modified()
{
    super();
    BookAmountsEngine_RU::processFacture(NoYes::Yes,
                                         tmpPurchBookVATProcessLogTrans.Marked,
                                         tmpPurchBookVATProcessLogTransOper,
                                         tmpPurchBookVATProcessLogTrans,
                                         tmpPurchBookVATProcessLogTransOper_DS);

    BookAmountsEngine_RU::processFactureAdj(tmpPurchBookVATProcessLogTrans,
                                            tmpPurchBookVATProcessLogTrans_DS,
                                            tmpPurchBookVATProcessLogTransOper);
}

display UnitQty operNotUsedQty(TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper)
{
    return this.operSettledQty(_tmpPurchBookVATProcessLogTransOper) - this.operUsedQty(_tmpPurchBookVATProcessLogTransOper);
}

display UnitQty operSelectedQty(TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper)
{
    UnitQty factureQty;
    if (this.operSelectedAmountInclVAT(_tmpPurchBookVATProcessLogTransOper))
    {
        factureQty = this.operNotUsedQty(_tmpPurchBookVATProcessLogTransOper);
    }
    return factureQty;
}

display UnitQty operUsedQty(TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper)
{
    UnitQty factureQty;
    Factor  factor;
    Amount  factureAmount;
    factureAmount = this.operFactureAmountInclVAT(_tmpPurchBookVATProcessLogTransOper);
    if (factureAmount)
    {
        factureQty = this.operFactureQty(_tmpPurchBookVATProcessLogTransOper) * _tmpPurchBookVATProcessLogTransOper.Percent / 100;
        factor = this.operUsedAmountInclVAT(_tmpPurchBookVATProcessLogTransOper) / factureAmount;
    }
    return factureQty * factor;
}

display UnitQty operSettledQty(TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper)
{
    UnitQty factureQty;
    Factor  factor;
    Amount  factureAmount;
    factureAmount = this.operFactureAmountInclVAT(_tmpPurchBookVATProcessLogTransOper);
    if (factureAmount)
    {
        factureQty = this.operFactureQty(_tmpPurchBookVATProcessLogTransOper) * _tmpPurchBookVATProcessLogTransOper.Percent / 100;
        factor = this.operSettledAmountInclVAT(_tmpPurchBookVATProcessLogTransOper) / factureAmount;
    }
    return factureQty * factor;
}

display UnitQty operFactureQty(TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper)
{
    UnitQty factureQty;
    bookProcessVATEngine.setCursors(tmpPurchBookVATProcessLogTrans, _tmpPurchBookVATProcessLogTransOper);
    factureQty =  bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::FacturedAmount).qty();
    return factureQty ? factureQty : 1;
}

edit UnitQty qty(boolean _set, TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper, UnitQty _qty)
{
    UnitQty lineQty;
    Factor  factor;
    BookStructSmallVATAmounts_RU  operSettledAmounts, operUsedAmounts;
    bookProcessVATEngine.setCursors(tmpPurchBookVATProcessLogTrans, _tmpPurchBookVATProcessLogTransOper);
    lineQty =  bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::FacturedAmount).qty() *
               _tmpPurchBookVATProcessLogTransOper.Percent / 100;

    if (_set && lineQty)
    {
        factor = _qty /lineQty ;
        _tmpPurchBookVATProcessLogTransOper.Percent = _tmpPurchBookVATProcessLogTransOper.Percent * factor;
        operSettledAmounts = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::SettledAmount);
        operUsedAmounts    = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::UsedAmount);
        _tmpPurchBookVATProcessLogTransOper.AmountLiableToVAT = CurrencyExchangeHelper::amount((operSettledAmounts.amountLiableToVAT() - operUsedAmounts.amountLiableToVAT()) * tmpPurchBookVATProcessLogTransOper.Percent / 100);
        element.amountModified(_tmpPurchBookVATProcessLogTransOper, _tmpPurchBookVATProcessLogTransOper.orig());
    }

    return bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::FacturedAmount).qty() *
           _tmpPurchBookVATProcessLogTransOper.Percent / 100;
}

display FactureItemName_RU itemName(TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper)
{
    bookProcessVATEngine.setCursors(tmpPurchBookVATProcessLogTrans, _tmpPurchBookVATProcessLogTransOper);
    return bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::FacturedAmount).itemName();
}

#resAppl
display Integer imgTransType(TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper)
{
    return _tmpPurchBookVATProcessLogTransOper.AllowDelete ? imageList.image(#ImageDefault) :
                                                             imageList.image(#ImageMethodError);
}

public int active()
{
    boolean                             allowEdit = true;
    int                                 ret = super();

    if (ret)
    {
        tmpPurchBookVATProcessLogTransOper_DS.allowDelete(tmpPurchBookVATProcessLogTransOper.RecId && tmpPurchBookVATProcessLogTransOper.AllowDelete);

        switch (tmpPurchBookVATProcessLogTrans.TransType)
        {
            case SalesPurchBookTransType_RU::TaxCorrection :
                tmpPurchBookVATProcessLogTransOper_ds.object(fieldNum(TmpPurchBookVATProcessLogTransOper_RU, LedgerDimension)).allowEdit(false);
                tmpPurchBookVATProcessLogTransOper_ds.object(fieldNum(TmpPurchBookVATProcessLogTransOper_RU, OffsetLedgerDimension)).allowEdit(false);
                break;
            case SalesPurchBookTransType_RU::Prepayment :
                element.setAccessByDS();
                break;

            default :
                allowEdit = tmpPurchBookVATProcessLogTransOper.OffsetLedgerDimension ? true : false;
                tmpPurchBookVATProcessLogTransOper_ds.object(fieldNum(TmpPurchBookVATProcessLogTransOper_RU, LedgerDimension)).allowEdit(allowEdit);
                tmpPurchBookVATProcessLogTransOper_ds.object(fieldNum(TmpPurchBookVATProcessLogTransOper_RU, OffsetLedgerDimension)).allowEdit(allowEdit);
        }
    }

    return ret;
}

public boolean validateDelete()
{
    boolean                                 ret;
    BookStructSmallVATAmounts_RU            currentAmounts;

    ret = super();

    if (ret)
    {
        if (tmpPurchBookVATProcessLogTransOper.AllowDelete == NoYes::No)
        {
            ret = false;
        }
        else
        {
            currentAmounts = new BookStructSmallVATAmounts_RU();

            bookProcessVATEngine.setCursors(tmpPurchBookVATProcessLogTrans, tmpPurchBookVATProcessLogTransOper);

            currentAmounts.addAmounts(tmpPurchBookVATProcessLogTransOper.AmountLiableToVAT + tmpPurchBookVATProcessLogTransOper.TaxAmount,
                                      tmpPurchBookVATProcessLogTransOper.AmountLiableToVAT,
                                      tmpPurchBookVATProcessLogTransOper.TaxAmount);

            bookProcessVATEngine.subOperAmounts(BookVATAmountType_RU::CurrentAmount, currentAmounts, tmpPurchBookVATProcessLogTransOper.Percent);
        }
    }

    return ret;
}

display AmountMST operSelectedTaxAmountVAT(TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper)
{
    if (_tmpPurchBookVATProcessLogTransOper.Marked)
    {
        bookProcessVATEngine.setCursors(tmpPurchBookVATProcessLogTrans, _tmpPurchBookVATProcessLogTransOper);
        return bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::CurrentAmount).taxAmountVAT();
    }
    return 0;
}

display AmountMST operSelectedAmountLiableToVAT(TmpPurchBookVATProcessLogTransOper_RU _tmpPurchBookVATProcessLogTransOper)
{
    if (_tmpPurchBookVATProcessLogTransOper.Marked)
    {
        bookProcessVATEngine.setCursors(tmpPurchBookVATProcessLogTrans, _tmpPurchBookVATProcessLogTransOper);
        return bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::CurrentAmount).amountLiableToVAT();
    }
    return 0;
}

