    tmpPurchBookVATProcessLogTransOper_DS.refresh();
}

public void lookup(FormControl _formControl, str _filterStr)
{
    if (PurchBookVATProcessParameters_RU::find(tmpPurchBookVATProcessLogTransOper.PurchBookVATProcessParameters_RU).IncludeInBook)
    {
        GridLedgerAccount.parmPostingType(LedgerPostingType::Tax);
        groupLedgerOperation_LedgerAccount.parmPostingType(LedgerPostingType::Tax);
    }
    else
    {
        GridLedgerAccount.parmPostingType(LedgerPostingType::None);
        groupLedgerOperation_LedgerAccount.parmPostingType(LedgerPostingType::None);
    }

    super(_formControl, _filterStr);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    if (PurchBookVATProcessParameters_RU::find(tmpPurchBookVATProcessLogTransOper.PurchBookVATProcessParameters_RU).IncludeInBook)
    {
        GridOffsetLedgerAccount.parmPostingType(LedgerPostingType::Tax);
        groupLedgerOperation_OffsetLedgerAccount.parmPostingType(LedgerPostingType::Tax);
    }
    else
    {
        GridOffsetLedgerAccount.parmPostingType(LedgerPostingType::None);
        groupLedgerOperation_OffsetLedgerAccount.parmPostingType(LedgerPostingType::None);
    }

    super(_formControl, _filterStr);
}

public void modified()
{
    BookStructSmallVATAmounts_RU  operSettledAmounts, operUsedAmounts;

    super();

    bookProcessVATEngine.setCursors(tmpPurchBookVATProcessLogTrans, tmpPurchBookVATProcessLogTransOper);
    operSettledAmounts = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::SettledAmount);
    operUsedAmounts    = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::UsedAmount);

    tmpPurchBookVATProcessLogTransOper.AmountLiableToVAT = CurrencyExchangeHelper::amount((operSettledAmounts.amountLiableToVAT() - operUsedAmounts.amountLiableToVAT()) * tmpPurchBookVATProcessLogTransOper.Percent / 100);

    element.amountModified(tmpPurchBookVATProcessLogTransOper, tmpPurchBookVATProcessLogTransOper.orig());

    tmpPurchBookVATProcessLogTransOper_DS.write();
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (PurchBookVATProcessParameters_RU::find(tmpPurchBookVATProcessLogTransOper.PurchBookVATProcessParameters_RU).ProcessingType != tmpPurchBookVATProcessLogTrans.ProcessingType)
        {
            ret = checkFailed(strFmt("@SYS108459",
                                     fieldStr(PurchBookVATProcessParameters_RU, OperationCode),
                                     tmpPurchBookVATProcessLogTrans.ProcessingType));
        }
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    PurchBookVATProcessParameters_RU    parameters;
    SysReferenceTableLookup             sysTableLookup  = SysReferenceTableLookup ::newParameters(tableNum(PurchBookVATProcessParameters_RU), _formReferenceControl, true);
    Query                               query           = new Query();
    QueryBuildDataSource                queryBuildDataSource;

    sysTableLookup.addLookupfield(fieldNum(PurchBookVATProcessParameters_RU, OperationCode));
    sysTableLookup.addLookupfield(fieldNum(PurchBookVATProcessParameters_RU, ProcessingType));
    sysTableLookup.addLookupfield(fieldNum(PurchBookVATProcessParameters_RU, Description));

    queryBuildDataSource = query.addDataSource(tableNum(PurchBookVATProcessParameters_RU));

    findOrCreateRange_W(queryBuildDataSource,
                        fieldNum(PurchBookVATProcessParameters_RU, ProcessingType),
                        queryValue(tmpPurchBookVATProcessLogTrans.ProcessingType));

    sysTableLookup.parmQuery(query);
    parameters = sysTableLookup.performFormLookup();

    return parameters;
}

public void modified()
{
    super();
    element.updateLedgerAccounts();
}

public void modified()
{
    BookStructSmallVATAmounts_RU    operSettledAmounts, operUsedAmounts;
    TaxAmount                       taxAmountNotUsed;

    super();

    bookProcessVATEngine.setCursors(tmpPurchBookVATProcessLogTrans, tmpPurchBookVATProcessLogTransOper);
    operSettledAmounts = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::SettledAmount);
    operUsedAmounts    = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::UsedAmount);
    taxAmountNotUsed   = operSettledAmounts.taxAmountVAT() - operUsedAmounts.taxAmountVAT();

    tmpPurchBookVATProcessLogTransOper.AmountLiableToVAT =  taxAmountNotUsed ?
                                                            CurrencyExchangeHelper::amount((operSettledAmounts.amountLiableToVAT() - operUsedAmounts.amountLiableToVAT()) * tmpPurchBookVATProcessLogTransOper.TaxAmount / taxAmountNotUsed) : 0;

    element.amountModified(tmpPurchBookVATProcessLogTransOper, tmpPurchBookVATProcessLogTransOper.orig());

    tmpPurchBookVATProcessLogTransOper_DS.write();
}

public boolean validate()
{
    boolean ret = super();

    if (ret)
    {
        if (tmpPurchBookVATProcessLogTransOper.TransDate < tmpPurchBookVATProcessLogTrans.PurchBookTransDate)
        {
            error("@GLS104952");
            ret = false;
        }
    }

    return ret;
}

