public void jumpRef()
{
    EmployeeTable_RU::jumpRef(element, tmpPurchBookVATProcessLogTrans.AccountNum);
}

void clicked()
{
    TmpPurchBookVATProcessLogTransOper_RU localTmpPurchBookVATProcessLogTransOper;

    super();

    if (tmpPurchBookVATProcessLogTransOper_DS.anyMarked())
    {
        localTmpPurchBookVATProcessLogTransOper = tmpPurchBookVATProcessLogTransOper_DS.getFirst(true);

        if (! element.checkRDeferrals(localTmpPurchBookVATProcessLogTransOper))
        {
            return;
        }

        while (localTmpPurchBookVATProcessLogTransOper)
        {
            element.createLine(tmpPurchBookVATProcessLogTrans, localTmpPurchBookVATProcessLogTransOper);
            localTmpPurchBookVATProcessLogTransOper = tmpPurchBookVATProcessLogTransOper_DS.getNext();
        }
    }
    else 
    {
        localTmpPurchBookVATProcessLogTransOper = tmpPurchBookVATProcessLogTransOper_QR.get(tableNum(TmpPurchBookVATProcessLogTransOper_RU));

        if (! element.checkRDeferrals(localTmpPurchBookVATProcessLogTransOper))
        {
            return;
        }

        element.createLine(tmpPurchBookVATProcessLogTrans, localTmpPurchBookVATProcessLogTransOper);
    }

    element.updateLedgerAccounts();

    tmpPurchBookVATProcessLogTransOper_DS.write();
    tmpPurchBookVATProcessLogTransOper_DS.research();
}
