[Form]
public class PurchBookTrans_RU extends FormRun
{
    int                             backColor;
    PurchBookTable_RU               purchBookTable;

    Map                             transCache;

    Map                             vatRateCache;
    FactureOperationCodesUpdate_RU  operCodesUpdate;
    Set                             markedLinesSet;

}

public container getModuleCached()
{
    FactureModule_RU    module;
    boolean             isCustTrans, isEmplTrans, isVendTrans;

    if (! transCache.exists(purchBookTrans.RecId))
    {
        module = PurchBookVATProcessLogTrans_RU::transType2FactureModule(purchBookTrans.TransType,
                                                                         purchBookTrans.FactureId,
                                                                         purchBookTrans.FactureDate,
                                                                         purchBookTrans.InvoiceRecIdRef);

        switch (module)
        {
            case FactureModule_RU::Cust                    :
            case FactureModule_RU::TaxCorrectionPurch_Cust :
                isCustTrans = true;
                break;
            case FactureModule_RU::TaxCorrectionPurch      :
            case FactureModule_RU::Vend                    :
                isVendTrans = true;
                break;
            case FactureModule_RU::Empl                    :
                isEmplTrans = true;
                break;
            default                                        :
                isVendTrans = true;
        }

        transCache.insert(purchBookTrans.RecId, [ isCustTrans, isVendTrans, isEmplTrans ]);
    }

    return transCache.lookup(purchBookTrans.RecId);
}

#define.White(255)
public void init()
{
    if (! this.args() || ! this.args().record())
    {
        throw error("@GLS103799");
    }

    switch (this.args().dataset())
    {
        case tableNum(PurchBookTable_RU) :
            purchBookTable = this.args().record();
            break;

        default :
            throw error(strFmt("@SYS19096", element.name(), tableStr(PurchBookTable_RU)));
    }

    if (this.args().caller() && this.args().caller() is FactureOperationCodesUpdate_RU)
    {
        operCodesUpdate = this.args().caller();
        markedLinesSet = Set::create(operCodesUpdate.parmMarkedLinesSet());
    }

    backColor = WinAPI::RGB2int(#White, 0, 0);

    super();

    element.initControls();
    transCache  = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Container);
}

public void run()
{
    super();

    this.updateDesign();
}

void setActiveButtons()
{
    boolean isCustTrans, isEmplTrans, isVendTrans;
    [ isCustTrans, isVendTrans, isEmplTrans ] = this.getModuleCached();

    if (updateBook.visible())
    {
        updateBook.enabled(purchBookTable.isActiveBook());
    }

    vendInvoiceTrans.visible(isVendTrans);
    vendPaymentTrans.visible(isVendTrans);
    vendFacture.visible(isVendTrans);

    custInvoiceTrans.visible(isCustTrans);
    custPaymentTrans.visible(isCustTrans);
    custFacture.visible(isCustTrans);

    emplFacture.visible(isEmplTrans);

    if (menuButtonPrint.visible())
    {
        menuButtonPrint.enabled(purchBookTrans ? true : false);
    }
}

void setLabels()
{
    vatRateCache = BookAmountsEngine_RU::setLabels(vatRateCache,
                                                   purchBookTrans.factureModule(),
                                                   purchBookTrans.FactureId,
                                                   VATType_RU::Full,
                                                   vat20,
                                                   vat20_AmountLiableToVAT20,
                                                   vat20_TaxAmountVAT20);
}

public void closeOk()
{
    if (operCodesUpdate)
    {
        operCodesUpdate.parmMarkedLinesSet(markedLinesSet.pack());
    }

    super();
}

public void initControls()
{
    DictTable dictTable = new DictTable(tableNum(PurchBookTrans_RU));
    int i;
    boolean isOperCodeUpdate = operCodesUpdate ? true : false;

    for (i = 1; i <= dictTable.fieldCnt(); i++)
    {
        if (!dictTable.fieldObject(dictTable.fieldCnt2Id(i)).isSystem())
        {
            PurchBookTrans_DS.object(dictTable.fieldCnt2Id(i)).allowEdit(false);
        }
    }

    PurchBookTrans_DS.object(fieldNum(PurchBookTrans_RU, OperationTypeCodes)).allowEdit(true);

    MarkLine.visible(isOperCodeUpdate);
    FactureOperationTypeCodes.visible(isOperCodeUpdate);

    TotalAmounts.visible(!isOperCodeUpdate);
    updateBook.visible(!isOperCodeUpdate);
    menuButtonPrint.visible(!isOperCodeUpdate);

    CommandButtonOK.visible(isOperCodeUpdate);
    CommandButtonCancel.visible(isOperCodeUpdate);
    MarkAllButton.visible(isOperCodeUpdate);
}

public void updateControls()
{
    correctedFactureGrid.visible(purchBookTrans.JointCorrection);
    correctedFactureExternalId.visible(! purchBookTrans.JointCorrection);
    correctedFactureDate.visible(! purchBookTrans.JointCorrection);

    if (operCodesUpdate)
    {
        PurchBookTrans_DS.object(fieldnum(PurchBookTrans_RU, OperationTypeCodes)).allowEdit(!markedLinesSet.in(purchBookTrans.RecId));
    }
}

public void updateDesign()
{
    element.setActiveButtons();
    element.setLabels();
    element.updateControls();
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = purchBookTrans_Q.dataSourceTable(tableNum(PurchBookTrans_RU));
    qbds.clearDynalinks();

    if (operCodesUpdate)
    {
        this.query(new Query(operCodesUpdate.queryRun().query().pack())).userUpdate(false);
    }
    else
    {
        qbds.addDynalink(fieldNum(PurchBookTrans_RU, PurchBookTable_RU), purchBookTable, fieldNum(PurchBookTable_RU, RecId));
    }
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.updateDesign();
    }

    return ret;
}

public void linkActive()
{
    super();
    element.setActiveButtons();
}

public edit boolean editMarkLine(
    boolean             _set,
    PurchBookTrans_RU   _purchBookTrans,
    boolean             _markLine)
{
    if (_set)
    {
        if (_markLine)
        {
            markedLinesSet.add(_purchBookTrans.RecId);
        }
        else
        {
            markedLinesSet.remove(_purchBookTrans.RecId);
        }
    }

    return markedLinesSet.in(_purchBookTrans.RecId);
}

public display FactureOperationTypeCodes_RU factureOperationTypeCodes(PurchBookTrans_RU _purchBookTrans)
{
    FactureOperationTypeCodes_RU operationTypeCodes;

    if (operCodesUpdate)
    {
        operationTypeCodes = operCodesUpdate.getNewFactureOperationTypeCode(_purchBookTrans.operationTypeCodes);
    }

    return operationTypeCodes;
}

public void jumpRef()
{
    boolean isCustTrans, isEmplTrans, isVendTrans;
    Args    args = new Args();

    [ isCustTrans, isVendTrans, isEmplTrans ] = element.getModuleCached();
    if (isVendTrans)
    {
        super();
    }
    else if (isCustTrans)
    {
        args.record(CustTable::find(purchBookTrans.AccountNum));
        new MenuFunction(menuitemDisplayStr(CustTable), MenuItemType::Display).run(args);
    }
    else if (isEmplTrans)
    {
        EmployeeTable_RU::jumpRef(element, purchBookTrans.AccountNum);
    }
}

public str toolTip()
{
    str     ret;
    boolean isCustTrans, isEmplTrans, isVendTrans;

    [ isCustTrans, isVendTrans, isEmplTrans ] = element.getModuleCached();

    if (isVendTrans)
    {
        ret = super();
    }
    else if (isCustTrans)
    {
        ret = "@SYS7149" + "@GLS111192" +
              purchBookTrans.AccountNum + "@GLS116882" +
              purchBookTrans.AccountName;
    }
    else if (isEmplTrans)
    {
        ret = "@GLS105343" + "@GLS111192" +
              purchBookTrans.AccountNum + "@GLS116882" +
              purchBookTrans.AccountName;
    }
    return ret;
}

void clicked()
{
    xMenuFunction    action  = this.menufunction();
    Args            args    = new Args();

    args.caller(element);
    args.record(purchBookTable);

    action.run(args);

    purchBookTrans_DS.linkActive();
}

void clicked()
{
    xMenuFunction    action = this.menufunction();
    Args            args   = new Args();
    FormRun         fr;

    args.caller(element);
    args.record(purchBookTable ? purchBookTable : purchBookTrans.purchBookTable());
    args.parmObject(purchBookTrans_DS.queryRun().query());

    fr = action.create(args);
    fr.run();
    fr.wait();
}

