FactureModule_RU getModuleCached(PurchBookVATProcessLogTrans_RU _vatProcessLogTrans = vatProcessLogTrans)
{
    FactureModule_RU    module;

    if (moduleCache.exists(_vatProcessLogTrans.RecId))
    {
        module = moduleCache.lookup(_vatProcessLogTrans.RecId);
    }
    else
    {
        module = _vatProcessLogTrans.factureModule();
        moduleCache.insert(_vatProcessLogTrans.RecId, module);
    }

    return module;
}

public void init()
{
    PurchBookTable_RU       bookTable;
    RestoredVATLogTrans_RU  restoredVATLogTrans;
    FactureJour_RU          factureJour;
    Common                  record = this.args().record();

    if (record)
    {
        switch (record.TableId)
        {
            case tableNum(PurchBookTrans_RU) :
                purchBookTrans  = record;
                bookTable       = PurchBookTable_RU::find(purchBookTrans.PurchBookTable_RU);
                fromDate        = bookTable.SalesPurchBookTable_RU::getPreviousBook().ClosingDate;

                if (fromDate)
                {
                    fromDate += 1;
                }

                toDate = bookTable.ClosingDate;
                break;

            case tableNum(RestoredVATLogTrans_RU) :
                restoredVATLogTrans = record;
                mapRecIdRange = Map::create(restoredVATLogTrans.purchBookVATProcessLog());
                break;

            case tableNum(FactureJour_RU) :
                factureJour = record;
                mapRecIdRange = Map::create(factureJour.bookVATProcessLog(ModuleCustVend::Vend));
                break;
        }
    }

    if ( ! toDate)
    {
        toDate = maxDate() - 1;
    }

    moduleCache = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Enum);
    vatCache    = new Map(Types::Container, Types::Class);

    super();
}

public void run()
{
    super();

    if (purchBookTrans)
    {
        this.design().caption("@GLS101123");
    }
}

void setContragentVisible()
{
    FactureModule_RU    module;
    boolean             qtyVisible;

    qtyVisible = processedAmounts ? processedAmounts.isGroupBy() : NoYes::No;

    itemName.visible(qtyVisible);
    qty.visible(qtyVisible);

    groupVendor.visible(false);
    groupCustomer.visible(false);
    groupEmployee.visible(false);

    vendTrans.visible(false);
    custTrans.visible(false);
    emplTrans.visible(false);

    factureVend.visible(false);
    factureCust.visible(false);
    factureEmpl.visible(false);

    if (vatProcessLogTrans.RecId)
    {
        module = element.getModuleCached();

        switch (module)
        {
            case FactureModule_RU::Cust                    :
            case FactureModule_RU::TaxCorrectionPurch_Cust :
                customerAccountNum.value(vatProcessLogTrans.AccountNum);
                custTable_DS.executeQuery();

                groupCustomer.visible(true);
                custTrans.visible(true);
                factureCust.visible(true);
                break;
            case FactureModule_RU::TaxCorrectionPurch      :
            case FactureModule_RU::Vend                    :
                groupVendor.visible(true);
                vendTrans.visible(true);
                factureVend.visible(true);
                break;
            case FactureModule_RU::Empl                    :
                groupEmployee.visible(true);
                emplTrans.visible(true);
                factureEmpl.visible(true);
                break;
            default                                        :
                throw error(Error::wrongUseOfFunction(funcName()));
        }
    }
    else
    {
        groupVendor.visible(true);
    }
}

#define.fieldRecIdName('RecId')
void setRecIdRange(RefTableId _tableId, Query _query)
{
    QueryBuildDataSource qbds;
    QueryBuildRange      qbr;
    int                  idx;
    container            conRecId;

    if (!mapRecIdRange)
        return;

    if (mapRecIdRange.exists(_tableId))
    {
        conRecId = mapRecIdRange.lookup(_tableId);
    }

    if (!conLen(conRecId))
    {
        conRecId += 0;
    }

    qbds = _query.dataSourceTable(_tableId);

    for (idx = 1; idx <= conLen(conRecId); idx++)
    {
        qbr = qbds.addRange(fieldName2id(_tableId, #fieldRecIdName));
        qbr.value(queryValue(conPeek(conRecId, idx)));
        qbr.status(RangeStatus::Hidden);
    }
}

