    super();

    this.initFromPurchBookTrans();

    element.setRecIdRange(this.table(), this.query());
}

private void initFromPurchBookTrans()
{
    Query                query;
    QueryBuildDataSource qbds;

    if (! purchBookTrans)
    {
        return;
    }
    query = this.query();

    qbds = query.dataSourceTable(tableNum(PurchBookVATProcessLogTable_RU));

    qbds = qbds.addDataSource(tableNum(PurchBookVATProcessLogTransOper_RU));
    qbds.joinMode(JoinMode::ExistsJoin);
    qbds.relations(true);

    qbds = qbds.addDataSource(tableNum(PurchBookVATProcessLogTrans_RU));
    qbds.joinMode(JoinMode::ExistsJoin);
    qbds.relations(true);

    qbds.addDynalink(fieldNum(PurchBookVATProcessLogTrans_RU, FactureId),       purchBookTrans, fieldNum(PurchBookTrans_RU, FactureId));
    qbds.addDynalink(fieldNum(PurchBookVATProcessLogTrans_RU, InvoiceRecIdRef), purchBookTrans, fieldNum(PurchBookTrans_RU, InvoiceRecIdRef));
    qbds.addDynalink(fieldNum(PurchBookVATProcessLogTrans_RU, PaymentRecIdRef), purchBookTrans, fieldNum(PurchBookTrans_RU, PaymentRecIdRef));
    qbds.addDynalink(fieldNum(PurchBookVATProcessLogTrans_RU, InvoiceCompany),  purchBookTrans, fieldNum(PurchBookTrans_RU, InvoiceCompany));
    qbds.addDynalink(fieldNum(PurchBookVATProcessLogTrans_RU, PaymentCompany),  purchBookTrans, fieldNum(PurchBookTrans_RU, PaymentCompany));
}

