public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(PurchBookVATProcessLogTrans_RU));
    qbds = qbds.addDataSource(tableNum(PurchBookVATProcessLogTransOper_RU));
    qbds.joinMode(JoinMode::ExistsJoin);
    qbds.relations(false);
    qbds.addLink(fieldNum(PurchBookVATProcessLogTrans_RU, RecId),
                 fieldNum(PurchBookVATProcessLogTransOper_RU, RefRecId));
    qbrLogTableId = qbds.addRange(fieldNum(PurchBookVATProcessLogTransOper_RU, LogTableRefRecId));

    element.setRecIdRange(this.table(), this.query());
}

public int active()
{
    int ret = super();

    if (ret)
    {
        qbrLogTransId.value(queryValue(vatProcessLogTrans.RecId));
        vatProcessLogOperations_ds.executeQuery();

        if (vatCache.exists([vatProcessLogTrans.RecId,vatProcessLogTable.RecId]))
        {
            processedAmounts = vatCache.lookup([vatProcessLogTrans.RecId,vatProcessLogTable.RecId]);
        }
        else
        {
            if (vatProcessLogTable.ActionType == PurchBookVATProcessCancelType_RU::Process)
            {
                processedAmounts = PurchBookVATProcessLogTrans_RU::getLogAmounts(vatProcessLogTrans.RecId, vatProcessLogTable.RecId);
            }
            else
            {
                processedAmounts = PurchBookVATProcessLogTrans_RU::getLogAmounts(vatProcessLogTrans.RecId, vatProcessLogTable.RecId, true, false);
            }
            vatCache.insert([vatProcessLogTrans.RecId,vatProcessLogTable.RecId], processedAmounts);
        }

        element.setLabels();
        element.setContragentVisible();
    }

    return ret;
}

public void jumpRef()
{
    FactureModule_RU module = element.getModuleCached();

    switch (module)
    {
        case FactureModule_RU::Cust                    :
        case FactureModule_RU::TaxCorrectionPurch_Cust :
            custTable_DS.object(fieldNum(CustTable, AccountNum)).jumpRef();
            break;
        case FactureModule_RU::TaxCorrectionPurch      :
        case FactureModule_RU::Vend                    :
            super();
            break;
        case FactureModule_RU::Empl                    :
            EmployeeTable_RU::jumpRef(element, vatProcessLogTrans.AccountNum);
            break;
    }
}

public str toolTip()
{
    str ret;

    FactureModule_RU module = element.getModuleCached();

    switch (module)
    {
        case FactureModule_RU::Cust                    :
        case FactureModule_RU::TaxCorrectionPurch_Cust :
            ret = "@SYS7149" + "@GLS111192" +
                  vatProcessLogTrans.AccountNum + "@GLS116882" +
                  vatProcessLogTrans.AccountName;
            break;

        case FactureModule_RU::TaxCorrectionPurch      :
        case FactureModule_RU::Vend                    :
            ret = super();
            break;

        case FactureModule_RU::Empl                    :
            ret = "@GLS105343" + "@GLS111192" +
                  vatProcessLogTrans.AccountNum + "@GLS116882" +
                  vatProcessLogTrans.AccountName;
            break;
    }

    return ret;
}

