public void init()
{
    QueryBuildRange         qbr;
    QueryBuildDataSource    qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(PurchBookVATProcessLogTransOper_RU));
    qbrLogTransId = qbds.addRange(fieldNum(PurchBookVATProcessLogTransOper_RU, RefRecId));

    qbr = findOrCreateRange_W(qbds, fieldNum(PurchBookVATProcessLogTransOper_RU, Correct));
    qbr.value(queryValue(NoYes::No));

    if (purchBookTrans)
    {
        qbr = qbds.addRange(fieldNum(PurchBookVATProcessLogTransOper_RU, TransDate));
        qbr.value(SysQuery::range(fromDate, toDate));
    }

    element.setRecIdRange(this.table(), this.query());
}

