/// <summary>
///     Sets the state of the vendor account control based on the commitment header and lines data
/// </summary>
void enableVendorAcccountField()
{
    boolean allowEdit = true;

    if (purchCommitmentHeader.RecId)
    {
        allowEdit = PurchCommitmentHeader_PSN::isValidForVendorEditing(purchCommitmentHeader);
    }

    purchCommitmentHeader_ds.object(fieldNum(PurchCommitmentHeader_PSN, VendorAccount)).allowEdit(allowEdit);
    purchCommitmentHeader_ds.object(fieldNum(PurchCommitmentHeader_PSN, VendorAccount)).skip(!allowEdit);
}

public void init()
{
    super();

    if ((element.args().record()) && (element.args().menuItemName() == menuitemDisplayStr(PurchCommitmentForRevise_PSN)))
    {
        element.reviseButtonClicked(element.args().record().RecId);
    }

    hasActiveWorkflow = PurchCommitmentHeader_PSN::hasActiveWorkflowForCompany();
}

/// <summary>
///    Provides form lookup functionality for the <c>AgreementLine</c> table lookups that filter by account
///    type.
/// </summary>
/// <param name="_formReferenceControl">
///    The form reference control for the lookup control
/// </param>
/// <returns>
///    The <c>AgreementLine</c> record that is selected in the lookup.
/// </returns>
public AgreementLine lookupAgreementLine(FormReferenceControl _formReferenceControl)
{
    AgreementLine           agreementLine;
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(AgreementLine), _formReferenceControl, true);
    Query                   lookupQuery;
    QueryBuildDataSource    qbdsAgreementLine,
                            qbdsAgreementHeader;

    // Display the Agreement Sequence number and Vendor account fields in the lookup form.
    sysTableLookup.addLookupMethod(tableMethodStr(AgreementLine, displayAgreementHeaderSequence));
    sysTableLookup.addLookupfield(fieldNum(AgreementLine, LineNumber));
    sysTableLookup.addLookupfield(fieldNum(AgreementLine, ItemId));
    sysTableLookup.addLookupfield(fieldNum(AgreementLine, Category));

    // Create a custom Query that filters on the Type field.
    lookupQuery = new Query();
    qbdsAgreementLine = lookupQuery.addDataSource(tableNum(AgreementLine));
    qbdsAgreementHeader = qbdsAgreementLine.addDataSource(tableNum(PurchAgreementHeader));
    qbdsAgreementHeader.relations(true);

    qbdsAgreementHeader.addOrderByField(fieldNum(PurchAgreementHeader, PurchNumberSequence));
    qbdsAgreementLine.addOrderByField(fieldNum(AgreementLine, LineNumber));

    qbdsAgreementLine.addRange(fieldNum(AgreementLine, IsDeleted)).value(queryValue(NoYes::No));

    if (purchCommitmentHeader.VendorAccount)
    {
        qbdsAgreementHeader.addRange(fieldNum(PurchAgreementHeader, VendAccount)).value(queryValue(purchCommitmentHeader.VendorAccount));
    }

    sysTableLookup.parmQuery(lookupQuery);

    // Return the record selected by the user.
    agreementLine = sysTableLookup.performFormLookup();

    return agreementLine;
}

