/// <summary>
/// Sets the state of the commitment document and its associated source document header for revision.
/// </summary>
/// <param name="_purchCommitmentHeaderRecId">
/// The record ID of the <c>PurchCommitmentHeader_PSN</c> table.
/// </param>
public void reviseButtonClicked(RecId _purchCommitmentHeaderRecId)
{
    if (purchCommitmentHeader.reviseCommitmentDocument(_purchCommitmentHeaderRecId))
    {
        PurchCommitmentHeader_PSN::setWorkflowState(_purchCommitmentHeaderRecId, PurchCommitmentWorkflowApprovalState_PSN::NotSubmitted);
    }
}

/// <summary>
///     Sets the states of the controls based on the header data
/// </summary>
public void updateHeaderControlStates()
{
    boolean isInDraftOrUnderRevisionStatus = (purchCommitmentHeader.DocumentStatus == PurchCommitmentDocumentStatus_PSN::Draft
        || purchCommitmentHeader.DocumentStatus == PurchCommitmentDocumentStatus_PSN::UnderRevision);
    boolean isInWorkFlowReviewState = (purchCommitmentHeader.WorkflowApprovalState == PurchCommitmentWorkflowApprovalState_PSN::InReview);

    purchCommitmentHeader_ds.allowEdit(isInDraftOrUnderRevisionStatus && !isInWorkFlowReviewState
            && !PurchCommitmentHeader_PSN::isCommitmentReferencedOnPurchLine(purchCommitmentHeader.RecId)
            && !PurchCommitmentHeader_PSN::isCommitmentReferencedOnVendInvoiceLine(purchCommitmentHeader.RecId));

    // Delete button
    purchCommitmentHeader_ds.allowDelete(purchCommitmentHeader && purchCommitmentHeader.canDelete());

    // Revise button
    reviseCmdButton.enabled(purchCommitmentHeader.DocumentStatus == PurchCommitmentDocumentStatus_PSN::Complete);

    // Confirm budget reservation button
    buttonBudgetCheck.enabled(!hasActiveWorkflow &&  purchCommitmentLine && isInDraftOrUnderRevisionStatus);

    // Workflow state field
    state_WorkflowApprovalState.visible(hasActiveWorkflow);
    state1_WorkflowApprovalState.visible(state_WorkflowApprovalState.visible());
    state2_WorkflowApprovalState.visible(state_WorkflowApprovalState.visible());

    // Fiscal calendar field
    fiscalCalendarYear.enabled(!purchCommitmentLine);
    transaction1_FiscalCalendarYear.enabled(fiscalCalendarYear.enabled());

    element.updateLineControlStates();
}

/// <summary>
///     Sets the states of the controls based on the line data
/// </summary>
public void updateLineControlStates()
{
    SourceDocumentLine sourceDocumentLine = SourceDocumentLine::find(purchCommitmentLine.SourceDocumentLine);
    boolean isLineReferenced = (purchCommitmentLine && PurchCommitmentLine_PSN::isCommitmentLineReferenced(purchCommitmentLine.RecId));
    boolean isInDraftOrUnderRevisionStatus = (purchCommitmentHeader.DocumentStatus == PurchCommitmentDocumentStatus_PSN::Draft
        || purchCommitmentHeader.DocumentStatus == PurchCommitmentDocumentStatus_PSN::UnderRevision);
    boolean isInWorkFlowReviewState = purchCommitmentHeader.WorkflowApprovalState == PurchCommitmentWorkflowApprovalState_PSN::InReview;
    boolean isCommitmentInEditableState = purchCommitmentHeader && isInDraftOrUnderRevisionStatus && (sourceDocumentLine.AccountingStatus != SourceDocumentLineAccountingStatus::Finalized);
    boolean isNewCommitmentLine = purchCommitmentLine.OriginalAmount == 0;

    PurchCommitmentLineBudgetCheckResult.allowEdit(!isLineReferenced && isCommitmentInEditableState);
    PurchCommitmentLine_LineNumber.allowEdit(!isLineReferenced && isCommitmentInEditableState);
    PurchCommitmentLine_LineDate.allowEdit(
        !isLineReferenced
        && isCommitmentInEditableState
        && isNewCommitmentLine);
    SegmentedEntry.allowEdit(!isLineReferenced && isCommitmentInEditableState);
    PurchCommitmentLine_LineAmount.allowEdit(isCommitmentInEditableState);
    Group_Group.allowEdit(!isLineReferenced && isCommitmentInEditableState);
    PurchCommitmentLine_Comment.allowEdit(!isLineReferenced && isCommitmentInEditableState);

    purchCommitmentLine_ds.allowCreate(purchCommitmentHeader && isInDraftOrUnderRevisionStatus && !isInWorkFlowReviewState);
    newLineGridCmdButton.enabled(purchCommitmentLine_ds.allowCreate());

    purchCommitmentLine_ds.allowDelete(purchCommitmentHeader && !isLineReferenced && isInDraftOrUnderRevisionStatus && !isInWorkFlowReviewState
        && (sourceDocumentLine.AccountingStatus != SourceDocumentLineAccountingStatus::Finalized));
    deleteLineGridCmdButton.enabled(purchCommitmentLine_ds.allowDelete());

    viewBalanceSummary.enabled(purchCommitmentHeader.DocumentStatus != PurchCommitmentDocumentStatus_PSN::Draft);

    closeLineGridButton.enabled(purchCommitmentLine
        && (purchCommitmentHeader.DocumentStatus == PurchCommitmentDocumentStatus_PSN::Complete)
        && (sourceDocumentLine.AccountingStatus != SourceDocumentLineAccountingStatus::Finalized));
}

public int task(int _taskId)
{
    #Task
    int taskResult;

    switch (_taskId)
    {
        case #taskEditRecord:

            if (purchCommitmentHeader.WorkflowApprovalState == PurchCommitmentWorkflowApprovalState_PSN::Approved)
            {
                if (Box::yesNo(strFmt("@SPS2140", purchCommitmentHeader.CommitmentNumber), DialogButton::No) == DialogButton::Yes)
                {
                    // Allow edit only if the dialog result is yes, other wise restrict editing
                    taskResult = super(_taskId);

                    PurchCommitmentHeader_PSN::setWorkflowState(purchCommitmentHeader.RecId, PurchCommitmentWorkflowApprovalState_PSN::NotSubmitted);
                }
            }
            else
            {
                // Allow edit only if workflow is not approved
                taskResult = super(_taskId);
            }
            break;

        default:
            taskResult = super(_taskId);
    }

    return taskResult;
}

