void clicked()
{
    super();

    element.reviseButtonClicked(purchCommitmentHeader.RecId);
    purchCommitmentHeader_ds.research(true);
}

public boolean modified()
{
    boolean isModified;

    isModified = super();

    if (isModified && purchCommitmentHeader.Name && purchCommitmentHeader.CommitmentNumber)
    {
        PurchCommitmentHeader_DS.write();
    }

    return isModified;
}

/// <summary>
/// Validates if the commitment line can be deleted.
/// </summary>
/// <remarks>
/// If any confirmed Budget Reservations for Pre-encumbrance exist for the commitment line an error will be thrown.
/// </remarks>
/// <exception cref="Exception::Error">
/// An error stating that a commitment line having a confirmed Budget Reservation for Pre-encumbrance cannot be deleted, only closed.
/// </exception>
void clicked()
{
    if (purchCommitmentline.existConfirmedBudgetReservation())
    {
        throw error("@SPS2381");
    }
    else
    {
        super();
    }
}

void clicked()
{
    super();

    purchCommitmentLine.finalizeSourceDocumentLine();
    purchCommitmentHeader_ds.research(true);
}

public boolean validate()
{
    boolean isValid;

    isValid = super();
    isValid = isValid && PurchAgreementFinDimensionAccess_PSN::isFinDimensionValueAuthorizedSegmentedEntry(purchCommitmentLine.getAgreementHeaderForAgreementLine().RecId,
                                                                                    0,
                                                                                    this);
    return isValid;
}

public void onSegmentChanged(DimensionControlSegment _segment)
{
    super(_segment);

    PurchAgreementFinDimensionAccess_PSN::isFinDimensionValueAuthorizedSegmentedEntry(purchCommitmentLine.getAgreementHeaderForAgreementLine().RecId,
                                                                                    0,
                                                                                    this);
}

public void jumpRef()
{
    Args            args = new Args();
    PurchAgreementHeader   purchAgreementHeader = purchCommitmentLine.getAgreementHeaderForAgreementLine();

    if (purchAgreementHeader)
    {
        args.caller(this);
        args.record(purchAgreementHeader);
        new MenuFunction(menuitemDisplayStr(PurchAgreement), MenuItemType::Display).run(args);
    }
}
