public void adjustDesign()
{
    boolean canPrint = false;
    boolean canEditTransDate;
    boolean isNFE = FiscalDocModel_BR::find(purchComplementaryInvoiceHeader.Model).IsForNFeFederal;
    PurchComplementaryInvoiceHeader complementaryInvoice;

    if (PurchComplementaryInvoiceHeader.ComplementaryType == InvoiceComplementaryType_BR::Price)
    {
        PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, AdditionalPovertyFundAmount)).visible(false);
        PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, AdditionalICMSAmount)).visible(false);
        PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, AdditionalIPIAmount)).visible(false);
        PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, OriginalLineAmount)).visible(true);
        PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, Amount)).visible(true);
        PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, CorrectedAmount)).visible(true);
        AllocateTaxAmountButton.enabled(false);
    }
    else
    {
        PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, AdditionalPovertyFundAmount)).visible(true);
        PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, AdditionalICMSAmount)).visible(true);
        PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, AdditionalIPIAmount)).visible(true);
        PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, OriginalLineAmount)).visible(true);
        PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, Amount)).visible(false);
        PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, CorrectedAmount)).visible(false);
        AllocateTaxAmountButton.enabled(true);
    }

    if (PurchComplementaryInvoiceHeader.Issuer == FiscalDocumentIssuer_BR::OwnEstablishment)
    {
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, Number)).allowEdit(false);

        Invoice_Series.allowEdit(false);
        InvoiceGroup_Series.allowEdit(false);
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, Series)).allowEdit(false);
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, Model)).allowEdit(false);
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, Specie)).allowEdit(false);
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, AccessKey_BR)).allowEdit(false);
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, FiscalDocumentType)).allowEdit(true);

        if (BrazilParameters::find().ChronologicalInvoiceNumbering &&
            FiscalDocumentType_BR::findRecId(PurchComplementaryInvoiceHeader.FiscalDocumentType).InvoiceIssueDate)
        {
            canEditTransDate = false;
        }
        else
        {
            canEditTransDate = true;
        }
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, DocumentDate)).allowEdit(canEditTransDate);
    }
    else
    {
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, AccessKey_BR)).allowEdit(isNFE);
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, Number)).allowEdit(!isNFE);
        Invoice_Series.allowEdit(!isNFE);
        InvoiceGroup_Series.allowEdit(!isNFE);
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, Series)).allowEdit(!isNFE);
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, Model)).allowEdit(true);
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, Specie)).allowEdit(true);
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, FiscalDocumentType)).allowEdit(false);
        PurchComplementaryInvoiceHeader_DS.object(fieldNum(PurchComplementaryInvoiceHeader, TransDate)).allowEdit(true);
    }

    switch (PurchComplementaryInvoiceHeader.Status)
    {
        case ComplementaryInvoiceStatus::Open:
            DropCancelButton.enabled(false);
            PostButton.enabled(true);
            VoucherButton.enabled(false);
            PurchComplementaryInvoiceHeader_ds.allowEdit(true);
            PurchComplementaryInvoiceHeader_ds.allowDelete(true);
            PurchComplementaryInvoiceLine_ds.allowEdit(true);
            PurchComplementaryInvoiceLine_ds.allowDelete(true);

            if (PurchComplementaryInvoiceHeader.CanceledInvoice)
            {
                PurchComplementaryInvoiceLine_ds.allowEdit(false);
                PurchComplementaryInvoiceLine_ds.allowDelete(false);
            }
            else
            {
                PurchComplementaryInvoiceLine_ds.allowEdit(true);
                PurchComplementaryInvoiceLine_ds.allowDelete(true);
            }

            if (PurchcomplementaryInvoiceHeader.PaymSched)
            {
                buttonPaymentSched.enabled(true);
            }
            else
            {
                buttonPaymentSched.enabled(false);
            }
            break;

        case ComplementaryInvoiceStatus::Canceled:
        case ComplementaryInvoiceStatus::CancelInProgress:
            DropCancelButton.enabled(false);
            PostButton.enabled(false);
            VoucherButton.enabled(true);
            PurchComplementaryInvoiceHeader_ds.allowEdit(false);
            PurchComplementaryInvoiceHeader_ds.allowDelete(false);
            PurchComplementaryInvoiceLine_ds.allowEdit(false);
            PurchComplementaryInvoiceLine_ds.allowDelete(false);
            PurchComplementaryInvoiceLine_ds.allowEdit(false);
            PurchComplementaryInvoiceLine_ds.allowDelete(false);
            buttonPaymentSched.enabled(false);
            break;

        case ComplementaryInvoiceStatus::Posted:
            if (PurchComplementaryInvoiceHeader.CanceledInvoice)
            {
                DropCancelButton.enabled(false);
            }
            else
            {
                DropCancelButton.enabled(true);
            }
            PostButton.enabled(false);
            VoucherButton.enabled(true);
            PurchComplementaryInvoiceHeader_ds.allowEdit(false);
            PurchComplementaryInvoiceHeader_ds.allowDelete(false);
            PurchComplementaryInvoiceLine_ds.allowEdit(false);
            PurchComplementaryInvoiceLine_ds.allowDelete(false);
            PurchComplementaryInvoiceLine_ds.allowEdit(false);
            PurchComplementaryInvoiceLine_ds.allowDelete(false);
            buttonPaymentSched.enabled(false);
            break;
    }

    if (vendPaymModeTable.PaymMode != PurchcomplementaryInvoiceHeader.VendPaymModeTable)
    {
        vendPaymModeTable = VendPaymModeTable::find(PurchcomplementaryInvoiceHeader.VendPaymModeTable);
    }

    buttonSetupPaymFormat.enabled(vendPaymModeTable.ExportOnInvoice);

    for (complementaryInvoice = getFirstSelection(PurchComplementaryInvoiceHeader_DS);
    complementaryInvoice && !canPrint;
    complementaryInvoice = PurchComplementaryInvoiceHeader_DS.getNext())
    {
        if (complementaryInvoice.Status != ComplementaryInvoiceStatus::Open)
        {
            canPrint = true;
        }
    }

    DocumentGroup.enabled(canPrint);
}

public void deleteCalculatedTaxData()
{
    TaxUncommitted::deleteForDocumentHeader(tableNum(PurchComplementaryInvoiceHeader), PurchComplementaryInvoiceHeader.RecId);
    VendPaymSched::deleteVendPaymSched(PurchComplementaryInvoiceHeader.TableId, PurchComplementaryInvoiceHeader.RecId);
}

public void init()
{
    super();

    if (element.args().record())
    {
        PurchComplementaryInvoiceHeader_ds.queryBuildDataSource().clearDynalinks();
    }
}

public void printInvoice(PrintCopyOriginal _action)
{
    GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceBR00026, funcName());

    PurchComplementaryInvoiceHeader complementaryInvoice;
    RecordSortedList                journalList  = new RecordSortedList(tableNum(FiscalDocument_BR));
    FiscalDocument_BR               fiscalDocument;
    Args                            parameters = new Args();

    for (complementaryInvoice = getFirstSelection(PurchComplementaryInvoiceHeader_DS);
    complementaryInvoice;
    complementaryInvoice = PurchComplementaryInvoiceHeader_DS.getNext())
    {
        fiscalDocument = FiscalDocument_BR::findByRef(complementaryInvoice.TableId, complementaryInvoice.RecId);
        if (fiscalDocument)
        {
            journalList.ins(fiscalDocument);
        }
    }

    while (journalList.next(fiscalDocument))
    {
        parameters.record(fiscalDocument);
        parameters.parmEnum(_action);
        parameters.parmEnumType(enumNum(PrintCopyOriginal));
        new MenuFunction(menuitemOutputStr(PurchComplementaryInvoice), MenuItemType::Output).run(parameters);
    }
}

public void setTitleText()
{
    if (PurchComplementaryInvoiceHeader.Number)
    {
        HeaderTitle.text(strFmt('%1 : %2 - %3', PurchComplementaryInvoiceHeader.Number,
                                                PurchComplementaryInvoiceHeader.VendTable,
                                                VendTable::find(PurchComplementaryInvoiceHeader.VendTable).name()));
    }
    else
    {
        HeaderTitle.text(strFmt('%1 - %2', PurchComplementaryInvoiceHeader.VendTable,
                                           VendTable::find(PurchComplementaryInvoiceHeader.VendTable).name()));
    }
}

public void init()
{
    super();

    if (element.args().record() && element.args().record().TableId == tableNum(FiscalDocument_BR))
    {
        FiscalDocument_BR fiscalDocument = element.args().record();
        PurchComplementaryInvoiceHeader complementaryInvoice = PurchComplementaryInvoiceHeader::find(fiscalDocument.RefRecId);
        element.args().lookupRecord(complementaryInvoice);
    }
}

public int active()
{
    int ret;

    ret = super();

    element.adjustDesign();
    element.setTitleText();

    if (PurchcomplementaryInvoiceHeader.DocumentDate)
    {
        DimensionEntryControl.parmNonActiveValueErrorTolerance(ErrorTolerance::Warning);
        DimensionEntryControl.parmActiveValueFilterDate(PurchComplementaryInvoiceHeader.DocumentDate);
    }

    return ret;
}

public void create(boolean _append = false)
{
    Args args = new Args();
    FormRun createComplementaryInvoice;
    Object formRunObj;
    Common newRecord;

    args.name(formStr(FiscalDocumentToComplementSelection_BR));
    args.caller(this);
    args.parmEnum(CustVendType_BR::Vendor);
    args.parmEnumType(enumNum(CustVendType_BR));
    createComplementaryInvoice = classFactory.formRunClass(args);
    createComplementaryInvoice.init();
    createComplementaryInvoice.run();

    if (!createComplementaryInvoice.closed())
        createComplementaryInvoice.wait();

    formRunObj = createComplementaryInvoice;
    newRecord = formRunObj.parmNewComplementaryInvoice();

    if (newRecord)
    {
        super(_append);

        element.args().lookupRecord(newRecord);
        PurchComplementaryInvoiceHeader_DS.research(false);
    }
    else
    {
        // If no fiscal document was chosen to be complemented, close the form
        if (TradeFormHelper::isCalledFromForm(element.args()))
        {
            element.close();
        }
    }
}

