void modified()
{
    super();

    purchcomplementaryinvoiceheader.CashDiscPercent = CashDisc::find(purchcomplementaryinvoiceheader.CashDisc).Percent;

    if (TaxParameters::find().CashDiscOnInvoice)
    {
        purchcomplementaryinvoiceheader.CashDiscDate = dateNull();
        purchcomplementaryinvoiceheader.calcCashDiscDate();
    }

    if (purchcomplementaryinvoiceheader.CashDisc == '')
    {
        purchcomplementaryinvoiceheader_ds.object(fieldNum(purchcomplementaryinvoiceheader, CashDiscDate)).allowEdit(false);
        purchcomplementaryinvoiceheader_ds.object(fieldNum(purchcomplementaryinvoiceheader, CashDiscDate)).skip(true);
    }
    else
    {
        purchcomplementaryinvoiceheader_ds.object(fieldNum(purchcomplementaryinvoiceheader, CashDiscDate)).allowEdit(true);
        purchcomplementaryinvoiceheader_ds.object(fieldNum(purchcomplementaryinvoiceheader, CashDiscDate)).skip(false);
    }
}

