public void write()
{
    boolean recreateTax;

    recreateTax = PurchComplementaryInvoiceLine.Amount != PurchComplementaryInvoiceLine.orig().Amount ||
                  PurchComplementaryInvoiceLine.AdditionalICMSAmount != PurchComplementaryInvoiceLine.orig().AdditionalICMSAmount ||
                  PurchComplementaryInvoiceLine.AdditionalIPIAmount != PurchComplementaryInvoiceLine.orig().AdditionalIPIAmount ||
                  PurchComplementaryInvoiceLine.TaxGroup != PurchComplementaryInvoiceLine.orig().TaxGroup ||
                  PurchComplementaryInvoiceLine.TaxItemGroup != PurchComplementaryInvoiceLine.orig().TaxItemGroup ||
                  PurchComplementaryInvoiceLine.AccountingDistributionTemplate != PurchComplementaryInvoiceLine.orig().AccountingDistributionTemplate;

    if (recreateTax)
    {
        element.deleteCalculatedTaxData();
    }

    super();
}

public void delete()
{
    element.deleteCalculatedTaxData();
    super();
}

public int active()
{
    boolean enableAdditionalPovertyFundAmount;
    int ret;

    ret = super();

    enableAdditionalPovertyFundAmount = this.enableAdditionalPovertyFundAmount();
    PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, AdditionalPovertyFundAmount)).allowEdit(enableAdditionalPovertyFundAmount);
    PurchComplementaryInvoiceLine_ds.object(fieldNum(PurchComplementaryInvoiceLine, AdditionalPovertyFundAmount)).enabled(enableAdditionalPovertyFundAmount);

    return ret;
}

private boolean enableAdditionalPovertyFundAmount()
{
    CustVendCFOPId_BR           cfopId;
    CfopDirectionCheck_BR       cfopDirectionCheck;

    cfopId = CFOPTable_BR::find(PurchComplementaryInvoiceLine.CFOPTable_BR).CFOPId;
    cfopDirectionCheck = CfopDirectionCheck_BR::construct(cfopId);

    return !cfopDirectionCheck.isOtherState();
}

