public void dialogClosed(xFormRun _formRun)
{
    #task

    super(_formRun);
    if (_formRun.closedOk())
    {
        PurchComplementaryInvoiceHeader cancelingDocument = PurchComplementaryInvoiceHeader.getCancelPurchComplInvoice(true);
        element.args().lookupRecord(cancelingDocument);
        PurchComplementaryInvoiceHeader_ds.research(false);
        element.task(#taskSwitchToLineDetailsView);
        element.adjustDesign();
    }
}

void clicked()
{
    PurchComplInvCalcTotals     complInvTotals;
    Args                        args;

    if (PurchComplementaryInvoiceHeader.Status != ComplementaryInvoiceStatus::Open)
    {
        args = new Args();
        args.caller(element);
        args.record(PurchComplementaryInvoiceHeader);

        new MenuFunction(menuitemDisplayStr(TaxTrans), MenuItemType::Display).run(args);
    }
    else
    {
        complInvTotals = new PurchComplInvCalcTotals(PurchComplementaryInvoiceHeader);
        complInvTotals.calc();
        Tax::showTaxes(menuitemDisplayStr(TaxTmpWorkSalesComplInv), complInvTotals.tax(), PurchComplementaryInvoiceHeader);
    }
}

void clicked()
{
    CustVendPaymSched   custVendPaymSched;

    if (purchComplementaryInvoiceHeader.PaymSched && !VendPaymSched::exist(purchComplementaryInvoiceHeader.TableId,purchComplementaryInvoiceHeader.RecId))
    {
        custVendPaymSched = CustVendPaymSched::construct(SysModule::Vend, purchComplementaryInvoiceHeader);
        custVendPaymSched.copyPaymSched(PaymSched::find(purchComplementaryInvoiceHeader.PaymSched));
        purchComplementaryInvoiceHeader.createPaymentSched();
    }
    super();
}

