public Common docCursor()
{
    Common docCursor;

    docCursor = super();

    if (docCursor.TableId == tablenum(InventDim))
    {
        docCursor = purchLineAllVersions;
    }

    return docCursor;
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    PurchLine       purchLine;

    InventMovement  movement = InventMovement::construct(purchLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strfmt("@SYS54195",funcname()));
    }
}

public void init()
{
    QueryBuildDataSource            qbDS;
    PurchConfirmationRequestJour    localPurchConfirmationRequestJour;

    super();

    if (element.args().record() && (element.args().record().TableId == tablenum(PurchConfirmationRequestJour)))
    {
        localPurchConfirmationRequestJour = element.args().record();
        qbDS = this.query().dataSourceTable(this.table());
        qbDS.addRange(fieldnum(PurchTableAllVersions, PurchId)).value(queryValue(localPurchConfirmationRequestJour.getPurchaseOrderId()));
    }
}

/// <summary>
/// Determines what to display in the <c>LineAmountTax</c> field on the lines grid.
/// If 'Prices include sales tax' is true, then show the included tax amount; else show zero.
/// </summary>
/// <param name="_purchLineAllVersions">
/// The current <c>PurchLineAllVersions</c> record.
/// </param>
/// <returns>
/// Shows the included tax amount if the included sales tax parameter is true; else show zero.
/// </returns>
public display LineAmountTax lineAmountTax(PurchLineAllVersions _purchLineAllVersions)
{
    return PurchTableAllVersions.InclTax ? _purchLineAllVersions.ConfirmedTaxAmount : 0;
}

public void init()
{
    QueryBuildDataSource    qbDS;
    QueryBuildRange         range;

    super();

    qbDS = this.query().dataSourceTable(this.table());
    qbDS.addDynalink(fieldnum(PurchLineAllVersions, PurchTableVersionRecId), purchTableAllVersions, fieldnum(PurchTableAllVersions, PurchTableVersionRecId));

    // In case of Delivery sechedule only show the delivery schedule lines
    range = qbDS.addRange(fieldnum(PurchLineAllVersions, LineDeliveryType));
    range.value(SysQuery::valueNot(LineDeliveryType::OrderLineWithMultipleDeliveries));
    range.status(RangeStatus::Hidden);
}

public void init()
{
    QueryBuildDataSource    qbDS;

    super();

    qbDS = this.query().dataSourceTable(this.table());
    qbDS.addDynalink(fieldnum(EcoResCategory, RecId), purchLineAllVersions, fieldnum(PurchLineAllVersions, ProcurementCategory));
}

public void init()
{
    QueryBuildDataSource    qbDS;

    super();

    qbDS = this.query().dataSourceTable(this.table());
    qbDS.addDynalink(fieldnum(PurchConfirmationRequestJour, PurchTableVersion), purchTableAllVersions, fieldnum(PurchTableAllVersions, PurchTableVersionRecId));
    qbDS.addSortField(fieldNum(PurchConfirmationRequestJour, DocumentNumber), SortOrder::Ascending);
    qbDS.addSortField(fieldNum(PurchConfirmationRequestJour, RecId), SortOrder::Ascending);

    this.excludeVendorPortalVendors(qbDS);
}

private void excludeVendorPortalVendors(QueryBuildDataSource _qbDS)
{
    QueryBuildRange qbr = _qbDS.addRange(fieldNum(PurchConfirmationRequestJour, VendVendorCollaborationType));

    qbr.status(RangeStatus::Hidden);
    qbr.value(queryValue(VendVendorCollaborationType::Disabled));
}

void clicked()
{
    Args    args;
    FormRun formRun;

    if (PurchTableAllVersions.IsArchived)
    {
        super();
    }
    else
    {
        args = new Args(formStr(MarkupTrans));
        args.caller(element);
        args.record(PurchTable::find(PurchTableAllVersions.PurchId));
        formRun = classfactory.formRunClass(args);
        formRun.init();
        formRun.run();
        formRun.wait();
    }
}

