/// <summary>
///     It Validates if there is only one Header selected
/// </summary>
/// <param name="_validateOk">
///     Current status of validations
/// </param>
/// <returns>
///     Returns false if selected lines are not all from the same header, otherwise keep the previous status
/// </returns>
public boolean areLinesFromASingleHeader_BR(boolean _validateOk)
{
    int countLines = 0;

    while  select tmpFrmVirtualLines group by Id
    {
        countLines++;
    }

    if (countLines > 1)
    {
        return checkFailed("@GLS1202");
    }

    return _validateOk;
}

/// <summary>
/// Updates a tree control which contain selected for copy operation lines.
/// </summary>
void updateTreeControl()
{
    if (TabPageTreeControl.isExpanded())
    {
        element.buildTreeControl();
    }
}

void buildTreeControl()
{
    int                 treeEntry;
    InvoiceId           invoiceId;
    PurchId             invoicePurchId;

    PackingSlipId       packingSlipId;
    PurchId             packingSlipPurchId;
    PurchId             tmpPurchId;

    PurchId             purchId;
    PurchRFQCaseId      purchRFQCaseId;
    PurchReqId          purchReqId;

    Invoice4PaymId_RU   invoice4PaymId;
    PurchId             invoice4PaymPurchId;

    #ISOCountryRegionCodes
    boolean                     countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    VendInvoice4PaymTrans_RU    localVendInvoice4PaymTrans_RU;

    ItemId          itemId;
    ItemNameDisplay itemName;
    PurchCategory   procurementCategory;
    ItemFreeTxt     name;

    treeControl.deleteAll();

    while select tmpFrmVirtualLines order by Id, TransDate
    {
        switch (tmpFrmVirtualLines.TableNum)
        {
            case tableNum(VendInvoiceTrans):
                VendInvoiceTrans localVendInvoiceTrans = VendInvoiceTrans::findRecId(tmpFrmVirtualLines.RecordNo);
                if (invoiceId != localVendInvoiceTrans.InvoiceId || invoicePurchId != localVendInvoiceTrans.PurchID )
                {
                    treeControl.expand(treeEntry);
                    treeEntry      = treeControl.addItem(0,
                                                         0,
                                                         new FormTreeItem(strFmt('%1 %2 %3 %4',
                                                                                 "@SYS12128",
                                                                                 localVendInvoiceTrans.InvoiceId,
                                                                                 "@SYS15812",
                                                                                 localVendInvoiceTrans.PurchID),
                                                         imageListAppl_Purch.image(#ImageInvoice)));
                    invoiceId      = localVendInvoiceTrans.InvoiceId;
                    invoicePurchId = localVendInvoiceTrans.PurchID;
                }
                itemName            = localVendInvoiceTrans.itemName();
                itemId              = localVendInvoiceTrans.ItemId;
                procurementCategory = localVendInvoiceTrans.ProcurementCategory;
                name                = localVendInvoiceTrans.Name;
                break;

            case tableNum(VendPackingSlipTrans):
                VendPackingSlipTrans localVendPackingSlipTrans = VendPackingSlipTrans::findRecId(tmpFrmVirtualLines.RecordNo);
                tmpPurchId = localVendPackingSlipTrans.vendPackingslipJour().PurchId;

                if (packingSlipId != localVendPackingSlipTrans.PackingSlipId || packingSlipPurchId != tmpPurchId)
                {
                    treeControl.expand(treeEntry);
                    treeEntry           = treeControl.addItem(0,
                                                              0,
                                                              new FormTreeItem(strFmt('%1 %2 %3 %4',
                                                                                      "@SYS11549",
                                                                                      localVendPackingSlipTrans.PackingSlipId,
                                                                                      "@SYS15812",
                                                                                      tmpPurchId),
                                                              imageListAppl_Purch.image(#ImagePackingSlip)));
                    packingSlipId       = localVendPackingSlipTrans.PackingSlipId;
                    packingSlipPurchId  = tmpPurchId;
                }
                itemName            = localVendPackingSlipTrans.itemName();
                itemId              = localVendPackingSlipTrans.ItemId;
                procurementCategory = localVendPackingSlipTrans.ProcurementCategory;
                name                = localVendPackingSlipTrans.Name;
                break;

            case tableNum(PurchLineHistory):
                PurchLineHistory localPurchLineHistory = PurchLineHistory::findRecId(tmpFrmVirtualLines.RecordNo);
                if (purchId != localPurchLineHistory.PurchId)
                {
                    treeControl.expand(treeEntry);
                    treeEntry      = treeControl.addItem(0,
                                                         0,
                                                         new FormTreeItem(strFmt('%1 %2',
                                                                                 "@SYS15812",
                                                                                 localPurchLineHistory.PurchId),
                                                         imageListAppl_Purch.image(#ImagePurchOrder)));
                    purchId = localPurchLineHistory.PurchId;
                }
                itemName            = localPurchLineHistory.itemName();
                itemId              = localPurchLineHistory.ItemId;
                procurementCategory = localPurchLineHistory.ProcurementCategory;
                name                = localPurchLineHistory.Name;
                break;

            case tableNum(PurchLine):
                PurchLine localPurchLine = PurchLine::findRecId(tmpFrmVirtualLines.RecordNo);
                if (purchId != localPurchLine.PurchId)
                {
                    treeControl.expand(treeEntry);
                    treeEntry      = treeControl.addItem(0,
                                                         0,
                                                         new FormTreeItem(strFmt('%1 %2',
                                                                                 "@SYS25545",
                                                                                 localPurchLine.PurchId),
                                                         imageListAppl_Purch.image(#ImageReqRefPurch)));
                    purchId         = localPurchLine.PurchId;
                }
                itemName            = localPurchLine.itemName();
                itemId              = localPurchLine.ItemId;
                procurementCategory = localPurchLine.ProcurementCategory;
                name                = localPurchLine.Name;
                break;

            case tableNum(VendInvoice4PaymTrans_RU):
                if (countryRegion_RU)
                {
                    localVendInvoice4PaymTrans_RU = VendInvoice4PaymTrans_RU::findRecId(tmpFrmVirtualLines.RecordNo);

                    if (invoice4PaymId != localVendInvoice4PaymTrans_RU.Invoice4PaymId || invoice4PaymPurchId != localVendInvoice4PaymTrans_RU.PurchId )
                    {
                        treeControl.expand(treeEntry);
                        treeEntry           = treeControl.addItem(0, 0, new FormTreeItem(strFmt('%1 %2', "@GLS102245", localVendInvoice4PaymTrans_RU.Invoice4PaymId), imageListAppl_Purch.image(#ImageConfirm)));
                        invoice4PaymId      = localVendInvoice4PaymTrans_RU.Invoice4PaymId;
                        invoice4PaymPurchId = localVendInvoice4PaymTrans_RU.PurchId;
                    }
                    itemName            = localVendInvoice4PaymTrans_RU.itemName();
                    itemId              = localVendInvoice4PaymTrans_RU.ItemId;
                    procurementCategory = localVendInvoice4PaymTrans_RU.Category;
                    name                = localVendInvoice4PaymTrans_RU.Name;
                    break;
                }

            case tableNum(PurchRFQCaseLine):
                PurchRFQCaseLine localPurchRFQCaseLine = PurchRFQCaseLine::findRecId(tmpFrmVirtualLines.RecordNo);
                if (purchRFQCaseId != localPurchRFQCaseLine.rfqCaseId)
                {
                    treeControl.expand(treeEntry);
                    treeEntry      = treeControl.addItem(0,
                                                         0,
                                                         new FormTreeItem(strFmt('%1 %2',
                                                                                 "@SYS102679",
                                                                                 localPurchRFQCaseLine.rfqCaseId),
                                                         imageListAppl_Purch.image(#ImageReqRefPurch)));
                    purchRFQCaseId  = localPurchRFQCaseLine.rfqCaseId;
                }
                itemName            = localPurchRFQCaseLine.itemNameDisplay();
                itemId              = localPurchRFQCaseLine.ItemId;
                // No procurement category for RFQ
                procurementCategory = 0;
                name                = localPurchRFQCaseLine.Name;
                break;

            case tableNum(PurchReqLine):
                PurchReqLine localPurchReqLine = PurchReqLine::find(tmpFrmVirtualLines.RecordNo);
                if (purchReqId != localPurchReqLine.PurchReqTable().PurchReqId)
                {
                    treeControl.expand(treeEntry);
                    treeEntry      = treeControl.addItem(0,
                                                         0,
                                                         new FormTreeItem(strFmt('%1 %2',
                                                                                 "@SYS94925",
                                                                                 localPurchReqLine.PurchReqTable().PurchReqId),
                                                         imageListAppl_Purch.image(#ImageItem)));
                    purchReqId  = localPurchReqLine.PurchReqTable().PurchReqId;
                }
                itemName            = localPurchReqLine.itemName();
                itemId              = localPurchReqLine.ItemId;
                procurementCategory = localPurchReqLine.ProcurementCategory;
                name                = localPurchReqLine.Name;
                break;
        }

        str formTreeItemText;
        if (itemId != '')
        {
            formTreeItemText =   this.printCWItem(itemId, tmpFrmVirtualLines.PdsCWQty) 
                               + strFmt('%1 %2 %3 %4', "@SYS14578", tmpFrmVirtualLines.Qty, "@SYS7407", itemName);
        }
        else
        {
            formTreeItemText =   this.printCWItem(itemId, tmpFrmVirtualLines.PdsCWQty)
                               + strFmt('%1 %2 %3 %4 %5', "@SYS14578", tmpFrmVirtualLines.Qty, "@SYS50039", EcoResCategory::find(procurementCategory).Name, name);
        }

        FormTreeItem formTreeItem = new FormTreeItem(formTreeItemText, imageListAppl_Purch.image(#ImageItem));
        treeControl.addItem(treeEntry, 0, formTreeItem);
    }
    treeControl.expand(treeEntry);
}

private str printCWItem(ItemId _itemId, PdsCWInventQty _cwQty)
{
    if (!PdsGlobal::pdsIsCWItem(_itemId))
    {
        return '';
    }

    return strFmt("@SYS312531", "@PDS176", _cwQty) + ' ';
}

boolean canClose()
{
    // <GBR>
    FiscalDocument_BR fiscalDocument;
    // </GBR>

    boolean canClose;

    using (var activityContext = instrumentationLogger.purchCopyingActivities().canClose())
    {
        canClose = super();

        try
        {
            if (element.closedOk() && canClose)
            {
                if (allowCallerUpdate)
                {
                    if (copyHeader.value())
                    {
                        select count(RecId) from tmpFrmVirtualHeader;

                        if (tmpFrmVirtualHeader.RecId > 1)
                        {
                            canClose = checkFailed("@SYS29009");
                        }
                        // <GBR>
                        if (BrazilParameters::isEnabled()
                            && salesPurchCopy == SalesPurchCopy::VoidFiscalDocument_BR)
                        {
                            canClose = this.areLinesFromASingleHeader_BR(canClose);
                        }
                        // </GBR>
                    }

                    else
                    {
                        // <GBR>
                        if (!BrazilParameters::isEnabled()
                        && salesPurchCopy != SalesPurchCopy::VoidFiscalDocument_BR)
                        {
                            // <GBR>
                            delete_from tmpFrmVirtualHeader;
                            // <GBR>
                        }
                        // <GBR>
                    }

                    // <GBR>
                    if (BrazilParameters::isEnabled()
                       && canClose
                       && salesPurchCopy == SalesPurchCopy::VoidFiscalDocument_BR)
                    {
                        select tmpFrmVirtualHeader;
                        fiscalDocument = FiscalDocument_BR::findByRef(tmpFrmVirtualHeader.TableNum,
                                                                      tmpFrmVirtualHeader.RecordNo);

                        canClose = canClose && EFDocUserInterfaceUtil_BR::constructFromFiscalDocument(fiscalDocument).checkReversal();

                        canClose = canClose && PurchTable_BR::ThereIsNoOtherOrderLinkedToFiscDoc(fiscalDocument.RefRecId);

                        canClose = canClose && this.purchOrderCannotCancel2FiscalDocs_BR();

                        if (!copyHeader.value())
                        {
                            delete_from tmpFrmVirtualHeader;
                        }
                    }
                    // </GBR>

                    // <GEERU>
                    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
                    {
                        purchCopyingForm.constructAndInitCopying(tmpFrmVirtualLines,
                                                                 tmpFrmVirtualHeader,
                                                                 qtyFactor.realValue(),
                                                                 reverseSign.value(),
                                                                 recalculateAmount.value(),
                                                                 copyMarkup.value(),
                                                                 copyPrecisely.value(),
                                                                 deleteLines.value(),
                                                                 vendInvoiceJour);
                        canClose = canClose && purchCopyingForm.validateLines();
                    }
                    // </GEERU>

                    if (canClose)
                    {
                        // <GIN>
                        if (TaxParameters::checkTaxParameters_IN())
                        {
                            purchCopyingForm.parmTaxAsPerOrginalInvoice_IN(taxPerOriginalInvoice_IN.value());
                        }
                        // </GIN>
                        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU])
                            || (PurchCopyExchRateInEEURegionFlight::instance().isEnabled()
                            && SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU, #isoCZ, #isoEE, #isoHU, #isoLT, #isoLV, #isoPL])))
                        {
                            purchCopyingForm.parmCopyExchRate(copyExchRate.value());
                        }
                        // <GBR>
                        if (BrazilParameters::isEnabled())
                        {
                            purchCopyingForm.parmReasonCode_BR(reasonTableRef);
                        }
                        // </GBR>
                        purchCopyingForm.constructAndInitCopying(tmpFrmVirtualLines,
                                                                 tmpFrmVirtualHeader,
                                                                 qtyFactor.realValue(),
                                                                 reverseSign.value(),
                                                                 recalculateAmount.value(),
                                                                 copyMarkup.value(),
                                                                 copyPrecisely.value(),
                                                                 deleteLines.value(),
                                                                 vendInvoiceJour);

                        purchCopyingForm.copy();
                        purchCopyingForm.updateCallerDatasource();

                        // <GJP>
                        if (AssetComponent_JP::isAssemblingEnabled()
                            && element.args().caller().name() == formStr(AssetComponentList_JP))
                        {
                            element.refreshCallerForm_JP();
                        }
                        // </GJP>
                    }
                }
            }
        }
        catch
        {
            canClose = false;
        }
    }

    return canClose;
}

public void closeOk()
{
    if (TaxParameters::find().CashDiscOnInvoice && salesPurchCopy)
    {
        switch (salesPurchCopy)
        {
            case SalesPurchCopy::CreditNoteHeader:
                this.creditNoteMessage();
                break;

            case SalesPurchCopy::CreditNoteLines:
                this.creditNoteMessage();
                break;

            case SalesPurchCopy::CopyAllHeader :
                if (reverseSign.value() == true)
                {
                    this.creditNoteMessage();
                }
                break;

            case SalesPurchCopy::CopyJournalHeader:
                if (reverseSign.value() == true)
                {
                    this.creditNoteMessage();
                }
                break;

            default:
                break;
        }
    }
    super();
}

void creditNoteMessage()
{
    VendTable           vendTable;

    if (vendInvoiceJour.InvoiceAmount > 0)
    {
        select firstonly CashDisc from vendTable where vendInvoiceJour.OrderAccount == vendTable.AccountNum;
        if (vendTable.CashDisc)
        {
            info("@SYS99050");
        }
    }
}

NoYes editCopy(boolean     _set,
               Common      _line,
               Num         _id,
               LineNum     _lineNum,
               TransDate   _transDate,
               Qty         _qty,
               NoYes       _copy,
                VersioningTimeStamp  _versionDateTime = utcDateTimeNull(),
                PdsCWInventQty     _pdsCWQty = 0
               )
{
    PurchLineAllVersions localPurchLineAllVersions;
    TableId tableId = _line.TableId;
    RecId recId = _line.RecId;

    if (_line.TableId == tableNum(PurchLineAllVersions))
    {
        localPurchLineAllVersions = _line;
        recId = localPurchLineAllVersions.RecId1;
        tableId = localPurchLineAllVersions.IsArchived ? tableNum(PurchLineHistory) : tableNum(PurchLine);
    }

    element.findTmpFrmVirtual(tmpFrmVirtualLines, tableId, recId);

    copy = tmpFrmVirtualLines ? NoYes::Yes : NoYes::No;

    if (_set)
    {
        if (_copy)
        {
            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
            {
                select count(RecId) from tmpFrmVirtualHeader;
            }
            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]) &&
                tmpFrmVirtualHeader.RecId == 0 &&
                ! purchCopyingForm.validateLine(_line))
            {
                copy   = NoYes::No;
            }
            else
            {
                element.writeTmpFrmVirtual(tmpFrmVirtualLines,
                                       tableId,
                                       recId,
                                       _id,
                                       _versionDateTime,
                                       _lineNum,
                                       _qty,
                                       _transDate
                                       , _pdsCWQty
                                       );

                copy   = NoYes::Yes;
            }
        }
        else
        {
            tmpFrmVirtualLines.delete();
            copy   = NoYes::No;
        }
    }

    if (!markAllLines)
    {
        element.updateTreeControl();
    }

    return copy;
}

NoYes editMarkAll(boolean               _set,
                  Common                _header,
                  Num                   _id,
                  NoYes                 _markAll,
                  FormDataSource        _lineDataSource,
                  VersioningTimeStamp   _versionDateTime = utcDateTimeNull())
{
    Object      lineDataSource = _lineDataSource;
    QueryRun    queryRun;
    NoYes       markAll;

    element.findTmpFrmVirtual(tmpFrmVirtualHeader, _header.TableId, _header.RecId);

    if (tmpFrmVirtualHeader)
    {
        markAll   = NoYes::Yes;
    }

    if (_set)
    {
        markAllLines = true;

        queryRun = new QueryRun(_lineDataSource.query());

        if (_markAll)
        {
            if (purchCopyingForm.validateHeader(_header))
            {
                element.writeTmpFrmVirtual(tmpFrmVirtualHeader,
                                       _header.TableId,
                                       _header.RecId,
                                       _id,
                                       _versionDateTime);

                markAll= NoYes::Yes;

                while (queryRun.next())
                {
                    lineDataSource.copy(true, queryRun.get(_lineDataSource.table()), NoYes::Yes);
                }
            }
            else
            {
                markAll= NoYes::No;
            }
        }
        else
        {
            tmpFrmVirtualHeader.delete();
            markAll= NoYes::No;

            while (queryRun.next())
            {
                lineDataSource.copy(true, queryRun.get(_lineDataSource.table()), false);
            }
        }

        markAllLines = false;
        element.updateTreeControl();
    }

    return markAll;
}

Qty editQty(boolean             _set,
            Common              _line,
            Num                 _id,
            LineNum             _lineNum,
            TransDate           _transDate,
            Qty                 _origQty,
            Qty                 _qty,
            FormDataSource      _lineDataSource,
            UnitOfMeasureSymbol _unitID,
            VersioningTimeStamp _versionDateTime = utcDateTimeNull())
{
    Object              lineDataSource = _lineDataSource;
    Qty                 qty;
    PurchLineAllVersions localPurchLineAllVersions;
    TableId tableId = _line.TableId;
    RecId recId = _line.RecId;

    if (_line.TableId == tableNum(PurchLineAllVersions))
    {
        localPurchLineAllVersions = _line;
        recId = localPurchLineAllVersions.RecId1;
        tableId = localPurchLineAllVersions.IsArchived ? tableNum(PurchLineHistory) : tableNum(PurchLine);
    }

    _qty = decRound( _qty, UnitOfMeasure::unitOfMeasureDecimalPrecision(
        UnitOfMeasure::unitOfMeasureIdBySymbol(_unitID)));

    element.findTmpFrmVirtual(tmpFrmVirtualLines, tableId, recId);

    if (tmpFrmVirtualLines)
    {
        qty = tmpFrmVirtualLines.Qty;
    }
    else
    {
        qty = _origQty;
    }

    if (_set)
    {
        if (!tmpFrmVirtualLines)
        {
            lineDataSource.copy(true, _line, true);
        }

        // If we are setting a value on a single line, an overall qtyFactor of 0 is not intended
        if (qtyFactor.realValue() == 0)
        {
            qtyFactor.realValue(1);
        }

        qty = _qty;

        element.findTmpFrmVirtual(tmpFrmVirtualLines, tableId, recId);

        element.writeTmpFrmVirtual(tmpFrmVirtualLines,
                                   tableId,
                                   recId,
                                   _id,
                                   _versionDateTime,
                                   _lineNum,
                                   qty,
                                   _transDate);
    }

    return qty;
}

public edit ReasonCode editReasonCode(boolean _set, ReasonCode _reasonCode)
{
    ReasonCode      tmpReasonCode = strLRTrim(_reasonCode);

    if (_set)
    {
        if (reasonTableRef == 0)
        {
            reasonTableRef = ReasonTableRef::createFromCode(tmpReasonCode);
        }
        else
        {
            reasonTableRef = ReasonTableRef::updateCode(tmpReasonCode, reasonTableRef);
        }

        // Update the reason comment with the default comment value for the updated reason code.
        // A blank reasonCode will result in a blank comment because a blank reasonCode
        // will return an empty reasonTable buffer.

        this.editReasonComment(true, ReasonTable::find(_reasonCode).Description);
    }
    else
    {
        tmpReasonCode = ReasonTableRef::find(reasonTableRef).Reason;
    }

    return tmpReasonCode;
}

public edit ReasonComment editReasonComment(boolean _set, ReasonComment _reasonComment)
{
    ReasonComment   tmpReasonComment = _reasonComment;

    if (_set)
    {
        // If the ReasonRefRecID is 0, then create a new ReasonTableRef record
        // otherwise, update the existing record.
        if (reasonTableRef == 0)
        {
            reasonTableRef = ReasonTableRef::createFromComment(tmpReasonComment);
        }
        else
        {
            reasonTableRef = ReasonTableRef::updateComment(tmpReasonComment, reasonTableRef);
        }
    }
    else
    {
        tmpReasonComment = ReasonTableRef::find(reasonTableRef).ReasonComment;
    }

    return tmpReasonComment;
}

void findTmpFrmVirtual(TmpFrmVirtual _tmpFrmVirtual, TableId _tableId, RecId _recId)
{
    select _tmpFrmVirtual
        where _tmpFrmVirtual.TableNum   == _tableId &&
              _tmpFrmVirtual.RecordNo   == _recId;
}

void  init()
{
    Args args = element.args();
    int  i;
    FormControl formControl;
    boolean     showQuantityGrp;
    #ISOCountryRegionCodes

    if (!element.args().record() || !element.args().parmEnumType())
    {
        throw error("@SYS22539");
    }

    instrumentationLogger = PurchInstrumentationLogger::createLogger(formStr(PurchCopying));

    salesPurchCopy      = args.parmEnum();

    purchCopyingForm    = PurchCopyingForm::newPurchCopyingForm(salesPurchCopy, args.record(), args.caller());
    allowCallerUpdate   = purchCopyingForm.checkUpdate();
    super();

    purchCopyingForm.initializeControls(element,
                                        qtyFactor,
                                        reverseSign,
                                        copyMarkup,
                                        recalculateAmount,
                                        copyPrecisely,
                                        deleteLines,
                                        copyHeader,
                                        tabPagePurchTable,
                                        tabPageConfirmation,
                                        tabPagePackingSlip,
                                        tabPageInvoice,
                                        tabpageRFQCaseTable,
                                        tabPagePurchReq,
                                        tabPageAgreementPO,
                                        groupReasonCode
                                        );

    // Quantity group fields might all be invisible (for RFQ fx), then we hide the whole group
    for (i=1; i<=quantityGrp.controlCount(); i++)
    {
        formControl = quantityGrp.controlNum(i);
        if (formControl.isVisible())
        {
            showQuantityGrp = true;
        }
    }
    quantityGrp.visible(showQuantityGrp);

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]) || PurchCopyExchRateInEEURegionFlight::instance().isEnabled())
    {
        CopyExchRate.visible(true);
    }

    imageListAppl_Purch = new ImageListAppl_Purch();
    treeControl.setImagelist(imageListAppl_Purch.imageList());

    element.updateDesign(InventDimFormDesignUpdate::Init);

    if (TaxParameters::checkTaxParameters_IN())
    {
        element.setVisible_IN();
    }
}

public Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

/// <summary>
/// Return and or set catch weight quantity field
/// </summary>
/// <param name="_set">Set value to field</param>
/// <param name="_line">Order line record</param>
/// <param name="_id">Record Id</param>
/// <param name="_lineNum">Line number</param>
/// <param name="_transDate">Transaction date</param>
/// <param name="_pdsCWOrigQty">Original CW Qty</param>
/// <param name="_pdsCWQty">Catch weight Qty</param>
/// <param name="_lineDataSource">Line Data source</param>
/// <param name="_itemId">Item </param>
/// <param name="_unitID">Inventory unit Id</param>
/// <param name="_versionDateTime">Transaction date of the version</param>
/// <returns>Returns current value of PdsCWQty field</returns>
PdsCWInventQty pdsCWEditQty(boolean             _set,
                            Common              _line,
                            Num                 _id,
                            LineNum             _lineNum,
                            TransDate           _transDate,
                            PdsCWInventQty      _pdsCWOrigQty,
                            PdsCWInventQty      _pdsCWQty,
                            FormDataSource      _lineDataSource,
                            ItemId              _itemId,
                            UnitOfMeasureSymbol _unitID,
                            VersioningTimeStamp _versionDateTime = utcDateTimeNull())
{
    Object          lineDataSource = _lineDataSource;
    PdsCWInventQty  pdsCWInventQty;

    element.findTmpFrmVirtual(tmpFrmVirtualLines, _line.TableId, _line.RecId);

    if (tmpFrmVirtualLines)
    {
        pdsCWInventQty = tmpFrmVirtualLines.PdsCWQty;
    }
    else
    {
        pdsCWInventQty = _pdsCWOrigQty;
    }

    if (_set)
    {
        if (!tmpFrmVirtualLines)
        {
            lineDataSource.copy(true, _line, true);
        }

        pdsCWInventQty = _pdsCWQty;

        element.writeTmpFrmVirtual(tmpFrmVirtualLines,
                                   _line.TableId,
                                   _line.RecId,
                                   _id,
                                   _versionDateTime,
                                   _lineNum,
                                   PdsCatchWeight::inventQty(_itemId, pdsCWInventQty, _unitID),
                                   DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                                   pdsCWInventQty);
    }

    return pdsCWInventQty;
}

/// <summary>
/// Set status of Catch weight and corresponding standard fields
/// </summary>
/// <param name="_dataSource">Calling data source</param>
void pdsCWEnableFields(Common _dataSource)
{
    boolean cwItem = false;

    switch (_dataSource.TableId)
    {
        case tableNum(PurchLine):
            cwItem = PdsGlobal::pdsIsCWItem(purchLine.ItemId);
            purchLine_Qty.allowEdit(!cwItem);
            purchLine_PdsCWQty.allowEdit(cwItem);
            purchLine_ds.object(fieldNum(PurchLine, PurchQty)).allowEdit(!cwItem);
            purchLine_ds.object(fieldNum(PurchLine, PdsCWQty)).allowEdit(cwItem);
            break;
        case tableNum(VendPackingSlipTrans):
        case (tableNum(VendPackingSlipTransHistory)):
            cwItem = PdsGlobal::pdsIsCWItem(vendPackingSlipTrans.ItemId);
            vendPackingSlipTrans_Qty.allowEdit(!cwItem);
            vendPackingSlipTrans_PdsCWQty.allowEdit(cwItem);
            break;
        case tableNum(PurchLineAllVersions):
            cwItem = PdsGlobal::pdsIsCWItem(purchLineAllVersions.ItemId);
            vendPurchOrderTrans_Qty.allowEdit(!cwItem);
            vendPurchOrderTrans_pdsCWQty.allowEdit(cwItem);
            break;
        case (tableNum(VendInvoiceTrans)):
            cwItem = PdsGlobal::pdsIsCWItem(vendInvoiceTrans.ItemId);
            vendInvoiceTrans_qty.allowEdit(!cwItem);
            vendInvoiceTrans_pdsCWQty.allowEdit(cwItem);
            break;
        case (tableNum(VendInvoice4PaymTrans_RU)):
            cwItem = PdsGlobal::pdsIsCWItem(vendInvoice4PaymTrans_RU.ItemId);
            invoice4PaymTrans_qty.allowEdit(!cwItem);
            invoice4PaymTrans_pdsCWQty.allowEdit(cwItem);
            break;
        case (tableNum(PurchRFQCaseLine)):
            cwItem = PdsGlobal::pdsIsCWItem(purchRFQCaseLine.ItemId);
            caseLine_qty.allowEdit(!cwItem);
            caseLine_pdsCWQty.allowEdit(cwItem);
            break;
        case (tableNum(PurchReqLine)):
            cwItem = PdsGlobal::pdsIsCWItem(purchRFQCaseLine.ItemId);
            purchReqLine_PurchQty.allowEdit(!cwItem);
            purchReqLine_pdsCWQty.allowEdit(cwItem);
            break;
        case (tableNum(AgreementLine)):
            cwItem = PdsGlobal::pdsIsCWItem(purchRFQCaseLine.ItemId);
            agreementLine_Qty.allowEdit(!cwItem);
            agreementLine_pdsCWQty.allowEdit(cwItem);
            break;
    }
}

private boolean purchOrderCannotCancel2FiscalDocs_BR()
{
    PurchTable      targetPurchTable;

    Debug::assert(element.args().record().TableId == tableNum(PurchTable));

    targetPurchTable = element.args().record();

    if (targetPurchTable.purchTable_BR().isCancellingOrReversingFiscalDocument())
    {
        return checkFailed("@SYS4009604");
    }

    return true;
}

public void refreshCallerForm_JP()
{
    Object caller;
    caller = element.args().caller();

    if (formHasMethod(caller, identifierStr(refresh)))
    {
        caller.refresh();
    }
}

private void setVisible_IN()
{
    taxPerOriginalInvoice_IN.visible((salesPurchCopy == SalesPurchCopy::CreditNoteHeader) || (salesPurchCopy == SalesPurchCopy::CreditNoteLines));
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventMovement movement= purchCopyingForm.constructInventMovement();

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

/// <summary>
///     Calculates the total invoiced catch weight quantity for a purchase order line.
/// </summary>
/// <param name="_vendInvoiceTrans">
///     A buffer of the <c>VendInvoiceTrans</c> table.
/// </param>
/// <returns>
///     The catch weight quantity of the current line that has been invoiced.
/// </returns>
public PdsCWPurchQty updatePdsCWQtyTotalInvoiced(VendInvoiceTrans _vendInvoiceTrans)
{
    if (purchLineBuffer.InventTransId != _vendInvoiceTrans.InventTransId)
    {
        purchLineBuffer.InventTransId           = _vendInvoiceTrans.InventTransId;
        purchLineBuffer.ItemId                  = _vendInvoiceTrans.ItemId;

        // The existing method for retrieving the CW invoiced qty requires the CW product to be stocked (even if CW items will always be stocked).
        purchLineBuffer.StockedProduct          = _vendInvoiceTrans.StockedProduct;
        catchWeightQuantityTotalInvoicedCache   = purchLineBuffer.pdsCWInvoicedInTotal();
    }

    return catchWeightQuantityTotalInvoicedCache;
}

/// <summary>
///     Calculates the total invoiced quantity for a purchase order line.
/// </summary>
/// <param name="_vendInvoiceTrans">
///     A buffer of the <c>VendInvoiceTrans</c> table.
/// </param>
/// <returns>
///     The quantity of the current line that has been invoiced.
/// </returns>
public PurchQty updateQtyTotalInvoiced(VendInvoiceTrans _vendInvoiceTrans)
{
    if (purchLineBuffer.InventTransId != _vendInvoiceTrans.InventTransId)
    {
        purchLineBuffer.InventTransId = _vendInvoiceTrans.InventTransId;
        quantityTotalInvoicedCache    = purchLineBuffer.invoicedInTotal();
    }

    return quantityTotalInvoicedCache;
}

void writeTmpFrmVirtual(TmpFrmVirtual        _tmpFrmVirtual,
                        TableId              _tableId,
                        RecId                _recId,
                        Num                  _id,
                        VersioningTimeStamp  _versionDateTime,
                        LineNum              _lineNum = 0,
                        Qty                  _qty = 0,
                        TransDate            _transDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())
                        , PdsCWInventQty     _pdsCWQty = 0
                        )

{
    _tmpFrmVirtual.TableNum     = _tableId;
    _tmpFrmVirtual.RecordNo     = _recId;
    _tmpFrmVirtual.Id           = _id;
    _tmpFrmVirtual.VersionDateTime  = _versionDateTime;
    _tmpFrmVirtual.LineNum      = _lineNum;
    _tmpFrmVirtual.Qty          = _qty;
    _tmpFrmVirtual.TransDate    = _transDate;

    _tmpFrmVirtual.PdsCWQty     = _pdsCWQty;
    _tmpFrmVirtual.write();
}

