public void init()
{
    QueryBuildRange     rangeLineDeliveryType;
    QueryBuildRange     rangeIsDeleted;

    super();

    this.query().dataSourceTable(tableNum(PurchLine)).addRange(fieldNum(PurchLine,IsDeleted)).value(queryValue(NoYes::No));

    rangeLineDeliveryType = this.query().dataSourceTable(tableNum(PurchLine)).addRange(fieldNum(PurchLine,LineDeliveryType));
    rangeLineDeliveryType.value(SysQuery::valueNot(LineDeliveryType::OrderLineWithMultipleDeliveries));
    rangeLineDeliveryType.status(RangeStatus::Hidden);

    rangeIsDeleted = this.query().dataSourceTable(tableNum(PurchLine)).addRange(fieldNum(PurchLine,IsDeleted));
    rangeIsDeleted.value(SysQuery::value(NoYes::No));
    rangeIsDeleted.status(RangeStatus::Hidden);
    this.cacheAddMethod(tableMethodStr(PurchLine,pdsCWUnitId));

    purchLine_ds.allowEdit(allowCallerUpdate);
}

