/// <summary>
///     Initializes the quantity and catch weight quantity mappings.
/// </summary>
public void flushRemainderCache()
{
    creditNoteReturnRemainder = null;
    creditNoteReturnRemainder = new Map(typeName2Type(extendedTypeStr(RecId)),Types::Real);

    creditNoteReturnRemainderCatchWeight = null;
    creditNoteReturnRemainderCatchWeight = new Map(typeName2Type(extendedTypeStr(RecId)),Types::Real);
}

/// <summary>
///     Updates the quantity and catch weight quantity mappings with the returned quantity of the current purchase order line.
/// </summary>
/// <param name="_vendInvoiceTrans">
///     A buffer of the <c>VendInvoiceTrans</c> table.
/// </param>
public void updateRemainderCache(VendInvoiceTrans _vendInvoiceTrans)
{
    PurchQty         quantity;
    PdsCWInventQty   catchWeightQuantity;

    if (!creditNoteReturnRemainder.exists(_vendInvoiceTrans.RecId))
    {
        [quantity,catchWeightQuantity] = PurchLine::returnedLotQty(_vendInvoiceTrans.InventTransId);

        creditNoteReturnRemainder.insert(_vendInvoiceTrans.RecId, quantity);
        creditNoteReturnRemainderCatchWeight.insert(_vendInvoiceTrans.RecId, catchWeightQuantity);
    }
}

/// <summary>
///     Checks if the suggested return catch weight quantity for the current line exceeds the total invoiced quantity.
///     If applicable throws the related warnings.
/// </summary>
/// <param name="_totalInvoiced">
///     The total invoiced quantity.
/// </param>
/// <param name="_qtyAlreadyReturned">
///     The already credited quantity.
/// </param>
/// <param name="_qtySelectForReturn">
///     The quantity selected for crediting.
/// </param>
/// <returns>
///     Always returns true.
/// </returns>
/// <remarks>
///     This method is boolean to enable possible customizations to return false if the quantity being returned is
///     greater than what can be covered by the return lot.
/// </remarks>
public boolean validateReturnPdsCWQty(Qty _totalInvoiced,
                                      Qty _qtyAlreadyReturned,
                                      Qty _qtySelectForReturn)
{
    Qty qtyLeftToReturn = _totalInvoiced - _qtyAlreadyReturned;

    if (_qtySelectForReturn > qtyLeftToReturn)
    {
        // warning: "The catch weight quantity being returned is greater than what can be covered by the return lot."
        warning("@SYS4004079");
        // The total catch weight quantity that was invoiced for the order: %1.
        info(strfmt("@SYS4004080", _totalInvoiced));
        // The catch weight quantity already credited: %1.
        info(strfmt("@SYS4004081", _qtyAlreadyReturned));
        // The catch weight quantity available for crediting: %1.
        info(strfmt("@SYS4004082", qtyLeftToReturn));
    }

    return true;
}

/// <summary>
///     Checks if the suggested return quantity for the current line exceeds the total invoiced quantity.
///     If applicable throws the related warnings.
/// </summary>
/// <param name="_totalInvoiced">
///     The total invoiced quantity.
/// </param>
/// <param name="_qtyAlreadyReturned">
///     The already credited quantity.
/// </param>
/// <param name="_qtySelectForReturn">
///     The quantity selected for crediting.
/// </param>
/// <returns>
///     Always returns true.
/// </returns>
/// <remarks>
///     This method is boolean to enable possible customizations to return false if the quantity being returned is
///     greater than what can be covered by the return lot.
/// </remarks>
public boolean validateReturnQty(Qty _totalInvoiced,
                                 Qty _qtyAlreadyReturned,
                                 Qty _qtySelectForReturn)
{
    Qty qtyLeftToReturn = _totalInvoiced - _qtyAlreadyReturned;

    if (_qtySelectForReturn > qtyLeftToReturn)
    {
        // warning: The quantity being returned is greater than what can be covered by the return lot.
        warning("@SYS53004");
        // The total invoiced quantity for the order: %1.
        info(strfmt("@SYS310092", _totalInvoiced));
        // The quantity already credited: %1.
        info(strfmt("@SYS310093", _qtyAlreadyReturned));
        // The quantity available for crediting: %1.
        info(strfmt("@SYS310094", qtyLeftToReturn));
    }

    return true;
}

public void init()
{
    creditNoteReturnRemainder            = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Real);
    creditNoteReturnRemainderCatchWeight = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Real);

    super();

    this.cacheAddMethod(tableMethodStr(VendInvoiceTrans,pdsCWUnitId));

    vendInvoiceTrans_ds.allowEdit(allowCallerUpdate);
}

public int active()
{
    int ret;

    ret = super();

    element.pdsCWEnableFields(vendInvoiceTrans);

    if (BrazilParameters::isEnabled()
        && salesPurchCopy == SalesPurchCopy::VoidFiscalDocument_BR)
    {
        vendInvoiceTrans_ds.allowEdit(false);
    }

    return ret;
}

