{
}

/// <summary>
///     Updates the line in the lines grid of the form.
///     In case of credit note a call is made to check if quantity being credited is more than original invoice.
/// </summary>
/// <param name="_set">
///     Indicates whether the Mark check box is being changed (checked or un-checked).
/// </param>
/// <param name="_vendInvoiceTrans">
///     The <c>VendInvoiceTrans</c> record which is being copied.
/// </param>
/// <param name="_copy">
///     Indicates whether the Mark check box is true or false.
/// </param>
/// <returns>
///     true if line is marked for copying; otherwise false.
/// </returns>
edit NoYes copy(boolean             _set,
                VendInvoiceTrans    _vendInvoiceTrans,
                NoYes               _copy)
{
    Qty                 quantity;
    PdsCWInventQty      catchWeightQuantity;

    if (vendInvoiceTrans.RecId == _vendInvoiceTrans.RecId) // if it is the selected record
    {
        quantity =  vendInvoiceTrans_qty.realValue();
        catchWeightQuantity = vendInvoiceTrans_pdsCWQty.realValue();
    }
    else
    {
        element.findTmpFrmVirtual(tmpFrmVirtualLines, _vendInvoiceTrans.TableId, _vendInvoiceTrans.RecId);
        quantity = tmpFrmVirtualLines ? tmpFrmVirtualLines.Qty : _vendInvoiceTrans.Qty;
        catchWeightQuantity = tmpFrmVirtualLines ? tmpFrmVirtualLines.PdsCWQty : _vendInvoiceTrans.PdsCWQty;
    }

    if (salesPurchCopy == SalesPurchCopy::CreditNoteHeader || salesPurchCopy == SalesPurchCopy::CreditNoteLines)
    {
        if (_copy)
        {
            this.updateRemainderCache(_vendInvoiceTrans);
            if (PdsGlobal::pdsIsCWItem(_vendInvoiceTrans.ItemId))
            {
                // Validate catch weight quantity
                _set = this.validateReturnPdsCWQty(element.updatePdsCWQtyTotalInvoiced(_vendInvoiceTrans),
                                                creditNoteReturnRemainderCatchWeight.lookup(_vendInvoiceTrans.RecId),
                                                catchWeightQuantity);
            }
            else
            {
                // Validate quantity
                _set = this.validateReturnQty(element.updateQtyTotalInvoiced(_vendInvoiceTrans),
                                            creditNoteReturnRemainder.lookup(_vendInvoiceTrans.RecId),
                                            quantity);
            }
        }
    }

    return element.editCopy(_set,
                            _vendInvoiceTrans,
                            _vendInvoiceTrans.InvoiceId,
                            _vendInvoiceTrans.LineNum,
                            _vendInvoiceTrans.InvoiceDate,
                            quantity,
                            _copy
                            , utcDateTimeNull()
                            , catchWeightQuantity
                            );
}

edit PurchInvoicedQty qty(boolean            _set,
             VendInvoiceTrans   _vendInvoiceTrans,
             Qty                _qty)
{
    // Validate quantity if the current line is a credit note line
    if (_set && (salesPurchCopy == SalesPurchCopy::CreditNoteHeader || salesPurchCopy == SalesPurchCopy::CreditNoteLines))
    {
        this.updateRemainderCache(_vendInvoiceTrans);
        _set = this.validateReturnQty(element.updateQtyTotalInvoiced(_vendInvoiceTrans),
                                      creditNoteReturnRemainder.lookup(_vendInvoiceTrans.RecId),
                                      _qty);
    }

    return element.editQty(_set,
                           _vendInvoiceTrans,
                           _vendInvoiceTrans.InvoiceId,
                           _vendInvoiceTrans.LineNum,
                           _vendInvoiceTrans.InvoiceDate,
                           _vendInvoiceTrans.Qty,
                           _qty,
                           vendInvoiceTrans_ds,
                           _vendInvoiceTrans.PurchUnit);
}

