/// <summary>
/// Return and or set Catch weight quantity field
/// </summary>
/// <param name="_set">Set value to field</param>
/// <param name="_vendInvoiceTrans">Current VendInvoiceTrans record</param>
/// <param name="_pdsCWQty">Current catch weight quantity</param>
/// <returns>Current Catch weight qty</returns>
edit PdsCWInventQty pdsCWQty(
    boolean            _set,
    VendInvoiceTrans   _vendInvoiceTrans,
    PdsCWInventQty     _pdsCWQty)
{
    // Validate catch weight quantity if the current line is a credit note line
    if (_set && (salesPurchCopy == SalesPurchCopy::CreditNoteHeader || salesPurchCopy == SalesPurchCopy::CreditNoteLines))
    {
        this.updateRemainderCache(_vendInvoiceTrans);
        _set = this.validateReturnPdsCWQty(element.updatePdsCWQtyTotalInvoiced(_vendInvoiceTrans),
                                           creditNoteReturnRemainderCatchWeight.lookup(_vendInvoiceTrans.RecId),
                                           _pdsCWQty);
    }

    return element.pdsCWEditQty(_set,
        _vendInvoiceTrans,
        _vendInvoiceTrans.InvoiceId,
        _vendInvoiceTrans.LineNum,
        _vendInvoiceTrans.InvoiceDate,
        _vendInvoiceTrans.PdsCWQty,
        _pdsCWQty,
        vendInvoiceTrans_ds,
        _vendInvoiceTrans.ItemId,
        _vendInvoiceTrans.PurchUnit);
}

