void init()
{
    if (salesPurchCopy == SalesPurchCopy::CreditNoteLines ||
        salesPurchCopy == SalesPurchCopy::CreditNoteHeader)
    {
        this.autoSearch(false);
    }
    else if (salesPurchCopy == SalesPurchCopy::CopyJournalLines ||
        salesPurchCopy == SalesPurchCopy::CopyJournalHeader)
    {
        //For 'Copy Journal', the Confirmations tab is the visible tab, and pageactivated would not be invoked during form load.
        //Hence setting autosearch to true here to trigger VendPurchOrderJour.ExecuteQuery during form load.
        //Also setting isVendPurchOrderJourLoaded to avoid rerunning the query.
        this.autoSearch(true);
        isVendPurchOrderJourLoaded = true;
    }

    super();

    this.query().dataSourceTable(this.table()).clearDynalinks();
    purchCopyingForm.vendPurchOrderJour_ds_Init(this);

    vendPurchOrderJour_ds.allowEdit(allowCallerUpdate);
}

