edit NoYes purchOrderMarkAll(boolean            _set,
                             VendPurchOrderJour _vendPurchOrderJour,
                             NoYes              _markAll)
{
    return element.editMarkAll(_set,
                               _vendPurchOrderJour,
                               _vendPurchOrderJour.PurchId,
                               _markAll,
                               purchLineAllVersions_ds,
                               purchTableAllVersions.VersionDateTime);
