edit NoYes agreementMarkAll(boolean           _set,
                          AgreementHeader   _agreementHeader,
                          NoYes             _markAll)
{
    PurchAgreementHeader  purchAgreementHeaderLocal = AgreementHeader::find(_agreementHeader.RecId);

    return element.editMarkAll(_set,
                               _agreementHeader,
                               purchAgreementHeaderLocal.PurchNumberSequence,
                               _markAll,
                               agreementLine_ds);
}

public void init()
{
    super();
    agreementLine_ds.allowEdit(allowCallerUpdate);
}

edit PurchOrderedQty qty(boolean             _set,
                         AgreementLine      _agreementLine,
                         Qty                 _qty)
{
    PurchAgreementHeader            purchAgreementHeaderLocal;
    AgreementHeader                 agreementheaderLocal;
    AgreementLineQuantityCommitment agreementLineQuantityCommitmentLocal = AgreementLineQuantityCommitment::find(_agreementLine.RecId);

    agreementheaderLocal = _agreementLine.agreementHeader();

    select  PurchNumberSequence
    from    purchAgreementHeaderLocal
    where   purchAgreementHeaderLocal.RecId == agreementheaderLocal.RecId;

    return element.editQty(_set,
                           _agreementLine,
                           purchAgreementHeaderLocal.PurchNumberSequence,
                           agreementLine_ds.getPosition(),
                           DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                           agreementLineQuantityCommitmentLocal.CommitedQuantity?agreementLineQuantityCommitmentLocal.CommitedQuantity:agreementLineVolumeCommitment.CommitedAmount,
                           _qty,
                           agreementLine_ds,
                           agreementLineQuantityCommitmentLocal.ProductUnitOfMeasure);
}

void write()
{
    // Do not write
}

edit NoYes copy(boolean             _set,
                AgreementLine       _agreementLine,
                NoYes               _copy)
{
    AgreementLineQuantityCommitment agreementLineQuantityCommitmentLocal = AgreementLineQuantityCommitment::find(_agreementLine.RecId);
    PurchAgreementHeader            purchAgreementHeaderLocal = AgreementHeader::find(_agreementLine.agreementHeader().RecId);

    return element.editCopy(_set,
                            _agreementLine,
                            purchAgreementHeaderLocal.PurchNumberSequence,
                            agreementLine_ds.getPosition(),
                            DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                            agreementLineQuantityCommitment.CommitedQuantity,
                            _copy);
}

edit PdsCWInventQty cwQty(boolean           _set,
                         AgreementLine      _agreementLine,
                         PdsCWInventQty     _cwQty)
{
    PurchAgreementHeader            purchAgreementHeaderLocal;
    AgreementHeader                 agreementheaderLocal;
    AgreementLineQuantityCommitment agreementLineQuantityCommitmentLocal = AgreementLineQuantityCommitment::find(_agreementLine.RecId);

    agreementheaderLocal = _agreementLine.agreementHeader();

    select  PurchNumberSequence
    from    purchAgreementHeaderLocal
    where   purchAgreementHeaderLocal.RecId == agreementheaderLocal.RecId;

    return element.pdsCWEditQty(_set,
                           _agreementLine,
                           purchAgreementHeaderLocal.PurchNumberSequence,
                           agreementLine_ds.getPosition(),
                           DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                           agreementLineQuantityCommitmentLocal.PdsCWCommitedQuantity,
                           _cwQty,
                           agreementLine_ds,
                           _agreementLine.ItemId,
                           agreementLineQuantityCommitmentLocal.ProductUnitOfMeasure);
}

int active()
{
    int             ret;

    ret = super();

    element.pdsCWEnableFields(agreementLine);

    return ret;
}

