public void init()
{
    QueryBuildDataSource    qbDS;

    super();

    qbDS = this.query().dataSourceTable(this.table());
    qbDS.addDynalink(fieldNum(PurchLineAllVersions, PurchTableVersionRecId), purchTableAllVersions, fieldNum(PurchTableAllVersions, PurchTableVersionRecId));
    purchLineAllVersions_ds.allowEdit(allowCallerUpdate);
}

edit NoYes copy(boolean             _set,
                PurchLineAllVersions _purchLineAllVersions,
                NoYes               _copy)
{
    VendPurchOrderJour localVendPurchOrderJour;

    localVendPurchOrderJour = _purchLineAllVersions.joinParent();
    return element.editCopy(_set,
                            _purchLineAllVersions,
                            localVendPurchOrderJour.PurchaseOrderId,
                            any2real(_purchLineAllVersions.LineNumber),
                            _purchLineAllVersions.DeliveryDate,
                            _purchLineAllVersions.PurchQty,
                            _copy,
                            _purchLineAllVersions.VersionDateTime,
                            _purchLineAllVersions.PdsCWQty
                            );
}

edit PurchOrderedQty qty(boolean                _set,
             PurchLineAllVersions   _purchLineAllVersions,
             Qty                    _qty)
{
    VendPurchOrderJour localVendPurchOrderJour;

    localVendPurchOrderJour = _purchLineAllVersions.joinParent();

    return element.editQty(_set,
                           _purchLineAllVersions,
                           localVendPurchOrderJour.PurchaseOrderId,
                           any2real(_purchLineAllVersions.LineNumber),
                           _purchLineAllVersions.DeliveryDate,
                           _purchLineAllVersions.PurchQty,
                           _qty,
                           purchLineAllVersions_ds,
                           _purchLineAllVersions.PurchUnit,
                             _purchLineAllVersions.VersionDateTime
                         );
}

