display AmountCur lineAmount(PurchLineAllVersions _purchLineAllVersions)
{
    return purchTableAllVersions.InclTax?_purchLineAllVersions.LineAmount-_purchLineAllVersions.ConfirmedTaxAmount:_purchLineAllVersions.LineAmount;
}

