public void expand()
{
    //For performance reasons, vendpurchorderjour_ds is only executed when the tab is active the first time.
    if (!isVendPurchOrderJourLoaded)
    {
        vendpurchorderjour_ds.executeQuery();
        isVendPurchOrderJourLoaded = true;
    }
    super();
}

public void initialize()
{
    //For performance reasons, vendpurchorderjour_ds is only executed when the tab is active the first time.
    if (!isVendPurchOrderJourLoaded)
    {
        vendpurchorderjour_ds.executeQuery();
        isVendPurchOrderJourLoaded = true;
    }
    super();
}

public void pageActivated()
{
    //For performance reasons, vendpurchorderjour_ds is only executed when the tab is active the first time.
    if (!isVendPurchOrderJourLoaded)
    {
        vendpurchorderjour_ds.executeQuery();
        isVendPurchOrderJourLoaded = true;
    }
    super();
}

