[Form]
public class PurchCreateOrder extends FormRun
{
    PurchTableType          purchTableType;
    PurchTableForm          purchTableForm;

    PurchCreateOrderForm    purchCreateOrderForm;
    PurchAgreementId        purchAgreementId;

    NumberSeqFormHandler    numberSeqFormHandlerPurchId;
    boolean                 transferCurrencyAndDeliveryAddressFromAgreement;
    private boolean projOpsEnabled;
    private boolean purchOrderEnabledForProjOps;

    // <GEERU>
    #ISOCountryRegionCodes
    boolean countryRegion_RU;
    // </GEERU>

    // Please notice
    // This field control PurchTable_contactPersonId is hidden in the form, but must be avalible
    // in order to make it possible to have lookup on contact-NAME

    BankLCImportFeatureChecker      bankLCImportFeatureChecker;
    BankLGFeatureChecker            bankLGFeatureChecker;

    boolean isPurchTableEnabled_W;

}

boolean canClose()
{
    if (purchTableType && !element.closedOk())
    {
        purchTableType.formMethodClose();
    }

    if (element.closedOk()
        && (!purchTable_ds.validateWrite()
            || (vendorName.allowEdit() && !vendorName.validate())))
    {
        return false;
    }

    purchTable_ds.write();

    return true;
}

void changeType(PurchaseType _purchaseType)
{
    if (purchTableType)
    {
        numberSeqFormHandlerPurchId = purchTableType.parmNumberSeqFormHandlerPurchId();
    }

    purchCreateOrderForm = PurchCreateOrderForm::construct(_purchaseType, purchTableForm.project());

    if (purchTableForm.project())
    {
        purchTableType = new PurchTableType_Project(purchTable);
    }
    else
    {
        purchTableType  = purchTable.type();
    }

    purchTableType.parmNumberSeqFormHandlerPurchId(numberSeqFormHandlerPurchId);
}

/// <summary>
/// Configs the Bank Document Controls.
/// </summary>
public void configBankDocumentControls()
{
    boolean enableDocumentType = false;

    if (bankLCImportFeatureChecker.checkFeatureEnabled()
        || bankLGFeatureChecker.checkFeatureEnabled())
    {
        enableDocumentType = purchTable.type().mayBankDocumentTypeBeModified();
        purchTable_DS.object(fieldNum(PurchTable, BankDocumentType)).enabled(enableDocumentType);
    }
}

private void removePurchAgreementLink()
{
    if (purchTable.OrderAccount)
    {
        if (transferCurrencyAndDeliveryAddressFromAgreement)
        {
            purchTable.initFromVendTable(VendTable::find(purchTable.OrderAccount));
        }
        transferCurrencyAndDeliveryAddressFromAgreement = false;
        purchTable.MatchingAgreement = 0;
    }
}

private boolean canPurchAgreementBeLinked(PurchAgreementId _purchAgreementId)
{
    if (!_purchAgreementId)
    {
        return false;
    }

    PurchAgreementHeader purchAgreementHeader = PurchAgreementHeader::findAgreementId(_purchAgreementId);

    if (!purchAgreementHeader)
    {
        checkFailed(strFmt("@SYS112224",
                        _purchAgreementId, "@SYS309959", tableId2pname(tableNum(PurchAgreementHeader))));
        return false;
    }

    if ((purchTable.OrderAccount
        && purchAgreementHeader.VendAccount
        && purchTable.OrderAccount != purchAgreementHeader.VendAccount)||
        (purchTable.InvoiceAccount
        && purchAgreementHeader.purchAgreementHeaderDefault().VendorInvoiceAccount
        && purchAgreementHeader.purchAgreementHeaderDefault().VendorInvoiceAccount != purchTable.InvoiceAccount))
    {
        checkFailed("@GLS115616");
        return false;
    }

    return true;
}

edit PurchAgreementId editPurchAgreementId(boolean _set, PurchAgreementId _purchAgreementId)
{
    PurchAgreementHeader    purchAgreementHeader;
    AgreementHeaderDefault  agreementHeaderDefault;
    #ISOCountryRegionCodes

    if (_set)
    {
        if (_purchAgreementId != purchAgreementId)
        {
            purchAgreementHeader = PurchAgreementHeader::findAgreementId(_purchAgreementId);

            if (purchAgreementHeader.RecId)
            {
                if (!this.canPurchAgreementBeLinked(_purchAgreementId))
                {
                    this.removePurchAgreementLink();
                    return purchAgreementId;
                }

                agreementHeaderDefault = AgreementHeaderDefault::findAgreementHeader(purchAgreementHeader.RecId);
                if (purchTable.ProjId
                    && agreementHeaderDefault.Project
                    && purchTable.ProjId != agreementHeaderDefault.Project)
                {
                    info("@SYS334900");
                    return purchAgreementId;
                }

                if (purchAgreementHeader.canReleaseAgreement(purchTable.DeliveryDate))
                {
                    transferCurrencyAndDeliveryAddressFromAgreement = true;
                    purchAgreementId = _purchAgreementId;
                    if (Box::yesNo("@SYS311926",DialogButton::Yes) == DialogButton::Yes)
                    {
                        purchTable.LanguageId = purchAgreementHeader.Language;

                        purchTable.initFromPurchAgreementHeader(purchAgreementHeader, true, true);

                        purchTable_CurrencyCode.update();
                        purchTable_ds.refresh();
                    }
                    else
                    if (countryRegion_RU)
                    {
                        purchTable.DefaultDimension = purchTable.copyDimension(purchAgreementHeader.DefaultDimension);
                    }
                }
                else
                {
                    transferCurrencyAndDeliveryAddressFromAgreement = false;
                    throw error("@SYS103075");
                }
            }
            else if (!_purchAgreementId)
            {
                purchAgreementId = _purchAgreementId;
                transferCurrencyAndDeliveryAddressFromAgreement = false;
                if (countryRegion_RU)
                {
                    purchTable.DefaultDimension = AgreementHeaderExt_RU::clearAgreementDimension(purchTable.DefaultDimension);
                }
            }
        }
    }

    return purchAgreementId;
}

void enableFieldsActive_IN()
{
    boolean isCustomEnabled = TaxParameters::isCustomsEnable_IN();

    purchTable_W_ds.object(fieldNum(PurchTable_W, CustomsImportOrder_IN)).visible(isCustomEnabled);
    purchTable_ds.object(fieldNum(PurchTable, EximPorts_IN)).visible(isCustomEnabled && PurchTable_W.CustomsImportOrder_IN);
}

void init()
{
    isPurchTableEnabled_W = PurchTable_W::isExtensionEnabled();

    super();

    if (! element.args().caller())
    {
        throw error("@SYS22539");
    }

    bankLCImportFeatureChecker = BankLCImportFeatureChecker::construct();
    bankLGFeatureChecker       = BankLGFeatureChecker::construct();

    purchTable_DS.object(fieldNum(PurchTable, BankDocumentType)).visible(
        bankLCImportFeatureChecker.checkFeatureEnabled()
            || bankLGFeatureChecker.checkFeatureEnabled());

    purchTableForm          = element.args().caller();
    purchCreateOrderForm    = PurchCreateOrderForm::construct(purchTableForm.defaultPurchaseType(),purchTableForm.project());

    TaxVATNumTable::enableLookupVATNum(purchTable_VATNum);
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        element.enableFieldsActive_IN();
    }
    countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    if (countryRegion_RU)
    {
        element.design().controlName(formControlStr(PurchCreateOrder, editPurchAgreementId)).visible(false);
    }

    if (purchTableForm.parmNumberSeqFormHandlerPurchId() != null)
    {
        numberSeqFormHandlerPurchId = purchTableForm.parmNumberSeqFormHandlerPurchId();
        numberSeqFormHandlerPurchId.parmFormDataSource(purchTable_ds);
    }

    projOpsEnabled = ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled();
    purchOrderEnabledForProjOps = ProjProjectOperationsIntegrationFeatureHelper::isPurchaseOrderEnabledWithProjOps(projOpsEnabled);
    PurchTable_ds.object(fieldNum(PurchTable, ProjId)).visible(!projOpsEnabled || purchOrderEnabledForProjOps);

    if (PurchIsSupplierShipmentDatesControlsVisibleFlight::instance().isEnabled())
    {
        PurchTable_RequestedShipDate.visible(PurchParameters::find().IsSupplierShipmentDatesEnabled);
    }
}

void modifyForm()
{
    boolean showVATNum = purchCreateOrderForm.showVATNum();

    purchTable_ds.object(fieldNum(PurchTable, PurchId)).allowEdit(purchCreateOrderForm.allowEditPurchId());
    purchTable_ds.object(fieldNum(PurchTable, PurchId)).skip(!purchCreateOrderForm.allowEditPurchId());

    purchTable_ds.object(fieldNum(PurchTable, ReturnItemNum)).visible(purchCreateOrderForm.returnItemNum());

    purchTable_ds.object(fieldNum(PurchTable, ProjId)).allowEdit(purchCreateOrderForm.editProjId(purchTableForm.projId()));
    purchTable_ds.object(fieldNum(PurchTable, ProjId)).skip(!purchCreateOrderForm.editProjId(purchTableForm.projId()));

    purchTable_ds.object(fieldNum(PurchTable, vatNum)).visible(showVATNum);
    groupTax.visible(showVATNum);

    purchTable_ds.object(fieldNum(PurchTable, PurchaseType)).allowEdit(purchTableForm.editPurchaseType());
    purchTable_ds.object(fieldNum(PurchTable, PurchaseType)).skip(!purchTableForm.editPurchaseType());

    element.configBankDocumentControls();
}

public void orderAccountModified()
{
    PurchTable_RU       purchTable_RU;

    if (purchTable.OrderAccount)
    {
        purchTable_ds.object(fieldNum(PurchTable, OneTimeVendor)).allowEdit(false);
        purchTable_ds.object(fieldNum(PurchTable, OneTimeVendor)).skip(true);
    }
    else
    {
        purchTable_ds.object(fieldNum(PurchTable, OneTimeVendor)).allowEdit(true);
        purchTable_ds.object(fieldNum(PurchTable, OneTimeVendor)).skip(false);
    }

    var vendor = VendTable::find(purchTable.OrderAccount);
    purchTable_ds.object(fieldNum(PurchTable, ChangeRequestRequired)).allowEdit(PurchParameters::find().ChangeRequestAllowOverride && vendor.ChangeRequestOverride && vendor.ChangeRequestAllowOverride);

    if (countryRegion_RU)
    {
        purchTable.DefaultDimension = AgreementHeaderExt_RU::clearAgreementDimension(purchTable.DefaultDimension);

        purchTable_RU = purchTable.PurchTable_RU();
        purchTable_RU.AgreementHeaderExt_RU = 0;
        purchTable.packPurchTable_RU(purchTable_RU);
    }

    if (purchTable.MatchingAgreement)
    {
        this.removePurchAgreementLink();
    }

    groupInterCompany.enabled(purchTable.vendTable_OrderAccount().interCompanyTradingRelationActive());
    vendorName.text(purchTable.vendorName());
    if (purchTable.OrderAccount != purchTable.InvoiceAccount)
    {
        invoiceName.text(purchTable.invoiceName());
    }
    else
    {
        // No need to look up the invoice name if the account is not different from the order account as it will cause a RPC.
        invoiceName.text(vendorName.text());
    }

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        this.enableFieldsBasedOnEPZCode();
        this.enableFieldsActive_IN();
    }

    purchTable_ds.refresh();
    purchTable_ds.rereadReferenceDataSources();
    if (isPurchTableEnabled_W)
    {
        purchTable_W_ds.refresh();
        purchTable_W_ds.rereadReferenceDataSources();
    }
}

void run()
{
    VendAccount accountNum;

    purchTable_ds.create();
    element.modifyForm();

    super();

    accountNum = element.args().parm();
    if (accountNum != '')
    {
        purchTable.OrderAccount = accountNum;
        purchTable.transferOrderAccount();
        element.orderAccountModified();
    }
    else
    {
        purchTable_orderAccount.setFocus();
    }

    if (!purchTableType)
    {
        element.close();
        throw Exception::Error;
    }
}

private void enableFieldsBasedOnEPZCode()
{
    TaxGSTEPZCodeHelper epzHelper = TaxGSTEPZCodeHelper::construct(purchTable);

    if (epzHelper.isSEZ())
    {
        PurchTable_WithIGSTPayment_IN.visible(true);
        PurchTable_WithIGSTPayment_IN.allowEdit(true);
    }
    else if (epzHelper.isDeemedExport())
    {
        PurchTable_WithIGSTPayment_IN.visible(true);
        PurchTable_WithIGSTPayment_IN.allowEdit(false);
    }
    else
    {
        PurchTable_WithIGSTPayment_IN.visible(false);
    }
}

