/// <summary>
/// Determines if the financial dimension must be copied when writing.
/// </summary>
/// <returns>true if the dimension must be copied; otherwise, false.</returns>
[Replaceable]
protected boolean mustCopyFinancialDimensionOnWrite()
{
    return Box::yesNo("@Proj:Project_PurchaseOrderHeaderDimension", DialogButton::No) == DialogButton::Yes;
}

void write()
{
    if (!element.closedOk())
    {
        return;
    }

    try
    {
        purchTableType.formMethodDataSourceWrite(element, purchTable_ds);

        // We keep for now the SysExtensionSerializerMap::packAll(), even if it is not used anymore.
        // In the future the SysExtension Framework will be revamped and we will perform the change then.
        boolean hasExensionTableEnabled = PurchTable::hasExensionTableEnabled();
        if (hasExensionTableEnabled)
        {
            purchTable.SysExtensionSerializerMap::packAll();
        }

        PurchAgreementHeader purchAgreementHeader;
        AgreementReleaseHeaderMatch agreementReleaseHeaderMatch;

        if (purchTable.ProjId)
        {
            ProjParameters projParameters = ProjParameters::find();

            if (this.mustCopyFinancialDimensionOnWrite())
            {
                purchTable.DefaultDimension = purchTable.copyDimension(ProjTable::find(purchTable.ProjId).DefaultDimension);
            }
            // To merge sales order dimensions to related purchase order when take financial dimensions from vendor on purchase order is disabled on project parameters form.
            else if (projParameters.CreateItemReqForPO == NoYes::Yes && projParameters.UseFinancialDimensionsFromVendorOnPOHeader == NoYes::No)
            {
                ProjTable projTable = purchTable.projTable();
                SalesTable salesTable;
                if (ProjItemRequirementMultipleFundingSourcesFeatureHelper::isFeatureEnabled())
                {
                    salesTable = SalesTable::findOrCreateSalesTable_ItemReqWhenMFSFeatureEnabled(projTable.currencyId(), projTable.ProjId);
                }
                else
                {
                    salesTable = projTable.salesTable_ItemReq(projTable.currencyId());

                    if (!salesTable && (ProjFundingSource::findCustAccount(projTable.ProjInvoiceProjId) || projTable.CustAccount))
                    {
                        salesTable = projTable.createSalesTable_ItemReq(projTable.currencyId());
                    }
                }

                if (salesTable)
                {
                    purchTable.DefaultDimension = LedgerDimensionDefaultFacade::serviceMergeDefaultDimensions(salesTable.DefaultDimension, purchTable.DefaultDimension);
                }
            }
        }

        ttsbegin;

        if (purchTable.ProjId && !purchTable.isProjectSalesItemReqPO())
        {
            purchTable.initFromProjInvoiceTable();
        }

        if (purchAgreementId)
        {
            purchAgreementHeader = PurchAgreementHeader::findAgreementId(purchAgreementId);
            purchTable.MatchingAgreement = purchAgreementHeader.RecId;
            // <GEERU>
            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
            {
                PurchAgreementHeaderExt_RU purchAgreementHeaderExt = PurchAgreementHeaderExt_RU::findByPurchAgreementHeader(purchAgreementHeader.RecId);

                PurchTable_RU purchTable_RU = purchTable.purchTable_RU();
                purchTable_RU.AgreementHeaderExt_RU = purchAgreementHeaderExt.RecId;
                purchTable.packPurchTable_RU(purchTable_RU);

                purchTable.DefaultDimension = purchTable.copyDimension(purchAgreementHeader.DefaultDimension);
            }
            // </GEERU>
        }

        purchTable.insert(false);

        if (purchAgreementHeader.RecId)
        {
            agreementReleaseHeaderMatch.initFromAgreementHeader(purchAgreementHeader);
            agreementReleaseHeaderMatch.initFromPurchTable(purchTable);
            agreementReleaseHeaderMatch.insert();
        }

        ttscommit;

        // We keep for now the SysExtensionSerializerMap::packAll(), even if it is not used anymore.
        // In the future the SysExtension Framework will be revamped and we will perform the change then.
        if (hasExensionTableEnabled)
        {
            purchTable.SysExtensionSerializerMap::unpackAll();
        }
    }
    catch (Exception::Error)
    {
        purchTable.RecId = 0;
        element.close();
        throw Exception::Error;
    }

    purchTable_ds.refresh();

    // We keep for now the SysExtensionSerializerMap::packAll(), even if it is not used anymore.
    // In the future the SysExtension Framework will be revamped and we will perform the change then.
    if (PurchTable::hasExensionTableEnabled())
    {
        // In case the purchTable table buffer has been re-read after being inserted the Packedextensions column is cleared.
        // Extension tables added outside of the form context will not be found by the call to SysExtensionSerializerMap::packAll() and
        // have to be manually added back.
        if (PurchTable.PackedExtensions == conNull())
        {
            if (SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(PurchTable_Intrastat)))
            {
                this.packExtensionTable(tableNum(PurchTable_Intrastat));
            }

            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBR]))
            {
                this.packExtensionTable(tableNum(PurchTable_BR));
            }
        }

        purchTable.SysExtensionSerializerMap::packAll();
    }

    purchTableForm.purchTable(purchTable);
}

private void packExtensionTable(TableId _extensiontableId)
{
    purchTable.SysExtensionSerializerMap::packExtensionTable(
        purchTable.SysExtensionSerializerMap::getExtensionTable(_extensiontableId, false));
}

boolean validateWrite()
{
    boolean ret;

    if (!element.closedOk())
    {
        return true;
    }

    if (PurchTable::find(purchTable.PurchId))
    {
        return checkFailed(strFmt("@SYS23485",purchTable.PurchId));
    }

    if (purchOrderEnabledForProjOps && purchTable.ProjId && purchTable.InterCompanyOrder)
    {
        return checkFailed(strFmt("@Proj:IntercompanyNotSupportedProjOpsPO"));
    }

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN])
        && TaxParameters::find().Customs_IN)
    {
        if (purchTable_W.CustomsImportOrder_IN    == NoYes::Yes &&
            purchTable.PurchaseType             != PurchaseType::Purch)
        {
            return checkFailed("@GLS5766");
        }
    }
    ret = super();

    if (ret && !purchTableType.formMethodDataSourceValidateWrite(element, purchTable_ds))
    {
        ret = false;
    }

    if (purchAgreementId)
    {
        if (!element.canPurchAgreementBeLinked(purchAgreementId))
        {
            element.removePurchAgreementLink();
            ret = false;
        }
    }

    return ret;
}

