Method.Source:
    [Form]
    public class PurchDeliverySchedule extends FormRun
    {
        PurchTableForm_DeliverySchedule purchTableForm;
        PurchTable                      purchTable;
        boolean                         dataChanged;
        PurchLine                       callerPurchLine;
        RecId                           tmpSourceDocumentLineRecId;
}

public void close()
{
    xSysLastValue::saveLast(purchTableForm);

    super();
}

public void closeCancel()
{
    if (!dataChanged || Box::yesNo("@SYS107523", DialogButton::No) == DialogButton::Yes)
    {
        super();
    }

    this.rereadCaller();
}

public void closeOk()
{
    if (PdsGlobal::pdsIsCWItem(purchLine.ItemId))
    {
        if (!this.remainingCWQty()
            || Box::yesNo("@PDS1436",DialogButton::Yes) == DialogButton::Yes)
        {
            if (purchTableForm.closeOk(purchLine_ds))
            {
                super();
            }
        }
    }
    else
    {
        // Remaining quantity is not 0. Do you want to update the order line of the delivery schedule?
        if (!this.remainingQty()
            || Box::yesNo("@SYS131412",DialogButton::Yes) == DialogButton::Yes)
        {
            if (purchTableForm.closeOk(purchLine_ds))
            {
                super();
            }
        }
    }
}

public void  init()
{
    if (! element.args().caller()
        ||  ! element.args().record())
    {
       throw error("@SYS22539");
    }

    tmpSourceDocumentLineRecId = (select maxof(RecId) from sourceDocumentLine).RecId + 1;

    super();
}

display PdsCWInventQty remainingCWQty()
{
    return purchTableForm.pdsGetCWQty() - this.totalCWQty();
}

display PurchQty remainingQty()
{
    return purchTableForm.getOrderLineQty() - this.totalQty();
}

private void rereadCaller()
{
    FormObjectSet formObjectSet;

    formObjectSet = FormDataUtil::getFormDataSource(callerPurchLine);
    if (formObjectSet)
    {
        formObjectSet.reread();
    }
}

public void run()
{
    super();
    purchLine_PurchQty.setFocus();
}

display PdsCWInventQty  totalCWQty()
{
    PdsCWInventQty  qty;
    PurchLine       localPurchLine;

    localPurchLine.setTmp();
    localPurchLine.setTmpData(purchLine_ds.cursor());

    while select RecId, PdsCWQty from localPurchLine
    {
        qty += (localPurchLine.RecId == purchLine.RecId ? purchLine.PdsCWQty : localPurchLine.PdsCWQty);
    }

    // for the new delivery line as long as we don't have the recid will be 0 thus different than localPurchLine.RecId and
    // the localPurchLine.PdsCWQty will be 0, this is why we need additional logic.
    if (!purchLine.RecId)
    {
        qty += purchLine.PdsCWQty;
    }

    return qty;
}

display PurchQty totalQty()
{
    PurchQty qty;
    PurchLine localPurchLine;

    localPurchLine.setTmp();
    localPurchLine.setTmpData(purchLine_ds.cursor());

    while select RecId, PurchQty from localPurchLine
    {
        qty += (localPurchLine.RecId == purchLine.RecId ? purchLine.PurchQty : localPurchLine.PurchQty);
    }

    // for the new delivery line as long as we don't have the recid will be 0 thus different than localPurchLine.RecId and
    // the localPurchLine.PurchQty will be 0, this is why we need additional logic.
    if (!purchLine.RecId)
    {
        qty += purchLine.PurchQty;
    }

    return qty;
}

public void linkActive()
{
    if (element.args().record().TableId == tableNum(PurchLine))
    {
        callerPurchLine = element.args().record();
        purchTable = callerPurchLine.purchTable();

        purchTableForm = PurchTableForm::construct(PurchTableFormId::DeliverySchedule, element.args().record());
        purchTableForm.purchLine_Init(purchLine);

        TabPageCharges.visible(callerPurchLine.LineDeliveryType == LineDeliveryType::OrderLine);
        dataChanged = false;
    }

    super();
}

public boolean leaveRecord(boolean _forceUpdate = false)
{
    boolean ret;

    if (!purchLine.RecId)
    {
        _forceUpdate = true;
    }

    ret = super(_forceUpdate);

    return ret;
}

public boolean validateDelete()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (purchLine.registered())
        {
            ret = checkFailed(strFmt("@SYS19413", StatusReceipt::Registered));
        }

        if (purchLine.arrived())
        {
            ret = checkFailed(strFmt("@SYS19413", StatusReceipt::Arrived));
        }
    }
    return ret;
}

public int active()
{
    int     ret;

    ret = super();

    purchLine_ds.allowCreate(purchTableForm.createLineAllowed(purchLine));
    purchLine_ds.allowEdit(purchTableForm.editLineAllowed(purchLine));
    purchLine_ds.allowDelete(purchTableForm.deleteLineAllowed(purchLine));

    purchTableForm.interCompanySetLineAccess(purchLine);
    purchTableForm.directDeliverySetLineAccess(purchLine);

    purchLine_LineNumber.allowEdit(purchTableForm.editLineNumberAllowed(purchLine));

    return ret;
}

public void initValue()
{
    purchTableForm.purchLine_InitValue(purchLine);

    super();
}

void  create(boolean append = true)
{
    purchTableForm.purchLine_CreatePreSuper(purchLine);

    if (purchTable.checkUpdate())
        super(append);

    purchLine_PurchQty.setFocus();

    purchLine_ds.refresh();
}

public void write()
{
    NumberSeq                               numberSeq;
    PurchLineWritePreSuperInputContract     inputContractPre = PurchLineWritePreSuperInputContract::construct();
    PurchLineWritePreSuperOutputContract    outputContractPre;
    PurchLineWritePostSuperInputContract    inputContractPost = PurchLineWritePostSuperInputContract::construct();

    inputContractPre.parmPurchLine(purchLine);
    outputContractPre   = purchTableForm.purchLine_WritePreSuper(inputContractPre);

    if (outputContractPre.parmPurchTableCheckUpdate())
    {
        purchLine.IsModified = NoYes::Yes;

        if (purchLine.RecId)
        {
            purchLine.doUpdate();
        }
        else
        {
            numberSeq = NumberSeq::newGetNum(InventParameters::numRefInventTransId());
            purchLine.InventTransId = numberSeq.num();
            // setting to a dummy temporary sourceDocumentLine to avoid index violations on the temp table.
            // SourceDocumentLine will be created and its RecId assigned in purchLine.insert()
            purchLine.SourceDocumentLine = tmpSourceDocumentLineRecId;
            tmpSourceDocumentLineRecId++;

            purchLine.doInsert();
        }

        inputContractPost.parmPurchLine(purchLine);
        purchTableForm.purchLine_WritePostSuper(inputContractPost);

        dataChanged = true;

        purchLine_ds.reread();
        purchLine_ds.refresh();
    }
    else
    {
        purchLine_ds.reread();
    }
}

public void delete()
{
    if (purchLine.RecId)
    {
        if (purchLine.checkDelete(true, purchTable))
        {
            super();
            dataChanged = true;
        }
    }
    else
    {
        super();
    }
}

public boolean validateWrite()
{
    boolean     ret;
    PurchLine   purchLineOrig;

    ret = super();

    if (ret && purchLine.PurchQty < 0)
    {
        // Quantity cannot be negative on a delivery line.
        ret = checkFailed("@SYS131411");
    }

    if (ret && purchLine.RecId)
    {
        purchLineOrig = purchLine.orig();

        if (purchLineOrig && abs(purchLine.PurchQty)  <  abs(purchLineOrig.PurchQty) && purchLine.PurchQty * purchLineOrig.PurchQty >= 0 )
        {
            if (purchLine.RemainPurchPhysical * purchLineOrig.RemainPurchPhysical   < 0 ||
                purchLine.RemainPurchPhysical * purchLineOrig.RemainPurchFinancial  < 0)
            {
                // Quantity cannot be reduced because the remaining update quantity changes sign.
                ret = checkFailed("@SYS25508");
            }
        }
    }

    if (ret)
    {
        ret = purchTableForm.purchLine_ValidateWrite(purchLine);
    }

    return ret;
}

edit DlvScheduleMarkupConversionMode parmMarkupConversionMode(boolean                         _set,
                                                              DlvScheduleMarkupConversionMode _dlvScheduleMarkupConversionMode)
{
    return _set ? purchTableForm.parmMarkupConversionMode(_dlvScheduleMarkupConversionMode)
        : purchTableForm.parmMarkupConversionMode();
}

public boolean validate()
{
    return true;
}

public void modified()
{
    if (purchLine.PurchQty < 0)
        error("@SYS131411");

    super();

    PurchLine::modifyPurchQty(purchLine,purchLine.inventDim());

    purchLine_ds.refresh();
}
