public void activate(boolean _active)
{
    super(_active);

    if (purchParmUpdate.CustomsImportOrderType_IN != CustomsImportOrderType_IN::ImportOrder)
    {
        specQty.delete("@GLS5652");
    }
}

public boolean allowEditMarkupTrans()
{
    return element.isChargeOnBOEEnabled();
}

/// <summary>
/// This method is used to change the editability of PurchParmTable_PackingSlipIdForUpdate_W control.
/// </summary>
void allowEditPackingSlips_W()
{
    purchParmTable_PackingSlipIdForUpdate_W.allowEdit(specQty.selectionAsPurchUpdate() == PurchUpdate::PackingSlip);
    if (documentStatus == DocumentStatus::PackingSlip)
    {
        purchParmTable_PackingSlipIdForUpdate_W.allowEdit(purchParmTable.DocAutoNumbering_LT);
    }
}

void batch(boolean batchable)
{
    buttonBatch.visible(batchable);
}

void  calcTotals(PurchParmTable calcPurchParmTable = purchParmTable)
{
    calcPurchParmTable.reread();
    calcPurchParmTable.initTotals();

    if (calcPurchParmTable.RecId == purchParmTable.RecId)
    {
        // <GEERU>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAllandRU))
        {
            purchFormLetter.initOfficials_RU(calcPurchParmTable);
        }
        // </GEERU>
        purchParmTable.write();

        // Daatsource PurchParmLine must be set to Linktype = active
        searchParmLine = false;
        purchParmTable_ds.active();
        searchParmLine = true;
    }
    else
    {
        calcPurchParmTable.write();
    }
}

public void closeCancel()
{
    using (SysErrorMessageHelpScope scope = SysErrorMessageHelpScope::newErrorContextByFormAndAction(formStr(PurchEditLines), enum2Symbol(enumNum(DocumentStatus), element.documentStatus)))
    {
        super();
    }
}

boolean  canClose()
{
    boolean ret = super();

    if (ret)
    {
        if (boxFormOnceIsActive)
            return false;

        if (FormletterJournalParmIdMappedFlight::instance().isEnabled()
            && purchFormLetter.batchInfo().parmBatchExecute()
            && printOut.selection() == Printout::After)
        {
            var formLetterTypeInstance = FormletterTypeBase::singletonFromFormletterType(FormletterType::Purchase);

            if (formLetterTypeInstance)
            {
                FormletterJournal formletterJournal = formLetterTypeInstance.initFormletterJournal(documentStatus);

                boolean parmIdFieldMapped = SysDictField::isFieldMapped(tableStr(FormletterJournal),
                                                                        tableId2Name(formletterJournal.TableId),
                                                                        fieldStr(FormletterJournal, ParmId));

                if (!parmIdFieldMapped)
                {
                    warning(strFmt('@SCM:PrintAfterIsNotSupported', purchEditLinesForm.editingCaption()));
                    return false;
                }
            }

        }
        if (! this.closedOk())
        {
            if (editLinesChanged)
            {
                if (Box::yesNo("@SYS26912", DialogButton::Yes) != DialogButton::Yes)
                    return false;
            }
        }
        else
        {
            // <GEEU>
            if (countryRegion_LTLV && docNumberingCode_LT.isVisible() && ! docNumberingCode_LT.text())
            {
                return checkFailed(strFmt("@SYS26332", fieldPName(LtInvoiceAutoNumberingTable, NumberingCode)));
            }
            // </GEEU>
            if (this.mustCheckCloseDialog() &&!purchFormLetter.checkCloseDialog())
            {
                purchParmUpdate_ds.executeQuery();
                return false;
            }

            if (ret && this.isBatchReadOnly())
            {
                ret = checkFailed(strFmt("@SCM:PurchEditLines_BatchStatusCannotSave", BatchStatus::Hold));
            }
        }
    }

    return ret;
}

