[Hookable(false)]
public container pack()
{
    return [#CurrentVersion, #CurrentList];
}

[Hookable(false)]
public boolean unpack(container packedClass)
{
    Integer version = conPeek(packedClass, 1);

    switch (version)
    {
        case #CurrentVersion:
            [version, #CurrentList] = packedClass;
            break;
        default :
            return false;
    }

    return true;
}

