void  closeOk()
{
    //<GEERU>
    PurchParmLine   purchParmLineSelect;
    PurchParameters purchParameters;
    int             i = 0;
    //</GEERU>
    Args            argsCaller;

    if (purchParmUpdate)
        purchParmUpdate_ds.write();

    if (purchParmTable.RecId)
        purchParmTable_ds.write();

    if (purchParmSubTable)
        purchParmSubTable_ds.write();

    if (purchParmLine)
    {
        isWritingPurchParmLineInCloseOK = true;
        purchParmLine_ds.write();
        isWritingPurchParmLineInCloseOK = false;
    }

    purchFormLetter.initParameters(purchParmUpdate.data(),
                                   printout.selection(),
                                   printFormletter.value(),
                                   usePrintManagement.value(),
                                   tamVendPrintOptionsCheckBox.value());

    purchFormLetter.printProductLabels(printProductLabels.value());
    purchFormLetter.printShelfLabels(printShelfLabels.value());
    if (printProductLabels.value() || printShelfLabels.value())
    {
        argsCaller = this.args();
        argsCaller.caller(element);
        purchFormLetter.parmArgs(argsCaller);
    }

    purchFormLetter.printSalesFormLetter(printSalesFormLetter.value());
    purchFormLetter.editLinesChanged(editLinesChanged);
    purchFormLetter.reArrangeNow(reArrangeNow);

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        purchFormLetter.printInventBaileeReport_RU(printInventBaileeReport_RU.value());
        purchFormLetter.parmReceiptListDeviationType_RU(printReceiptListDeviationType_RU.selection());
        purchFormLetter.parmPreserveReceiptStatement(preserveReceiptStatement.value());
        purchEditLinesForm.parmReceiptListFormType_RU(printReceiptListFormType_RU.selection());
        xSysLastValue::saveLast(purchEditLinesForm);
    }
    // </GEERU>
    // <GEEU>
    if (countryRegion_LTLV)
    {
        purchFormLetter.getPurchFormLetterContract().parmLtDocNumberingCode(docNumberingCode_LT.text());
    }
    // </GEEU>

    // <GIN>
    if (isTaxParametersMarked && isPackingSlip)
    {
        purchFormLetter.printGRN_IN(printGoodReceiptNote_IN.value());
    }
    // </GIN>

    // <GEERU>
    if (isPackingSlip &&
        SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        while select purchParmLineSelect
            group by PostingProfile_RU, InventProfileType_RU
            where purchParmLineSelect.ParmId == purchParmUpdate.ParmId
        {
            i++;
        }

        if (i > 1)
        {
            purchParameters = PurchParameters::find();
            if (!purchParameters.SplitInventProfileTypePackingSlip_RU || !purchParameters.SplitPostingProfilePackingSlip_RU)
            {
                if (Box::yesNo("@SYS4081896", DialogButton::Yes) != DialogButton::Yes)
                {
                    return;
                }
            }
        }
    }
    // </GEERU>

    purchFormLetter.parmSaveChanges(false);
    if (!purchFormLetter.parmCurrentBatch())
    {
        this.savePrintSettings();
    }

    super();
}

/// <summary>
/// Save print settings to the syslastvalue
/// </summary>
void savePrintSettings()
{
    lastPrintFormLetter = printFormletter.value();
    lastPrintUsePrintManagement = usePrintManagement.value();
    xSysLastValue::saveLast(this);
}

/// <summary>
/// Gets print settings to the syslastvalue
/// </summary>
void getPrintSettings()
{
    xSysLastValue::getLast(this);
    printFormletter.value(lastPrintFormLetter);
    usePrintManagement.value(lastPrintUsePrintManagement);
}

/// <summary>
/// Configs the Letter of Credit related controls.
/// </summary>
public void configBankLCControls()
{
    boolean     isAllowEdit;
    boolean     isVisilbe;

    isVisilbe   = purchEditLinesForm.bankLCImportLine();

    if (isVisilbe)
    {
        isAllowEdit = purchEditLinesForm.editBankLCImportLine();
    }

    purchParmTable_ds.object(fieldNum(PurchParmTable, BankLCImportLine)).visible(isVisilbe);
    purchParmTable_ds.object(fieldNum(PurchParmTable, BankLCImportLine)).allowEdit(isAllowEdit);

    if (isAllowEdit)
    {
        purchEditLinesForm.setupBankLCImportLineController();
    }
}

