{
    PurchParmLine           pmfPurchParmLine;

    pmfPurchParmLine = purchParmLine.data();
    purchParmLine_ds.research();
    purchParmLine_ds.positionToRecord(pmfPurchParmLine);
}

public FormRun formRun()
{
    //Required by the SysOperation framework.
    return this;
}

void handleSecurity(FormDataSource _fds, boolean _hasAccess)
{
    if (_fds && !_fds.allowCheck() && !_hasAccess)
    {
        _fds.query().dataSourceNo(1).enabled(false);
    }
}

boolean hasTableAccess(TableId _tableId, AccessType _neededAccessLevel = AccessType::View)
{
    DictTable dictTable = new DictTable(_tableId);

    if (dictTable)
    {
        return dictTable.rights() >= _neededAccessLevel;
    }
    return false;
}

void hideQualityManagement()
{
    qualityOrderStatusDisplay.visible(false);
}

/// <summary>
/// Is charge on BOE enabled.
/// </summary>
/// <returns>true if charge on BOE enabled; otherwise, false.</returns>
internal boolean isChargeOnBOEEnabled()
{
    return documentStatus == DocumentStatus::BillOfEntry_IN && TaxIntegrationUtils::isChargeOnBOEEnabled();
}

void init()
{
    ParmId  parmId;

    pdsControlsSet = false;
    if (#PdsEnabled)
    {
        pdsFormCtrl = PdsFormCtrl_PurchEditLines::newFromForm(this);
        pdsFormCtrl.initPre();
    }

    if (!element.args().caller())
    {
        throw error("@SYS22539");
    }

    purchFormLetter         = element.args().caller();
    if (!purchFormLetter.parmCurrentBatch())
    {
        xSysLastValue::getLast(this);
    }

    super();

    // <GSA>
    zakatParameterEnabled = LedgerParameters::find().IsZakatEnabled_SA;
    // </GSA>

    documentStatus              = purchFormLetter.documentStatus();
    parmId                      = purchFormLetter.parmId();

    isOpenedFromPostingHistory  = purchFormLetter.parmCreateFromHistory();
    isHistoryReadOnly           = purchFormLetter.parmHistoryReadOnly();

    purchEditLinesIsBackOrderFlightEnabled = PurchEditLinesIsBackOrderFlight::instance().isEnabled();

    if (PurchFormLetterCreateFromBatchFlight::instance().isEnabled())
    {
        isOpenedFromBatch       = !purchFormLetter.parmOpenedWithArgs()
                                    && purchFormLetter.parmCurrentBatch().RecId;
    }

    reArrangeNow            = true;

    purchEditLinesForm      = PurchEditLinesForm::construct(documentStatus,
                                                            purchFormLetter.parmVersioningUpdateType());
    // <GIN>
    countryRegionCodeEnabled = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
    isTaxParametersMarked = TaxParameters::checkTaxParameters_IN();
    isPackingSlip = (documentStatus == DocumentStatus::PackingSlip);

    // Enable only to print product labels from purchase order during receive
    element.design().controlName(PrintProductLabels.name()).enabled(isPackingSlip);
    element.design().controlName(PrintShelfLabels.name()).enabled(isPackingSlip);

    if (countryRegionCodeEnabled && documentStatus == DocumentStatus::BillOfEntry_IN)
    {
        element.designBillOfEntryForm_IN();
        purchParmTable_ds.refresh();
    }

    if (countryRegionCodeEnabled)
    {
        purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, CustomsImportOrderType_IN)).visible(TaxParameters::isCustomsEnable_IN());
    }
    if (countryRegionCodeEnabled && documentStatus == DocumentStatus::PackingSlip)
    {
        GateEntryDetails_IN.visible(true);
        InventSiteGateEntryLine.visible(true);
    }
    // </GIN>
    purchEditLinesForm.setupSumByControl(sumBy);

    purchEditLinesForm.purchFormLetter(purchFormLetter);
    lateSelection.visible(purchEditLinesForm.lateSelection());

    searchParmLine = true;

    purchParmUpdate_ds.query().dataSourceTable(tableNum(PurchParmUpdate)).addRange(fieldNum(PurchParmUpdate, ParmId)).value(queryValue(parmId));
    purchParmTable_ds.query().dataSourceTable(tableNum(PurchParmTable)).addRange(fieldNum(PurchParmTable, ParmId)).value(queryValue(parmId));

    boolean canGoBatch = purchFormLetter.canGoBatch() && (purchFormLetter.showBatchTab());
    this.batch(canGoBatch);

    if (purchFormLetter.batchInfo().parmBatchExecute())
    {
        purchFormLetter.batchInfo().parmBatchExecute(canGoBatch);
    }

    // <GIN>
    if (countryRegionCodeEnabled)
    {
        element.setVisible_IN();
    }
    // </GIN>
    if (!InventParameters::find().UseQualityManagement)
    {
        element.hideQualityManagement();
    }

    if (BankLCImportFeatureChecker::checkBankLCImportEnabled())
    {
        bankLCImportLineNumController =  BankLCImportLineNumController::construct();
        purchEditLinesForm.parmBankLCImportLineNumController(bankLCImportLineNumController);
    }
    purchParmTable_ds.object(fieldNum(PurchParmTable, BankLCImportLine)).visible(purchEditLinesForm.bankLCImportLine());

    if (purchFormLetter.isVersioningCorrectionOrCancel())
    {
        purchParmTable_ds.allowEdit(false);
    }
    // <GSA>
    zakat_SA.visible(zakatParameterEnabled);
    // </GSA>

    element.updateDesign(InventDimFormDesignUpdate::Init);

    if (pdsFormCtrl)
    {
        pdsFormCtrl.parmFormLetter(purchFormLetter);
        pdsFormCtrl.parmPurchEditLinesForm(purchEditLinesForm);
        pdsFormCtrl.setDatasources(
                        purchParmLine,
                        purchParmUpdate,
                        purchParmTable,
                        null,
                        null);
        pdsFormCtrl.initPost();
    }
    // <GEEU>
    countryRegion_LTLV = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLT, #isoLV]);
    if (countryRegion_LTLV)
    {
        docNumberingCode_LT.text(purchFormLetter.getPurchFormLetterContract().parmLtDocNumberingCode());
    }
    // </GEEU>

    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxDocumentLauncher.visible(true);
        TaxTransSource_IN.visible(false);
    }

    PurchParmLine_DeliverRemainder.visible(true);
    PurchParmLine_QuantityOrdered.visible(true);

    if (TradeDisplayCalculatedColumnControlsFormFlight::instance().isEnabled())
    {
        displayCalculatedColumnControlsForm = TradeDisplayCalculatedColumnControlsForm_PurchEditLines_PackingSlip::newFromForm(element, purchEditLinesForm);
    }
}

private void initOpenedFromBatch()
{
    if (isOpenedFromBatch)
    {
        PurchFormLetterContract formLetterContract = purchFormLetter.getPurchFormLetterContract();

        if (formLetterContract.parmChooseLinesQueryPacked())
        {
            QueryRun queryRun = new QueryRun(SysOperationHelper::base64Decode(formLetterContract.parmChooseLinesQueryPacked()));

            if (queryRun)
            {
                this.initQueryControl(queryRun.query());
            }
        }
    }
}

private boolean allowSaveChanges()
{
    boolean allowSave = true;

    if (this.isBatchReadOnly())
    {
        allowSave = false;
    }
    else if (this.isPostingHistoryReadOnly())
    {
        allowSave = false;

        element.design().controlName(formControlStr(PurchEditLines, OK)).enabled(false);
        ButtonBatch.enabled(false);
    }

    return allowSave;
}

private boolean isBatchReadOnly()
{
    boolean isReadOnly;

    if (isOpenedFromBatch)
    {
        Batch localBatch = purchFormLetter.parmCurrentBatch();

        BatchJob localBatchJob;
        select Status from localBatchJob
            where localBatchJob.RecId == localBatch.BatchJobId;

        isReadOnly = localBatch.Status != BatchStatus::Hold || localBatchJob.Status != BatchStatus::Hold;
    }

    return isReadOnly;
}

private boolean isPostingHistoryReadOnly()
{
    return isOpenedFromPostingHistory
        && isHistoryReadOnly
        && PurchPostingHistoryUpdateStatusFlight::instance().isEnabled();
}

/// <summary>
/// This method is used to change the visibility/editability of LT,LV specific fields
/// </summary>
void initDesign_LT()
{
    docNumberingCode_LT.visible(purchEditLinesForm.showDocNumberingCode_LT(purchFormLetter.parmId()));
    purchParmTable_ds.object(fieldNum(PurchParmTable, Num)).allowEdit(!purchParmTable.DocAutoNumbering_LT);
    purchParmTable_ds.object(fieldNum(PurchParmTable, Description)).allowEdit(!purchParmTable.DocAutoNumbering_LT);
    this.allowEditPackingSlips_W();
}

void initDesign_RU()
{
    purchParmLine_ds.object(fieldNum(PurchParmLine, DeviationQty_RU)).visible(purchEditLinesForm.showDeviationQty_RU());
    officialsTrans_RU_DS.object(fieldNum(OfficialsTrans_RU, InvLocation)).visible(purchEditLinesForm.showOffInventLocation_RU());

    preserveReceiptStatement.visible(purchEditLinesForm.showPreserveReceiptStatement());

    printInventBaileeReport_RU.visible(purchEditLinesForm.showPrintInventBaileeReport_RU());
    printInventBaileeReport_RU.value(purchFormLetter.printInventBaileeReport_RU());

    printReceiptListFormType_RU.visible(purchEditLinesForm.showPrintReimbursementSlip_RU());
    printReceiptListFormType_RU.selection(purchEditLinesForm.parmReceiptListFormType_RU());

    printReceiptListDeviationType_RU.visible(purchEditLinesForm.showPrintReimbursementSlip_RU());
    element.setPrintReceiptListDeviationType_RU();

    if (purchFormLetter.parmVersioningUpdateType() == VersioningUpdateType::Correction)
    {
        element.setCorrectedReceiptListDeviationAndType();
    }

    element.setAllowEditDeviationType();

    printerSettings.enabled(purchEditLinesForm.showPrinterSetting_RU());

    purchParmTable_PostingProfile_RU.visible   (purchParmUpdate.SplitPostingProfile_RU);
    purchParmTable_InventProfileType_RU.visible(purchParmUpdate.SplitInventProfileType_RU);

    purchParmTable_ds.object(fieldNum(PurchParmTable, InventProfileType_RU)).visible(purchEditLinesForm.showInventProfileType_RU());
    purchParmTable_ds.object(fieldNum(PurchParmTable, PostingProfile)).visible   (purchEditLinesForm.showPostingProfile_RU());
    purchParmTable_ds.object(fieldNum(PurchParmTable, PostingProfile)).enabled(false);

    if (purchFormLetter.parmVersioningUpdateType() == VersioningUpdateType::Correction ||
        purchFormLetter.parmVersioningUpdateType() == VersioningUpdateType::Cancel)
    {
        tabPageParmLine.activatePage();

        if (purchFormLetter.parmVersioningUpdateType() == VersioningUpdateType::Cancel)
        {
            purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, Storno)).visible(false);
            purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, CheckCreditMax)).visible(false);

            purchParmTable_ds.allowEdit(false);
            purchParmLine_ds.allowEdit(false);
            officialsTrans_RU_ds.allowEdit(false);

            groupSumPurch.visible(false);
            groupPrint.visible(false);
        }
    }
}

#ISOCountryRegionCodes
void initDesign_W()
{
    if (! SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAllandRU))
    {
        return;
    }

    officials_RU.visible(purchEditLinesForm.showOfficials_RU());

    this.initDesign_RU();

    inventLocationId_PL.visible(purchEditLinesForm.showInventLocationId_PL());
    if (inventLocationId_PL.isVisible())
    {
        inventLocationId_PL.modified();
    }
}

void initQuery()
{
    Query query = purchFormLetter.parmQueryUsed();

    this.initQueryControl(query);

    tabPageLateSelection.visible(true);
}

private void initQueryControl(Query _query)
{
    if (_query)
    {
        const int maxControls = 10;

        if (_query.showInForm(LateSelectionGroup, maxControls))
        {
            pleaseMakeSelection.visible(false);
        }
        else
        {
            pleaseMakeSelection.visible(true);
        }
    }
    tabPageLateSelection.visible(true);
}

