{
    if (printFormletter.value())
    {
        tamVendPrintOptionsCheckBox.enabled(true);
    }
    else
    {
        tamVendPrintOptionsCheckBox.enabled(false);
    }
}

/// <summary>
/// Show batch disposition field
/// </summary>
void pdsShow()
{
    if (#PdsShelfEnabled)
    {
        if (    (documentStatus == DocumentStatus::PackingSlip)
            ||  (documentStatus == DocumentStatus::PickingList))
        {
            if (inventDimFormSetup.parmDimParmVisibleGrid().InventBatchIdFlag == NoYes::Yes)
            {
                pdsDispositionCode.visible(true);
                pdsDispositionStatus.visible(true);
            }
            else
            {
                pdsDispositionCode.visible(false);
                pdsDispositionStatus.visible(false);
            }
        }
    }
}

PurchFormLetter purchFormLetter()
{
    return purchFormLetter;
}

boolean  reArrange(boolean  _clearTotals)
{
    boolean  ok = true;

    purchParmUpdate_ds.write();

    purchFormLetter.initParameters(purchParmUpdate.data(),
                                   printout.selection(),
                                   printFormletter.value(),
                                   usePrintManagement.value(),
                                   tamVendPrintOptionsCheckBox.value());

    purchFormLetter.editLinesChanged(editLinesChanged);
    purchFormLetter.reArrangeNow(reArrangeNow);

    purchFormLetter.validateAll(false);

    ok = purchFormLetter.reArrange(_clearTotals);
    reArrangeNow = !ok;

    purchFormLetter.validateAll(true);
    purchParmTable_ds.reread();
    purchParmTable_ds.executeQuery();

    buttonReArrange.enabled(reArrangeNow);

    element.setFieldAccess();

    return ok;
}

void  reArrangeNow()
{
    reArrangeNow = true;

    purchFormLetter.reArrangeNow(reArrangeNow);

    buttonReArrange.enabled(reArrangeNow);
}

public void recalcAssessableValue_IN()
{
    CustomsImportAssessableValue_IN::recalculateAssessableValue(purchParmTable);
    purchParmTable_ds.reread();
    purchParmLine_ds.reread();
    purchParmLine_IN_ds.reread();
    purchParmTable_ds.refresh();
    purchParmLine_ds.refresh();
    purchParmLine_IN_ds.refresh();
}

private void recalcAssessableValueAccountingCurrency_IN()
{
    if (purchFormLetter.documentStatus() == DocumentStatus::BillOfEntry_IN)
    {
        PurchParmLine purchParmLineLoc;
        while select forupdate * from purchParmLineLoc
            where purchParmLineLoc.ParmId == PurchParmTable.ParmId
                && purchParmLineLoc.TableRefId == PurchParmTable.TableRefId
        {
            PurchParmLine_IN purchParmLineINLoc =  purchParmLineLoc.purchParmLine_IN();
            purchParmLineINLoc.AssessableValueAccountingCurrency = TaxAssessableValueHandler_IN::calcAssessableValueAccountingCurrency(purchParmLineINLoc.AssessableValueTransactionCurrency, purchParmLineLoc.currencyCode, PurchParmTable.TransDate);
            purchParmLineLoc.packPurchParmLine_IN(purchParmLineINLoc);
            purchParmLineLoc.SysExtensionSerializerMap::postUpdate();
        }
        this.refreshPurchParmLine_IN();
    }
}

internal void markupTransFormClosed()
{
    purchParmLine_ds.reread();
    purchParmLine_IN_ds.reread();
    purchParmLine_ds.refresh();
    purchParmLine_IN_ds.refresh();
}

internal void refreshLinesAfterChargeAllocation()
{
    PurchParmLine_ds.research(true);
}

/// <summary>
/// Refreshes the PurchParmTable and PurchParmLine table data sources.
/// </summary>
/// <remarks>
/// Called by child forms when closing due to possibly changed data contained in these two tables.
/// </remarks>
public void refresh()
{
    int pos;

    // Reread the header which will reread the lines. Retain the current row selected on both the header and lines
    pos = purchParmLine_ds.getPosition();
    purchParmTable_ds.research(true);
    purchParmLine_ds.setPosition(pos);
}

void refreshPurchParmLine_IN()
{
    purchParmLine_ds.reread();
    purchParmLine_ds.refresh();
}

void reSelect()
{
    // <GEEPL>
    if (inventLocationId_PL.isVisible())
    {
        purchFormLetter.reSelect(purchParmUpdate.data(), inventLocationId_PL.text());
    }
    else
    {
        // </GEEPL>
        purchFormLetter.reSelect(purchParmUpdate.data());
        // <GEEPL>
    }
    // <//GEEPL>

    element.chooseTable();
}

void  run()
{
    PurchUpdate recommendedQty;
    SysDictEnum salesUpdateEnum    = new SysDictEnum(enumNum(PurchUpdate));
    BoxFormOnce box;

    super();

    sumBy.selection(purchEditLinesForm.sumByIndex2Selection(purchEditLinesForm.sumBy()));
    purchParmUpdate.SumBy = purchEditLinesForm.sumBy();
    element.sumByChanged(true);

    if (purchParmUpdate.ParmId)
    {
        purchFormLetter.setPurchEditLinesDataSource(purchParmTable_ds);

        if (purchParmUpdate.LateSelection)
        {
            if (purchParmUpdate.LateSelectionQuery)
                purchFormLetter.chooseLinesQuery(new SysQueryRun(purchParmUpdate.LateSelectionQuery, true));

            element.initQuery();
            element.initOpenedFromBatch();
        }

        // <GEERU>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
        {
            printout.selection(Printout::After);
        }
        else
        {
            // </GEERU>
            printout.selection          (purchFormLetter.printout());
            // <GEERU>
        }
        // </GEERU>
        printFormletter.value       (purchFormLetter.printFormLetter());
        printSalesFormletter.value  (purchFormLetter.printSalesFormLetter());
        usePrintManagement.value    (purchFormLetter.usePrintManagement());
        this.setFieldAccess();

        vendAccountInQuery        = (purchFormLetter.vendAccountInQuery());
        invoiceAccountInQuery     = (purchFormLetter.invoiceAccountInQuery());

        if (purchParmUpdate.SumBy == AccountOrder::Order && purchFormLetter.sumNum())
            initSumNum = purchFormLetter.sumNum();

        element.sumByChanged(true);

        purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, SumNum)).allowEdit(purchEditLinesForm.sumNumAllowEdit());
        purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, SumBy)).allowEdit(purchEditLinesForm.sumByAllowEdit());
        sumBy.allowEdit(purchEditLinesForm.sumByAllowEdit());

        purchParmTable_ds.object(fieldNum(PurchParmTable, ExchRate)).allowEdit(false);
        purchParmTable_ds.object(fieldNum(PurchParmTable, ReportingCurrencyExchangeRate)).allowEdit(false);
        purchParmTable_ds.object(fieldNum(PurchParmTable, ExchRateSecondary)).allowEdit(false);

        element.design().caption                    (purchEditLinesForm.editingCaption());

        purchParmTable_Num.label                    (purchEditLinesForm.numCaption());
        purchParmTable_Num.helpText                 (purchEditLinesForm.numHelpText());
        purchParmTable_TransDate.label              (purchEditLinesForm.dateCaption());
        sumNum.label                                (purchEditLinesForm.sumNumCaption());
        purchParmTable_Num.visible(purchEditLinesForm.num());
        purchParmTable_TransDate.visible(purchEditLinesForm.transDate());
        purchParmTable_ds.object(fieldNum(PurchParmTable, Num)).visible                  (purchEditLinesForm.num());
        purchParmTable_ds.object(fieldNum(PurchParmTable, TransDate)).visible            (purchEditLinesForm.transDate());
        purchParmTable_ds.allowCreate(purchEditLinesForm.allowHeaderCreate());
        purchParmTable_ds.allowDelete(purchEditLinesForm.allowHeaderDelete());

        if (purchFormLetter.documentStatus() == DocumentStatus::Invoice)
        {
            buttonChangeDate.visible                    (purchEditLinesForm.transDate());
            buttonChangeDate.text                       (strFmt("@SYS68696", purchEditLinesForm.dateCaption()));
            buttonSetupPaymFormat.visible               (!purchEditLinesForm.paymFormat());
            buttonGroupSetup.visible                    (purchEditLinesForm.transDate());
        }
        else
        {
            buttonGroupSetup.visible(false);
        }
        // Hide or show the vendor print option check box
        if (purchFormLetter.MCRShowVendPrintOptionsCheckBox())
        {
            TAMVendPrintOptionsCheckBox.visible(true);
            element.MCRSetVendPrintOptionsCheckBox();
        }
        else
        {
            TAMVendPrintOptionsCheckBox.visible(false);
        }
        // <GIN>
        if (TaxParameters::isCustomsEnable_IN())
        {
            if (purchFormLetter.documentStatus() == DocumentStatus::BillOfEntry_IN)
            {
                buttonChangeDate.visible(false);
                buttonGroupSetup.visible(false);
            }
        }
        // </GIN>
        buttonSubledgerJournalLine_Overview.visible (purchEditLinesForm.subLedgerJournalButton());
        buttonSubLedgerJournalLine_Document.visible (purchEditLinesForm.subLedgerJournalButton());
        element.showSubLedgerJournalButton();

        printFormletter.label                       (purchEditLinesForm.formLetterCaption());
        buttonPrintFormLetter.text                  (purchEditLinesForm.formLetterButtonCaption());
        buttonPrintFormLetterCopy.text              (purchEditLinesForm.formLetterButtonCaptionCopy());
        // <GEERU>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
        {
            printout.allowEdit                          (false);
            printout.skip                               (true);
        }
        else
        {
            // </GEERU>
            printout.allowEdit                          (purchEditLinesForm.printout());
            printout.skip                               (!purchEditLinesForm.printout());
            // <GEERU>
        }
        // </GEERU>
        printFormletter.allowEdit                   (purchEditLinesForm.printFormletter());
        printFormletter.skip                        (!purchEditLinesForm.printFormletter());
        buttonPrintFormLetter.enabled               (purchEditLinesForm.printFormletterButton());
        buttonPrintFormLetterCopy.enabled           (purchEditLinesForm.printFormletterButton());

        usePrintManagement.visible                  (purchEditLinesForm.printout());
        parmTableTotals.visible                     (purchEditLinesForm.totalsButton());

        buttonSetupPaymFormat.visible               (!purchEditLinesForm.paymFormat() && purchFormLetter.documentStatus() != DocumentStatus::PackingSlip);
        printout.visible                            (purchEditLinesForm.printout());
        printFormletter.visible                     (purchEditLinesForm.printFormletter());
        printSalesFormletter.visible                (purchEditLinesForm.printSalesFormLetter());
        buttonPrintFormLetter.visible               (purchEditLinesForm.printFormletterButton());
        buttonPrintFormLetterCopy.visible           (purchEditLinesForm.printFormletterButton());
        usePrintManagement.visible                  (purchEditLinesForm.printout());

        choose.visible                              (purchEditLinesForm.chooseButton());

        purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, Storno)).visible(purchEditLinesForm.correct());
        // <GEERU>
        purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, Storno)).enabled(purchEditLinesForm.correctEnabled_RU());
        purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, StornoPhysical_RU)).visible(purchEditLinesForm.correctPhysical_RU());
        // </GEERU>
        purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, SpecQty)).allowEdit(purchEditLinesForm.specQtyAllowEdit());

        purchParmLine_ds.object(fieldNum(PurchParmLine, Closed)).visible                 (purchEditLinesForm.close());

        purchParmLine_ds.object(fieldNum(PurchParmLine, LineAmount)).allowEdit           (purchEditLinesForm.editPrices());
        purchParmLine_ds.object(fieldNum(PurchParmLine, ReceiveNow)).allowEdit           (purchEditLinesForm.editReceiveNow());
        purchParmLine_ds.object(fieldNum(PurchParmLine, PurchPrice)).allowEdit           (purchEditLinesForm.editPrices());
        purchParmLine_ds.object(fieldNum(PurchParmLine, DeliveryPostalAddress)).allowEdit(purchEditLinesForm.editPostalAddress());
        purchParmLine_ds.allowDelete                                                     (purchEditLinesForm.editReceiveNow());

        if (pdsFormCtrl)
        {
            pdsFormCtrl.specQtyChanged(purchEditLinesForm.editReceiveNow());
        }

        groupPrice.allowEdit                        (purchEditLinesForm.editPrices());
        purchaseLineInvoice.allowEdit               (purchEditLinesForm.editPrices());
        groupMarkup.allowEdit                       (purchEditLinesForm.editMarkup());
        groupDiscount.allowEdit                     (purchEditLinesForm.editDiscount());
        purchParmLine_PurchQtyCorrection.visible    (purchFormLetter.isVersioningCorrectionOrCancel());
        purchParmLine_InventQtyCorrection.visible   (purchFormLetter.isVersioningCorrectionOrCancel());
        purchParmLine_PdsCWQtyCorrection.visible    (purchFormLetter.isVersioningCorrectionOrCancel());

        purchParmTable_ds.object(fieldNum(PurchParmTable, Num)).allowEdit                (purchEditLinesForm.numAllowEdit());
        purchParmTable_ds.object(fieldNum(PurchParmTable, TransDate)).allowEdit          (purchEditLinesForm.transDateAllowEdit());

        buttonPrintFormLetter.enabled               (printFormLetter.value());
        buttonPrintFormLetterCopy.enabled           (printFormLetter.value());

        groupParameters.visible                     (purchEditLinesForm.parametersGroup());

        updateNow.enabled                           (purchFormLetter.enableUpdateNowField());
        printOut.enabled                            (!purchFormLetter.proforma());
        backOrder.visible                           (!purchEditLinesForm.backOrder());

        buttonReArrange.visible                     (purchEditLinesForm.reArrangeButton());

        tabPageParmTable.visible                    (purchEditLinesForm.overviewTab());
        GroupDocument.visible                       (purchEditLinesForm.setupTab());
        Posting_RU.visible                          (purchEditLinesForm.setupTab());
        InventProfile_RU.visible                    (purchEditLinesForm.setupTab());
        groupOtherSettings.visible                        (purchEditLinesForm.otherTab());
        tabPageParmLine.visible                     (purchEditLinesForm.linesTab());
        specification.visible                       (purchEditLinesForm.lineDetailsTab());

        tabPageParmSubTable.visible                 (purchEditLinesForm.purchasesTab());
        tabPageFixedAsset.visible                   (purchEditLinesForm.fixedAssetTab());

        if (!TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
        {
            taxTransSource_IN.visible(purchEditLinesForm.showSalesTax());
        }

        // <GEERU>
        element.initDesign_W();
        // </GEERU>
        // <GEEU>
        if (countryRegion_LTLV)
        {
            element.initDesign_LT();
        }
        // </GEEU>

        element.tmsEnableFields();
        if (pdsFormCtrl)
        {
            pdsFormCtrl.parmHasWhsItem(hasWHSItem);
        }

        if (this.mustPromptQty())
        {
            recommendedQty = purchFormLetter.recommendedSpecQty();
            if (recommendedQty != specQty.selectionAsPurchUpdate())
            {
                boxFormOnceIsActive = true;

                box = BoxFormOnce::construct();
                box.parmDialogBoxType(DialogBoxType::YesNoBox);
                box.parmDialogButton(DialogButton::Yes);
                box.parmTitle("@SYS59372");
                box.parmText(strFmt("@SYS102340", salesUpdateEnum.index2Name(specQty.selection()), salesUpdateEnum.index2Name(enum2int(recommendedQty))));
                box.parmOwner(new SysDictClass(classIdGet(purchEditLinesForm)).name());
                if (box.prompt() == DialogButton::Yes)
                {
                    specQty.selection(enum2int(recommendedQty));
                    specQty.selectionChange();
                }

                boxFormOnceIsActive = false;
            }
        }

        if (purchParmTable && tabPageParmTable.visible() && purchParmTable_Num.visible())
        {
            purchParmTable_Num.setFocus();
        }

        if (displayCalculatedColumnControlsForm && displayCalculatedColumnControlsForm.hasDisplayCalculatedColumnControls())
        {
            TradeDisplayCalculatedColumns.enabled(true);
            TradeDisplayCalculatedColumns.visible(true);
        }
    }

    editLinesChanged = false;

    if (!purchFormLetter.parmCurrentBatch())
    {
        this.getPrintSettings();
        element.setFieldAccess();
    }

    element.setReadOnlyMode();
}

private void setReadOnlyMode()
{
    if (!this.allowSaveChanges())
    {
        PurchParmUpdate_ds.allowEdit(false);
        PurchParmTable_ds.allowEdit(false);
        PurchParmSubTable_ds.allowEdit(false);
        PurchParmLine_ds.allowEdit(false);
        InventDim_ds.allowEdit(false);

        PurchParmUpdate_ds.allowCreate(false);
        PurchParmTable_ds.allowCreate(false);
        PurchParmSubTable_ds.allowCreate(false);
        PurchParmLine_ds.allowCreate(false);
        InventDim_ds.allowCreate(false);

        PurchParmUpdate_ds.allowDelete(false);
        PurchParmTable_ds.allowDelete(false);
        PurchParmSubTable_ds.allowDelete(false);
        PurchParmLine_ds.allowDelete(false);
        InventDim_ds.allowDelete(false);

        buttonGroupParameters.enabled(false);
        LineStripUpdate.enabled(false);
    }
}

/// <summary>
/// Determines if the quantity field must be prompted.
/// </summary>
/// <returns>
/// true if prompt is required; otherwise, false.
/// </returns>
protected boolean mustPromptQty()
{
    return PurchParameters::find().PromptQty;
}

PurchFormLetter runBase()
{
    return purchFormLetter;
}

public void setAllowEditDeviationType()
{
    printReceiptListDeviationType_RU.allowEdit(
        printReceiptListFormType_RU.selection() != ReceiptListFormType_RU::ReceiptListForMaterials && !preserveReceiptStatement.value());
}

public void setCorrectedReceiptListDeviationAndType()
{
    PurchFormLetter_PackingSlip purchFormLetter_PackingSlip;

    if (purchFormLetter is PurchFormLetter_PackingSlip)
    {
        purchFormLetter_PackingSlip = purchFormLetter;

        printReceiptListDeviationType_RU.selection(purchFormLetter_PackingSlip.getReceptListDeviationType());

        switch (purchFormLetter_PackingSlip.getReceptListDeviationType())
        {
            case ReceiptListDeviationType_RU::ReceiptListForMaterialsM7:
                printReceiptListFormType_RU.selection(ReceiptListFormType_RU::ReceiptListForMaterials);
                break;
            case ReceiptListDeviationType_RU::ReceiptListForGoodsTORG1:
            case ReceiptListDeviationType_RU::ReceiptListForGoodsTORG2:
            case ReceiptListDeviationType_RU::ReceiptListForGoodsTORG3:
                printReceiptListFormType_RU.selection(ReceiptListFormType_RU::ReceiptListForGoods);
                break;
        }
    }
}

void setFieldAccess()
{
    usePrintManagement.enabled(printFormLetter.value());

    if (!usePrintManagement.enabled())
    {
        usePrintManagement.value(enum2int(NoYes::No));
    }

    element.MCRSetVendPrintOptionsCheckBox();

    if (!tamVendPrintOptionsCheckBox.enabled())
    {
        tamVendPrintOptionsCheckBox.value(enum2int(NoYes::No));
    }
}

void setPrintReceiptListDeviationType_RU()
{
    printReceiptListDeviationType_RU.selection(
        (printReceiptListFormType_RU.selection() == ReceiptListFormType_RU::ReceiptListForMaterials) ?
            ReceiptListDeviationType_RU::ReceiptListForMaterialsM7:
            ReceiptListDeviationType_RU::ReceiptListForGoodsTORG1
    );
}

private void setVisible_IN()
{
    purchParmLine_IN_ds.object(fieldNum(PurchParmLine_IN, ReceivedQty)).visible(isPackingSlip);
    purchParmLine_IN_ds.object(fieldNum(PurchParmLine_IN, RejectedQty)).visible(isPackingSlip);
    purchParmLine_IN_ds.object(fieldNum(PurchParmLine_IN, AcceptedQty)).visible(isPackingSlip);
    if (!isPackingSlip)
    {
        goodsReceiptNote_IN.frameType(1);
    }
    purchParmLine_IN_ds.object(fieldNum(PurchParmLine_IN, AssessableValueTransactionCurrency)).visible(isTaxParametersMarked);
    purchParmLine_IN_ds.object(fieldNum(PurchParmLine_IN, MaximumRetailPrice)).visible(isTaxParametersMarked);

    goodsReceiptNote_IN.visible(isTaxParametersMarked);
    printGoodReceiptNote_IN.visible(isTaxParametersMarked && isPackingSlip);
}

void showSubLedgerJournalButton()
{
    switch (documentStatus)
    {
        case DocumentStatus::PackingSlip, DocumentStatus::ReceiptsList:
            buttonSubledgerJournalLine_Overview.visible(false);
            buttonSubLedgerJournalLine_Document.visible(false);
            break;
    }
}

PurchUpdate  specQty(PurchUpdate  _specQty = specQty.selectionAsPurchUpdate())
{
    // This method is used when calculating totals
    specQty.selection(enum2int(_specQty));
    return specQty.selectionAsPurchUpdate();
}

AccountOrder  sumBy(AccountOrder _sumBy = purchParmUpdate.SumBy)
{
    // This method is used when calculating totals
    purchParmUpdate.SumBy = _sumBy;
    return purchParmUpdate.SumBy;
}

void  sumByChanged(boolean _init)
{
    if (purchEditLinesForm.sumBySelection2Index(sumBy.selection()) == AccountOrder::Order)
    {
        purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, SumNum)).visible(purchEditLinesForm.num());

        if (purchEditLinesForm.num())
        {
            purchParmUpdate.SumNum = initSumNum;
        }

        purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, SumPurchId)).visible(true);
        purchParmUpdate.SumPurchId = initSumPurchId;
    }
    else
    {
        purchParmUpdate.SumNum      = '';
        purchParmUpdate.SumPurchId  = '';

        purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, SumNum)).visible(false);
        purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, SumPurchId)).visible(false);
    }

    if (!_init)
    {
        element.clearParmTableTotals();
    }

    if (_init && purchParmUpdate.SumBy == AccountOrder::None)
        reArrangeNow = false;
    else
        reArrangeNow = true;

    buttonReArrange.enabled(reArrangeNow);

    if (purchFormLetter.isEGAISPosting_RU() && reArrangeNow)
    {
        element.reArrange(false);
    }
}

public int task(int _taskId)
{
    #task
    int ret;

    if (_taskId == #taskEsc)
    {
        _taskId = #taskCtrlQ;
    }

    if (_taskId == #taskOk)
    {
        using (SysErrorMessageHelpScope scope = SysErrorMessageHelpScope::newErrorContextByFormAndAction(formStr(PurchEditLines), enum2Symbol(enumNum(DocumentStatus), element.documentStatus)))
        {
            ret = super(_taskId);
        }
    }
    else
    {
        ret = super(_taskId);
    }

    return ret;
}

void tmsCheckItems()
{
    PurchParmLine    localPurchParmLine;
    TMSInventEnabled tmsInventEnabled;
    WHSInventEnabled whsInventEnabled;

    select firstonly RecId from localPurchParmLine
        where localPurchParmLine.ParmId == purchParmTable.ParmId
        exists join tmsInventEnabled
            where tmsInventEnabled.ItemId == localPurchParmLine.ItemId;

    hasTMSItem = localPurchParmLine.RecId != 0;

    select firstonly RecId from localPurchParmLine
        where localPurchParmLine.ParmId == purchParmTable.ParmId
        exists join whsInventEnabled
            where whsInventEnabled.ItemId == localPurchParmLine.ItemId;

    hasWHSItem = localPurchParmLine.RecId != 0;

    if (hasWHSItem)
    {
        whsHasWHS.enabled(hasWHSItem);
    }
}

void tmsEnableFields()
{
    if (purchFormLetter.tmsParmRunFromSCS())
    {
        element.tmsCheckItems();

        if (hasWHSItem || hasTMSItem)
        {
            specQty.enabled(false);
            updateNow.enabled(false);
            choose.enabled(false);
            buttonReArrange.enabled(false);
            purchParmTable_ds.allowDelete(false);
            purchParmLine_ds.allowDelete(false);
            buttonBatch.visible(false);
        }

        if (hasWHSItem)
        {
            inventReserve.enabled(false);
            inventTransRegister.enabled(false);
            inventTransPick.enabled(false);
            element.tmsDisablePurchLineEdit();
        }
    }
}

/// <summary>
/// Updates line receive-now quatities to the registered quantities.
/// </summary>
/// <remarks>
/// Intention is this method is called after registration is done from the Update line menu.
/// </remarks>
public void updateRegisteredLineQuantities()
{
    InventMovement movement = InventTransWMS_Register::inventMovement(purchParmLine);
    InventQtyRegistered inventQtyRegistered = movement.transIdSum().registered();
    PdsCWInventQty cwQtyRegistered = movement.transIdSum().pdsCWRegistered();

    PurchQtyRegistered purchQtyRegistered = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(purchParmLine.ItemId,
                                                                                                                  purchParmLine.InventDimId,
                                                                                                                  inventQtyRegistered,
                                                                                                                  purchParmLine.inventTable().inventUnitId(),
                                                                                                                  purchParmLine.purchLine().PurchUnit,
                                                                                                                  NoYes::Yes);

    purchParmLine.ReceiveNow = purchQtyRegistered ? purchQtyRegistered : purchParmLine.ReceiveNow;
    purchParmLine.PdsCWReceiveNow = cwQtyRegistered ? cwQtyRegistered : purchParmLine.PdsCWReceiveNow;
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement;
    boolean isCorrection;

    if (isWritingPurchParmLineInCloseOK)
    {
        //Form is closing so no need to update inventDim bound controls
        return;
    }

    movement = InventMovement::construct(purchParmLine);
    isCorrection = purchFormLetter.isVersioningCorrectionOrCancel();

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                if (#PdsShelfEnabled)
                {
                    inventDimFormSetup = InventDimCtrl_Frm_Mov_Parm_PdsShelf::newFromFormMovement(element, movement, isCorrection);
                }
                else
                {
                    inventDimFormSetup = InventDimCtrl_Frm_Mov_Parm::newFromFormMovement(element, movement, isCorrection);
                }
            }
        // fall through

        case InventDimFormDesignUpdate::Active,
            InventDimFormDesignUpdate::FieldChange:
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(mode == InventDimFormDesignUpdate::Active);
            if (    !pdsControlsSet
                &&  (mode == InventDimFormDesignUpdate::Active)) // controls are set based on above
            {
                pdsControlsSet = true;
                element.pdsShow();
            }
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }

    if (displayCalculatedColumnControlsForm)
    {
        displayCalculatedColumnControlsForm.setSourceFormControls();
    }
}

/**
    Synchronize the purchParmTable datasource with the database
**/
void updateFormCachePurchParmTable()
{
    PurchParmTable  purchParmTableDataBase;
    PurchParmTable  purchParmTableFormCache;

    purchParmTableFormCache = purchParmTable_ds.getFirst(0,false);

    while (purchParmTableFormCache)
    {
        purchParmTableDataBase = PurchParmTable::find(purchParmTableFormCache.ParmId, purchParmTableFormCache.TableRefId);

        if (purchParmTableDataBase)
        {
            purchParmTableFormCache.data(purchParmTableDataBase);

            if (purchParmTableDataBase.RecId == purchParmTable.RecId)//the currently active record
            {
                purchParmTable.data(purchParmTableDataBase);
                purchParmTable_ds.setCurrent();
            }
        }

        purchParmTableFormCache = purchParmTable_ds.getNext();
    }
}

void updatePrintOut(container _printerSettings)
{
    if (printOut.selection() == Printout::Current &&
            !purchParmUpdate.Proforma &&
            FormLetter::isClientOutput(_printerSettings))
    {
        printOut.selection(Printout::After);
    }
}

boolean validatePrintDeviationDocumentType_RU()
{
    if (printReceiptListFormType_RU.selection()      != ReceiptListFormType_RU::ReceiptListForMaterials &&
        printReceiptListDeviationType_RU.selection() == ReceiptListDeviationType_RU::ReceiptListForMaterialsM7)
    {
        return false;
    }
    return true;
}

private void tmsDisablePurchLineEdit()
{
    SysDictTable    dictTable;
    SysDictField    dictField;
    FieldId         fieldId;
    SetEnumerator   se;

    dictTable = new SysDictTable(tableNum(PurchParmLine));

    se = dictTable.fields(true, false, false).getEnumerator();
    while (se.moveNext())
    {
        dictField = se.current();
        fieldId = dictField.id();

        if (!dictField.isSystem()
            && fieldId != fieldNum(PurchParmLine, Closed))
        {
            purchParmLine_ds.object(fieldId).allowEdit(false);
        }
    }
}

