/// <summary>
/// Checks for data specific situations when a field value in the <c>PurchParmTable</c> Form Data Source changes.
/// </summary>
/// <param name="_fieldId">
/// The field identifier of the field being modified.
/// </param>
/// <param name="_unapprove">
/// true if the approveVariance should be called; otherwise false.
/// </param>
/// <remarks>
/// If the InvoiceAccount value is blank, the retrievePOButton needs to be disabled.
/// </remarks>
void modifiedField(FieldId  _fieldId, boolean _unapprove)
{
    switch (_fieldId)
    {
        case fieldNum(PurchParmTable, InvoiceAccount):
            purchFormLetter.checkW9Received(purchParmTable);
            break;

        case fieldNum(PurchParmTable, PurchId):
            purchFormLetter.checkW9Received(purchParmTable);
            break;
    }
}

public void initValue()
{
    #define.tmpTableRefId('tmp')
    super();

    // <GIN>
    if (countryRegionCodeEnabled && documentStatus == DocumentStatus::BillOfEntry_IN)
        purchParmTable.TableRefId = #tmpTableRefId + formletterParmData::getNewTableRefId();
    else
        purchParmTable.TableRefId = #tmpTableRefId;
    // </GIN>

    purchParmTable.CurrencyCode = Ledger::accountingCurrency(CompanyInfo::current());
    purchParmTable.Ordering = documentStatus;
}

public void init()
{
    super();

    purchParmTable_ds.cacheAddMethod(tableMethodStr(PurchParmTable, checkIfLines));
    purchParmTable_ds.cacheAddMethod(tableMethodStr(PurchParmTable, displayOrdering));
}

boolean  checkPaymentId(PurchParmTable  _purchParmTable)
{
    boolean                                 ret = true;
    LogisticsAddressCountryRegionISOCode    bankValidationCountryRegionISOCode;

    if (!this.paymentIdValidated(false, _purchParmTable, false))
    {
        bankValidationCountryRegionISOCode = Bank::paymIdValidationCountryRegionISOCode(
                                                LedgerJournalACType::Vend,
                                                LedgerDynamicAccountHelper::getDynamicAccountFromAccountNumber(_purchParmTable.purchTable().InvoiceAccount, LedgerJournalACType::Vend),
                                                _purchParmTable.VendBankAccountID);

        ret = Bank::construct(bankValidationCountryRegionISOCode).checkPaymId(_purchParmTable.PaymId);
    }

    return ret;
}

edit boolean  paymentIdValidated(boolean             set,
                                 PurchParmTable     _purchParmTable,
                                 NoYes              _validated)
{
    NoYes  validated;

    if (_purchParmTable.RecId)
    {
        if (!paymentIdValidatedSet)
            paymentIdValidatedSet = new Set(typeName2Type(extendedTypeStr(RecId)));

        if (set)
        {
            validated = _validated;
            if (_validated)
                paymentIdValidatedSet.add(_purchParmTable.RecId);
            else
                paymentIdValidatedSet.remove(_purchParmTable.RecId);
        }
        else
        {
            validated = paymentIdValidatedSet.in(_purchParmTable.RecId);
        }
    }
    else
    {
        validated = NoYes::No;
    }

    return validated;
}

void  write()
{
    Num     num;

    if (! purchParmTable.RecId  &&  purchTable)
    {
        purchFormLetter.showQueryForm(false);
        purchFormLetter.purchTable(purchTable);
        purchParmTable.ParmId = purchParmUpdate.ParmId;
        if (purchParmTable.Num)
        {
            num = purchParmTable.Num;
        }
        purchFormLetter.purchParmUpdate(purchParmUpdate.data());
        purchFormLetter.chooseLines(false, purchParmTable, true);
        purchParmTable.data(PurchParmTable::findRecId(purchFormLetter.parmLastParmTableRecId()));
        purchParmTable_ds.setCurrent();

        if (num && purchParmTable.RecId)
        {
            purchParmTable.Num = num;
        }

        element.chooseTable();

        purchParmTable_ds.cacheCalculateMethod(tableMethodStr(PurchParmTable, checkIfLines));
    }
    else
    {
        if (!purchParmTable.RecId && purchParmTable.isNonPO())
        {
            // For new, non-PO records that won't go through chooseLines, set the TableRefId here
            purchParmTable.TableRefId = formletterParmData::getNewTableRefId();
        }

        super();
    }

    element.checkParmTable();
    editLinesChanged = true;

    // Saving or Posting will update this value, at this point we only want to flag that the value is something other than LastMatchVarianceOptions::Unknown
    purchParmTable.LastMatchVariance = LastMatchVarianceOptions::OK;
}

void  delete()
{
    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAllandRU))
    {
        purchParmTable.deleteRelatedTrans();
    }
    // </GEERU>

    super();

    element.checkParmTable();

    element.reArrangeNow();

    purchTable = null;
}

void create(boolean append = false)
{
    append = true;

    super(append);
    // <GIN>
    if (countryRegionCodeEnabled)
    {
        purchParmTable_ds.object(fieldNum(PurchParmTable, CustomsImportInvoiceNumberTable_IN)).allowEdit(TaxParameters::find().Customs_IN);
        purchParmTable_ds.object(fieldNum(PurchParmTable, PurchId)).allowEdit(true);
    }
    // </GIN>

    element.reArrangeNow();
}

int active()
{
    PurchTable      localPurchTable;
    int             ret;
    // <GIN>
    PurchParmLine   purchParmLineLoc;
    boolean         isImportOrder;
    Array           markedRecords;
    // </GIN>

    PurchParmLine currentParmLine = purchParmLine.data();

    if (searchParmLine && purchParmLine_ds.anyMarked())
    {
        markedRecords = purchParmLine_ds.recordsMarked();
    }

    ret = super();

    if (searchParmLine)
    {
        if (markedRecords)
        {
            purchParmLine_ds.markRecords(markedRecords);
        }

        if (currentParmLine.RecId != purchParmLine.RecId)
        {
            purchParmLine_ds.positionToRecord(currentParmLine);
        }
    }

    //Block PurchId field if already persisted
    purchParmTable_ds.object(fieldNum(PurchParmTable, PurchId)).allowEdit(!purchParmTable.RecId);

    localPurchTable = purchParmTable.purchTable();

    purchEditLinesForm.setPurchTable(localPurchTable);

    element.configBankLCControls();

    // <GIN>
    if (ret &&
        TaxParameters::isCustomsEnable_IN())
    {
        purchParmLineLoc = PurchParmLine::findTableRefId_IN(purchParmTable.ParmId, purchParmTable.TableRefId);
        if (purchParmLineLoc.RecId)
        {
            customsBillOfEntryDetails_IN.enabled(true);
        }
        else
        {
            customsBillOfEntryDetails_IN.enabled(false);
        }
        if (purchParmUpdate.CustomsBillOfEntry_IN == NoYes::Yes)
        {
            purchParmTable_ds.object(fieldNum(PurchParmTable, CustomsBillOfEntryNumberTable_IN)).allowEdit(false);
        }
        else
        {
            purchParmTable_ds.object(fieldNum(PurchParmTable, CustomsBillOfEntryNumberTable_IN)).allowEdit(true);
        }
        purchParmLine_IN_ds.object(fieldNum(PurchParmLine_IN, MaximumRetailPrice)).allowEdit(localPurchTable.purchTable_W().CustomsImportOrder_IN == NoYes::No);
    }

    if (isPackingSlip && countryRegionCodeEnabled)
    {
        isImportOrder = purchParmTable.purchTable().isCustomsImportOrder_IN();
        purchParmLine_IN_ds.object(fieldNum(PurchParmLine_IN, AssessableValueTransactionCurrency)).allowEdit(!isImportOrder);
        purchParmLine_IN_ds.object(fieldNum(PurchParmLine_IN, AssessableValueAccountingCurrency)).visible(isImportOrder);
        purchParmLine_IN_ds.object(fieldNum(PurchParmLine_IN, AssessableValueAccountingCurrency)).allowEdit(!isImportOrder);
    }
    // </GIN>
    // <GEEU>
    if (countryRegion_LTLV)
    {
        element.initDesign_LT();
    }
    // </GEEU>
    return ret;
}

