public boolean validate()
{
    boolean     ret;
    PurchTable  localPurchTable;

    ret = super();

    localPurchTable = purchParmTable.purchTable();

    if (!(PurchFormletterParmData::allowJournal(purchFormLetter.documentStatus(), localPurchTable)))
        ret = checkFailed(strFmt("@SYS62423",localPurchTable.PurchaseType,localPurchTable.PurchId));

    // <GIN>
    if (ret
        && TaxParameters::isCustomsEnable_IN())
    {
        if (purchFormLetter.documentStatus() == DocumentStatus::BillOfEntry_IN)
        {
            if (purchParmTable.PurchId)
            {
                if (!localPurchTable.isCustomsImportOrder_IN())
                {
                    ret = checkFailed(strFmt("@GLS5765",purchParmTable.PurchId));
                }
            }
        }
    }
    // </GIN>

    return ret;
}

void  modified()
{
    purchParmTable.Ordering = documentStatus;
    purchTable = purchParmTable.purchTable();
    purchParmTable.ParmId = purchParmUpdate.ParmId;
    purchParmTable.defaultRow();
    super();

    //Enforce record to be inserted which will then trigger creation of related data
    if (!purchParmTable.RecId)
    {
        purchParmTable_DS.write();
    }

    purchParmTable_ds.paymentIdValidated(true, purchParmTable, false);

    // <GIN>
    if (TaxParameters::isCustomsEnable_IN())
    {
        if (purchFormLetter.documentStatus() == DocumentStatus::BillOfEntry_IN)
        {
            // clear purchTable
            purchTable = null;
            // refresh record
            purchParmTable_ds.cacheCalculateMethod(tableMethodStr(PurchParmTable, checkIfLines));
        }
    }
    // </GIN>

    element.configBankLCControls();
}

public void lookup(
    FormControl _formControl,
    str _filterStr)
{
    if (TaxParameters::isCustomsEnable_IN() &&
        documentStatus == DocumentStatus::BillOfEntry_IN)
    {
        PurchTable::lookupCustomsPurchOrder_IN(_formControl);
    }
    else if (documentStatus == DocumentStatus::ConfirmationRequest || documentStatus == DocumentStatus::PurchaseOrder)
    {
        PurchTable::lookupOrdersForConfirmationRequestOrConfirmation(_formControl);
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

