public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (!purchEditLinesForm.validateInventDimId(purchParmLine, inventDim))
    {
        ret = false;
    }

    if (ret && PurchParmLine_IN.CustomsImportInvoiceNumberTable && !PurchParmLine_IN.EximPorts)
    {
        ret = checkFailed(strfmt("@SYS26332", fieldid2pname(tablenum(PurchParmLine_IN), fieldnum(PurchParmLine_IN, EximPorts))));
    }

    return ret;
}

void  write()
{
    // Write InventDim
    purchParmLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::IsCompanyEnabled())
    {
        if (purchParmLine.SalesPurchJournalLine::taxRecalculationNeeded())
        {
            purchParmLine.markCurrentTaxDocumentTaxStatusDirty();
        }
    }
    // </GTE>

    super();

    // Pull InventDim changes into form.
    if (purchParmLine.InventDimId != inventDim.InventDimId)
    {
        inventDim.data(InventDim::find(purchParmLine.InventDimId));
        inventDim_ds.setCurrent();
    }

    editLinesChanged = true;
}

void linkActive()
{
    if (searchParmLine)
        super();
}

void  delete()
{
    PurchId purchId;
    ParmId  parmId;
    // <GIN>
    RefRecId purchLineRecId;
    VendDocumentLineMap vendDocumentLineMap;
    TradeLineRefId tradeLineRefId;
    // </GIN>

    purchId = purchParmLine.OrigPurchId;
    parmId  = purchParmLine.ParmId;

    // <GIN>
    purchLineRecId = purchParmLine.PurchLineRecId;
    tradeLineRefId = purchParmLine.TableRefId;
    // </GIN>

    super();

    // <GIN>
    if (purchParmTable && VendDocumentLineMap::isTransactionRelatedToBOE_IN(purchParmTable.purchTable(), purchParmUpdate))
    {
        vendDocumentLineMap.updateLinkedParmline_IN(purchParmTable.Ordering, parmId, purchLineRecId, tradeLineRefId, purchParmUpdate.CreditRemaining, false);
    }
    // </GIN>
    PurchParmSubTable::deleteIfNoParmLines(purchId,parmId);

    purchParmTable_ds.cacheCalculateMethod(tableMethodStr(PurchParmTable, checkIfLines));

    purchParmSubTable_ds.research();
}

int active()
{
    int ret;
    boolean allowEdit;

    ret = super();

    allowEdit = purchParmLine.purchLine().isStocked() && purchEditLinesForm.editReceiveNow();

    // Note that the PdsFromCtrl_PurchEditLines class updateDesign method extends the field enabling
    // functionality. Changes here might need to be ported to it as well.
    purchParmLine_ds.object(fieldNum(PurchParmLine, RemainBeforeInvent)).allowEdit(allowEdit);
    purchParmLine_ds.object(fieldNum(PurchParmLine, RemainBeforeInvent)).skip(!allowEdit);
    purchParmLine_ds.object(fieldNum(PurchParmLine, RemainAfterInvent)).allowEdit(allowEdit);
    purchParmLine_ds.object(fieldNum(PurchParmLine, RemainAfterInvent)).skip(!allowEdit);

    purchParmLine_ds.object(fieldNum(PurchParmLine, InventNow)).allowEdit(allowEdit);
    purchParmLine_ds.object(fieldNum(PurchParmLine, InventNow)).skip(!allowEdit);

    purchParmLine_ds.object(fieldNum(PurchParmLine, RemainAfter)).allowEdit(purchEditLinesForm.editRemainAfter());
    purchParmLine_ds.object(fieldNum(PurchParmLine, RemainAfter)).skip(!(purchEditLinesForm.editRemainAfter()));

    purchParmLine_ds.object(fieldNum(PurchParmLine, Closed)).allowEdit(!purchParmLine.purchLine().SalesPurchLine::isEGAIS_RU());

    if (pdsFormCtrl)
    {
        pdsFormCtrl.dataSourceActivePost();
    }

    element.updateDesign(InventDimFormDesignUpdate::Active);

    TradeCustomizationsEventHandler::purchParmLineActive(this);

    return ret;
}

display ImageRes backOrder(PurchParmLine _purchParmLine)
{
    ImageRes imageRes = 0;

    if (purchEditLinesIsBackOrderFlightEnabled)
    {
        if (InventTrans::hasAnyBackorderIssueTransactions(_purchParmLine.ItemId, _purchParmLine.InventDimId))
        {
            imageRes = #Imageinfo;
        }
    }
    else
    {
        if (-InventTrans::backOrderQtyIssue(_purchParmLine.ItemId, _purchParmLine.InventDimId) > 0)
        {
            imageRes = #Imageinfo;
        }
    }

    return imageRes;
}

void modifiedField(FieldId  _fieldId, boolean _unapprove)
{
    switch (_fieldId)
    {
        case fieldNum(PurchParmLine,PdsVendBatchDate):
            if (!pdsUseVendUpdated)
            {
                pdsUseVendUpdated = !pdsUseVendUpdated;
                purchParmLine.pdsUpdateUseVendBatchDate();
            }

            pdsUseVendUpdated = !pdsUseVendUpdated;
            break;
        case fieldNum(PurchParmLine,PdsVendExpiryDate):
            if (!pdsUseVendExp)
            {
                pdsUseVendExp = !pdsUseVendExp;
                purchParmLine.pdsUpdateUseVendExpiryDate();
            }
            pdsUseVendExp = !pdsUseVendExp;
            break;
    }
}

