/// <summary>
/// Activates the <c>PurchLine</c> datasource.
/// </summary>
/// <returns>
/// An integer value indicating if the mehtod is successful. It always returns 1.
/// </returns>
/// <remarks>
/// This method calls the <c>setAllowEditAssetFields</c> method of the <c>PurchTableForm</c> class
/// to ensure that the fixed asset fields are available to be edited.
/// </remarks>
int  active()
{
    int  ret;

    ret = super();

    purchEditLinesForm.setAllowEditAssetFields(purchParmTable, purchParmLine,
        purchParmLine_Asset, purchParmLine_Asset_ds);

    return ret;
}

void  create(boolean append = true)
{
    super(append);

    this.createFixedAssetModified();
}

/// <summary>
/// Broadcasts that the <c>CreateFixedAsset</c> field value has changed to the <c>purchLine</c> table buffer.
/// </summary>
void createFixedAssetModified()
{
    purchParmLine_Asset.modifyCreateFixedAsset();
}

/// <summary>
/// Modifies the <c>CreateFixedAssetField</c> field value on the <c>PurchLine</c> table buffer.
/// </summary>
/// <remarks>
/// This method broadcasts the change in value of the <c>CreateFixedAssetField</c> field by
/// calling <c>createFixedAssetModified</c> on the data source. Then it calls the
/// <c>setAllowEditAssetFields</c> method of the <c>purchTableForm</c> class to correctly
/// set the <c>allowEdit</c> property of the fixed asset fields.
/// </remarks>
public void modified()
{
    super();

    purchParmLine_Asset_ds.createFixedAssetModified();
    purchEditLinesForm.setAllowEditAssetFields(purchParmTable, purchParmLine,
        purchParmLine_Asset, purchParmLine_Asset_ds);
}

public int active()
{
    int ret = super();

    this.object(fieldNum(OfficialsTrans_RU, InvLocation)).allowEdit(officialsTrans_RU.invLocationEditable());

    return ret;
}

void executeQuery()
{
    switch (purchParmTable.Ordering)
    {
        case DocumentStatus::PackingSlip     :
            OfficialsTrans_RU::searchAccountIdx(officialsTrans_RU,
                                                OffReportType_RU::PurchM4M7,
                                                purchParmTable.OffSessionId_RU);
            break;
        case DocumentStatus::Invoice4Paym_RU :
        case DocumentStatus::ApproveJournal  :
            OfficialsTrans_RU::searchAccountIdx(officialsTrans_RU,
                                                purchParmTable.OffReportType_RU,
                                                purchParmTable.OffSessionId_RU);
            break;
    }
    super();
}

public void modified()
{
    super();
    officialsTrans_RU.nameChanged(NoYes::No);
    officialsTrans_RU_ds.refresh();
}

public void lookup(FormControl _formControl, str _filterStr)
{
    boolean isOurGuy = officialsTrans_RU.isOurGuy() == OffIsOurGuy_RU::Yes;
    OfficialsTrans_RU::nameLookup(_formControl,
                                  isOurGuy,
                                  "",
                                  isOurGuy ? ""   : officialsTrans_RU.CustVendRelation);
}

