/// <summary>
/// Initialize <c>PurchParmLine_IN</c> form datasource.
/// </summary>
public void init()
{
    PurchFormLetter purchFormLetterLoc;
    DocumentStatus  documentStatusLoc;

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]) && element.args().caller())
    {
        purchFormLetterLoc = element.args().caller();
        documentStatusLoc = purchFormLetterLoc.documentStatus();

        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]) && (documentStatusLoc == DocumentStatus::BillOfEntry_IN || documentStatusLoc == DocumentStatus::PackingSlip
            || documentStatusLoc == DocumentStatus::Invoice || documentStatusLoc == DocumentStatus::ReceiptsList))
        {
            this.linkType(FormLinkType::OuterJoin);
        }
    }
    // </GIN>

    super();
}

public void write()
{
    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::IsCompanyEnabled())
    {
        if (purchParmLine.SalesPurchJournalLine::taxRecalculationNeeded())
        {
            purchParmLine.markCurrentTaxDocumentTaxStatusDirty();
        }
    }
    // </GTE>

    super();
}

