void  clicked()
{
    super();

    if (! purchFormLetter.lateSelectionRemoveLines())
        return;

    if (element.isBatchReadOnly())
    {
        checkFailed(strFmt("@SCM:PurchEditLines_BatchStatusCannotChangeSelection", BatchStatus::Hold));
    }
    else
    {
        this.initializePurchFormLetter();

        if (purchParmUpdate.LateSelection)
        {
            purchParmUpdate.LateSelectionQuery = purchFormLetter.parmQueryUsed().pack();

            element.initQuery();
        }

        element.chooseTable(true);
    }
}

/// <summary>
/// Initializes the <c>PurchFormLetter</c> class.
/// </summary>
protected void initializePurchFormLetter()
{
    purchFormLetter.initParameters(purchParmUpdate.data(),
                                   printout.selection(),
                                   printFormletter.value(),
                                   usePrintManagement.value(),
                                   tamVendPrintOptionsCheckBox.value());

    purchFormLetter.editLinesChanged(editLinesChanged);
    purchFormLetter.reArrangeNow(reArrangeNow);
    purchFormLetter.wmsJournalTable_ds(null);
    purchFormLetter.showQueryForm(true);

    if (isOpenedFromBatch
        && FormletterParmDataChooseLinesFlight::instance().isEnabled())
    {
        using (FormletterParmDataChooseLinesContext context = FormletterParmDataChooseLinesContext::construct())
        {
            purchFormLetter.initLinesQuery();
        }
    }
    else
    {
        purchFormLetter.initLinesQuery();
    }
}

