public void jumpRef()
{
    Args args = new Args();

    args.lookupField(fieldNum(LtInvoiceAutoNumberingTable, NumberingCode));
    args.lookupValue(this.text());

    new MenuFunction(menuitemDisplayStr(LtInvoiceAutonumberingTable), MenuItemType::Display).run(args);
}

public void lookup()
{
    switch (documentStatus)
    {
        case DocumentStatus::PackingSlip:
             LtInvoiceAutoNumberingTable::lookup(this,
                                                 DocNumberingModule_W::Purch,
                                                 LtInvoiceAutoNumberingType::PackingSlip);
            break;
        case DocumentStatus::Invoice, DocumentStatus::Facture_RU:
             LtInvoiceAutoNumberingTable::lookup(this,
                                                 DocNumberingModule_W::Purch,
                                                 LtInvoiceAutoNumberingType::Invoice);
            break;
        default:
            super();
    }
}

public boolean validate()
{
    boolean                    ret = super();
    LtInvoiceAutoNumberingType invoiceAutoNumberingType;

    if (ret)
    {
        if (! this.text())
        {
            ret = checkFailed(strFmt("@SYS26332",
                                     fieldPName(LtInvoiceAutoNumberingTable, NumberingCode)));
        }
        if (ret)
        {
            switch (documentStatus)
            {
                case DocumentStatus::Invoice, DocumentStatus::Facture_RU:
                    invoiceAutoNumberingType = LtInvoiceAutoNumberingType::Invoice;
                    break;
                case DocumentStatus::PackingSlip:
                    invoiceAutoNumberingType = LtInvoiceAutoNumberingType::PackingSlip;
                    break;
            }
            if (! LtInvoiceAutoNumberingTable::existUserNumbering(this.text(),
                                                                  DocNumberingModule_W::Purch,
                                                                  invoiceAutoNumberingType))
            {
                ret = checkFailed(strFmt("@GLS110719", this.text(), curUserId()));
            }
        }
    }
    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        printOut.enabled(updateNow.value());
        purchParmTable_ds.object(fieldNum(PurchParmTable, Num)).allowEdit(updateNow.value());
        if (updateNow.value())
        {
            // <GEERU>
            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
            {
                printOut.selection(Printout::After);
            }
            else
            {
                // </GEERU>
                printOut.selection(purchFormLetter.printout());
                // <GEERU>
            }
            // </GEERU>
        }
        else
        {
            printOut.selection(Printout::Current);
        }
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    ret = ret && purchFormLetter.checkPrintOut(this.selection());

    return ret;
}

