void clicked()
{
    PurchEditLinesChangeDate    purchEditLinesChangeDate;

    if (purchParmTable)
    {
        super();

        purchEditLinesChangeDate = PurchEditLinesChangeDate::construct(purchParmTable.ParmId, purchParmTable.TransDate);

        if (purchEditLinesChangeDate.prompt())
        {
            ttsbegin;

            purchEditLinesChangeDate.runOperation();

            if (purchParmTable.Ordering == DocumentStatus::ApproveJournal)
            {
                purchFormLetter.modifyVoucherDate(purchEditLinesChangeDate.toDate());
            }

            ttscommit;

            purchParmTable_ds.executeQuery();
        }
    }
}

